@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.kerbalspaceprogram.com") {


/*----------------------------------------------------------------

    Written by n.b.z.

    This stuff is public domain and belongs to absolutely everyone.
    It is not meant for, and has not been tested on, mobile devices.

    Before you start modding this mess, I recommend slowly scrolling
    down all the way, and taking a look at the ENTIRE source. It has
    been structured
            1. - to separate the LAYOUT and the COLORS, and
            2. - to separate TEXT colors from BACKGROUND colors.

    For some elements, this distinction makes little sense. These
    are adressed further down. (In general, this approach is good
    for quickly changing a few colors here and there, but has the
    disadvantage that many elements have MULTIPLE style rules in
    different places.)

    Some EMPTY style rules have been included at various places.
    These are provided to make modding the corresponding elements
    as convenient as possible.

----------------------------------------------------------------*/



/* ###################################################### */
/* ####################  TYPOGRAPHY  #################### */
/* ###################################################### */


    /* font choice */
    div {
    /* font-family: Verdana; */
    }


    /* line height */
    .ipsType_normal.ipsType_richText {
        line-height: 1.4;
    }

    /*
        Text colors are handled further down.

        A totally incomplete font SIZE section can be
        found at the very bottom. If you want to add some
        rules   for text size, I recommend to dump them
        into that section.
    */



/* ################################################## */
/* ####################  LAYOUT  #################### */
/* ################################################## */


    /* main navigation */
    .ipsNavBar_primary {
        padding: 5px 5px 36px;
    }
    /* sub navigation */
    .ipsNavBar_secondary,
    .ipsNavBar_primary:not(.ipsNavBar_noSubBars) > ul::before {
        height: 36px;
    }
    .ipsNavBar_secondary {
        padding: 0 10px;
    }
    .ipsNavBar_secondary > li > a {
        border-bottom: 2px solid transparent;
        padding: 10px 18px 8px;
    }
    .ipsNavBar_primary:not( .ipsNavBar_noSubBars ) > ul:before {
        height: 36px !important;
    }


    /* wider page */
    .ipsLayout_container {
        max-width: 98%;
    }
    #ipsLayout_mainArea {
        padding: 0 10px;
    }


    /* sidebar column */
    html[dir="ltr"] #ipsLayout_sidebar.ipsLayout_sidebarright, html[dir="rtl"] #ipsLayout_sidebar.ipsLayout_sidebarleft {
        padding-right: 10px;
    }


    /* denser thread lists */
    .ipsDataList_large .ipsDataItem_icon,
    .ipsDataList_large .ipsDataItem_main, .ipsDataList_large .ipsDataItem_stats, .ipsDataList_large .ipsDataItem_lastPoster, .ipsDataList_large .ipsDataItem_generic
    {
        padding: 12px 10px;
    }
    .ipsDataItem_title {
        margin: 0 0 3px;
    }
    .ipsDataItem_subList .ipsItemStatus:not(.ipsItemStatus_large) {
        vertical-align: 0.25em;
    }


    /* thinner borders around posts & articles */
    .ipsPad, .ipsApp ul.ipsPad, .ipsApp ol.ipsPad {
        padding: 5px;
    }
    .ipsComment {
        margin-bottom: 5px;
    }
    .cPost .cPost_contentWrap {
        padding: 20px 15px 15px 0;
    }


    /* separate content from status */
    DIV.ipsComment_meta.ipsType_light {
        position: relative;
        border-left: 14px solid; /* border-left is overwritten in BACKGROUNDS section */
        left: -14px;
        width: 98%;
        box-sizing: content-box;
        padding: 7px 2% 7px 0px;
    }


    /* breadcrumbs */
    .ipsBreadcrumb.ipsBreadcrumb_top {
        margin: 0 10px 10px;
        padding: 7px 0;
    }
    .ipsBreadcrumb.ipsBreadcrumb_bottom {
        margin: 15px 10px 0;
        padding: 10px 0;
    }


    /* pagination */
    .ipsButtonBar {
        margin: 3px 0;
    }
    .ipsPagination:not(.ipsPagination_mini) .ipsPagination_page a, .ipsPagination:not(.ipsPagination_mini) .ipsPagination_next a, .ipsPagination:not(.ipsPagination_mini) .ipsPagination_prev a, .ipsPagination:not(.ipsPagination_mini) .ipsPagination_first a, .ipsPagination:not(.ipsPagination_mini) .ipsPagination_last a {
        padding: 0 7px;
    }
    .ipsPagination,
    .ipsButtonRow li > a, .ipsButtonRow li > button, .ipsButtonRow li > span {
        line-height: 22px;
    }
    .ipsPagination.ipsPagination_mini {
        line-height: 15px;
    }
    .ipsPagination li:not(.ipsPagination_pageJump):not(.ipsPagination_mini) .fa {
        line-height: 11px;
    }


    /* footer */
    #ipsLayout_footer {
        margin: 20px 0 10px;
    }


/* ################################################## */
/* ####################  COLORS  #################### */
/* ################################################## */



    /* ########## BACKGROUNDS ########## */


    /* global */
    body {
        background: #112233; /* Old browsers */
        background: -moz-linear-gradient(top, #225577 250px, #112233 500px); /* FF 3.6-15 */
        background: -webkit-linear-gradient(top, #225577 250px, #112233 500px); /* Chrome 10-25, Safari 5.1-6 */
        background: linear-gradient(to bottom, #225577 250px, #112233 500px); /* W3C */
    }


    /* main content */
    #ipsLayout_contentWrapper {
        background: #001133;
    }
    .ipsAreaBackground {
        background: #556677;
    }
    .ipsAreaBackground_reset {
        background-color: #556677;
    }


    /* main page header */
    #ipsLayout_header header {
        background-color: #225577;
    }


    /* sidebar and article content bodies */
    .ipsBox:not(.ipsBox_transparent):not(.ipsModerated):not(.ipsWidget):not(.ipsWidget_vertical) {
        background-color: #001122 !important;
    }


    /* sidebar */
    .ipsWidget, .ipsWidget_vertical {
        background-color: #223344 !important;
    }
    .ipsWidget.ipsWidget_vertical .ipsWidget_title {
        background: #114466;
    }


    /* main navigation */
    .ipsNavBar_primary {
        background: #001144;
    }
    .ipsNavBar_primary > ul {
    }
    .ipsNavBar_primary > ul > li {
    }
    .ipsNavBar_primary > ul > li > a {
        background: rgba(255, 255, 255, 0.15);
    }
    /* main nav - active tab */
    .ipsNavBar_primary > ul > li.ipsNavBar_active {
    }
    .ipsNavBar_primary > ul > li.ipsNavBar_active > a {
        background: #8899aa;
    }
    .ipsNavBar_secondary {
        box-shadow: 0 -3px 3px rgba(0, 0, 0, 0.25);
    }
    .ipsNavBar_secondary > li > a {
    }
    /* main nav - tab hover */
    .ipsNavBar_secondary > li > a:hover {
        background: rgba(255, 255, 255, 0.5);
    }
    /* sub navigation */
    .ipsNavBar_secondary,
    .ipsNavBar_primary:not(.ipsNavBar_noSubBars) > ul::before {
        background: #8899aa;
    }
    .ipsNavBar_secondary {
    }
    .ipsNavBar_secondary > li {
    }
    .ipsNavBar_secondary > li > a {
    }
    .ipsNavBar_secondary > li > a:hover {
        background: #aabbcc;
        border-color: #223344;
    }
    /* sub navigation - active item */
    .ipsNavBar_secondary > li.ipsNavBar_active {
    }
    .ipsNavBar_secondary > li.ipsNavBar_active a {
        border-color: #224466;
    }
    .ipsNavBar_secondary > li.ipsNavBar_active {
        background: #99aabb;
    }
    .ipsNavBar_secondary > li.ipsNavBar_active a:hover {
        border-color: transparent;
    }
    .ipsNavBar_primary:not( .ipsNavBar_noSubBars ) > ul:before {
        background: #8899aa !important;
    }


    /* breadcrumbs */
    .ipsBreadcrumb.ipsBreadcrumb_top {
        border-bottom: 1px dotted rgba(40%, 50%, 60%, 0.2);
        background-color: transparent;
    }
    .ipsBreadcrumb.ipsBreadcrumb_bottom {
        border-top: 1px dotted rgba(30%, 40%, 50%, 0.2);
    }


    /* main area below navigation */
    #ipsLayout_mainArea, #ipsLayout_sidebar {
        background-color: #001122;
        background-image: linear-gradient(to bottom, #001133 0, #001122 70px, #001122 98%, #001133 100%);
        border-top: 1px solid #445566;
    }


    /* pagination */
    .ipsButtonBar {
        background: #113355 linear-gradient(to bottom, #002244 0%, #225577 20%, #225577 80%, #002244 100%) repeat scroll 0 0;
    }
    .ipsApp .ipsButtonBar .ipsPagination .ipsPagination_page a,
    .ipsApp .ipsButtonBar .ipsPagination .ipsPagination_next a,
    .ipsApp .ipsButtonBar .ipsPagination .ipsPagination_prev a,
    .ipsApp .ipsButtonBar .ipsPagination .ipsPagination_first a,
    .ipsApp .ipsButtonBar .ipsPagination .ipsPagination_last a {
        background: rgba(255, 255, 255, 0.15) none repeat scroll 0 0;
        transition: background 0.15s;
    }
    .ipsApp .ipsButtonBar .ipsPagination .ipsPagination_page:hover a,
    .ipsApp .ipsButtonBar .ipsPagination .ipsPagination_next:hover a,
    .ipsApp .ipsButtonBar .ipsPagination .ipsPagination_prev:hover a,
    .ipsApp .ipsButtonBar .ipsPagination .ipsPagination_first:hover a,
    .ipsApp .ipsButtonBar .ipsPagination .ipsPagination_last:hover a {
        background: rgba(255, 255, 255, 0.5) none repeat scroll 0 0;
    }
    .ipsPagination.ipsPagination_mini {
        opacity: 0.5;
    }
    .ipsPagination .ipsPagination_page a {
    }
    .ipsPagination.ipsPagination_mini a {
        background: rgba(0, 100, 200, 0.5) none repeat scroll 0 0;
    }
    .ipsPagination a {
        border: 1px solid #112233;
    }
    .ipsButtonBar .ipsPagination a {
        border: 1px solid transparent;
    }
    .ipsApp .ipsButtonBar .ipsPagination .ipsPagination_page a,
    .ipsApp .ipsButtonBar .ipsPagination .ipsPagination_next a,
    .ipsApp .ipsButtonBar .ipsPagination .ipsPagination_prev a,
    .ipsApp .ipsButtonBar .ipsPagination .ipsPagination_first a,
    .ipsApp .ipsButtonBar .ipsPagination .ipsPagination_last a,
    .ipsButtonBar .ipsButtonRow li > a, .ipsButtonBar .ipsButtonRow li > button, .ipsButtonBar .ipsButtonRow li > span {
        border: 1px solid #223344;
    }


    /* section headers */
    .ipsType_sectionTitle,
    .ipsAreaBackground_light {
        background-color: #334455;
        border-top: 1px solid #001122;
    }
    .ipsType_sectionTitle + .ipsType_sectionTitle,
    .ipsType_sectionTitle + .ipsAreaBackground_light {
        border-top: none;
    }


    /* article status */
    DIV.ipsComment_meta.ipsType_light {
        background-color: #112233;
        border-left: 14px solid #112233;
    }
    /* article fallback rule */
    .ipsComment {
        background-color: #112233;
    }
    /* article author */
    .ipsComment_author {
        background-color: #112233;
    }


    /* list item (or article) background - general */
    .ipsDataItem {
        background-color: #001122;
    }
    /* list item background - sidebar */
    .ipsWidget .ipsDataItem,
    .ipsWidget_vertical .ipsDataItem {
        background-color: #334455;
        border: none;
        border-bottom: 1px solid #223344;
    }
    /* list item borders */
    .ipsDataList.ipsDataList_zebra .ipsDataItem {
        border-left: 1px solid #223344;
        border-right: 1px solid #223344;
    }
    .ipsDataList {
        border-bottom: 1px solid #223344;
    }
    .ipsDataList .ipsDataItem {
        border-top: 1px solid #223344;
        border-bottom: none;
    }


    /* alternating list items - STRIPES */
    .ipsDataList.ipsDataList_zebra .ipsDataItem:nth-child(2n):not(.ipsDataItem_selected):not(.ipsModerated):not(.ipsDataItem_new):not(.ipsDataItem_success):not(.ipsDataItem_warning):not(.ipsDataItem_error):not(.ipsDataItem_info):not(.ipsDataItem_status) {
        background: #112233 !important; /* Old browsers */
        background: -moz-linear-gradient(left, #1a2b3c 0%, #112233 25%, #112244 67%, #1a2b3c 100%) !important; /* FF 3.6-15 */
        background: -webkit-linear-gradient(left, #1a2b3c 0%, #112233 25%, #112244 67%, #1a2b3c 100%) !important; /* Chrome 10-25, Safari 5.1-6 */
        background: linear-gradient(to right, #1a2b3c 0%, #112233 25%, #112244 67%, #1a2b3c 100%) !important; /* W3C */
    }


    /* buttons (well, some of them) */
    .ipsApp .ipsButton_primary {
        background-color: #114466;
    }
    .ipsApp .ipsButton_important {
        background-color: #114466;
    }
    .ipsApp .ipsButton_light {
        background-color: #114466;
    }
    .ipsApp .ipsButton:hover:not(.ipsButton_noHover) {
        background-image: linear-gradient(to bottom, rgba(255, 255, 255, 0.2) 0%, rgba(255, 255, 255, 0.05) 50%, rgba(255, 255, 255, 0.2) 100%);
    }
    /* follow button */
    .ipsFollow .ipsCommentCount {
        opacity: 0.66;
    }
    /* like/rep button */
    .ipsLikeRep {
        background: transparent;
    }


    /* boxes at bottom of front page */
    .ipsWidget.ipsWidget_horizontal .ipsWidget_title {
        background: #556677;
        border-bottom: 1px solid #112233;
    }


    /* quick reply box container */
    .ipsBox {
        background-color: #001122;
    }


    /* ########## TEXT COLORS ########## */


    /* global */
    body {
        color: #fffff8;
    }


    /* title text */
    .ipsType_pageTitle {
        color: #fffff0;
    }
    /* main post/article text */
    .ipsType_richText {
        color: #fffff0;
    }
    /* light text */
    .ipsType_light {
        color: #bbaa66;
    }


    /* main navigation */
    .ipsNavBar_primary {
    }
    .ipsNavBar_primary > ul {
    }
    .ipsNavBar_primary > ul > li {
    }
    .ipsNavBar_primary > ul > li > a {
    }
    /* main nav - active tab */
    .ipsNavBar_primary > ul > li.ipsNavBar_active {
    }
    .ipsNavBar_primary > ul > li.ipsNavBar_active > a {
        color: #fffff0;
    }
    /* main nav - tab hover */
    .ipsNavBar_secondary > li > a:hover {
    }
    /* sub navigation */
    .ipsNavBar_secondary,
    .ipsNavBar_primary:not(.ipsNavBar_noSubBars) > ul::before {
    }
    .ipsNavBar_secondary {
    }
    .ipsNavBar_secondary > li {
    }
    .ipsNavBar_secondary > li > a {
        color: #223344;
    }
    .ipsNavBar_secondary > li > a:hover {
        color: #225588;
    }
    /* sub navigation - active item */
    .ipsNavBar_secondary > li.ipsNavBar_active {
        color: #001122;
    }
    .ipsNavBar_secondary > li.ipsNavBar_active a {
    }
    .ipsNavBar_secondary > li.ipsNavBar_active {
    }
    .ipsNavBar_secondary > li.ipsNavBar_active a:hover {
    }


    /* links */
    a {
        color: #ffeeaa;
    }
    a:hover {
        color: #ff8800;
    }
    a.ipsType_blendLinks {
        color: #ffdd99;
    }
    .ipsType_blendLinks a {
        color: #fff4cc;
    }
    a.ipsType_blendLinks:hover:not(.ipsType_noUnderline) {
        color: #ffaa00;
    }
    .ipsType_blendLinks:not(.ipsType_noUnderline) a:hover:not(.ipsType_noUnderline) {
        color: #ffaa00;
    }


    /* buttons */
    .ipsApp .ipsButton_primary {
        color: #fff4cc;
    }
    .ipsApp .ipsButton_important {
        color: #fff4cc;
    }
    .ipsApp .ipsButton_light {
        color: #ccddee;
    }
    .ipsApp .ipsButton_alternate {
        color: #fff4cc;
    }
    .ipsApp .ipsButton:hover:not(.ipsButton_noHover) {
    }
    /* follow button */
        .ipsFollow .ipsCommentCount {
    }
    /* like/rep button */
        .ipsLikeRep {
    }


    /* some widget text */
    .ipsWidget.ipsWidget_vertical .ipsWidget_inner {
        color: #bbbbaa;
    }


    /* titles of boxes at bottom of front page */
    .ipsWidget.ipsWidget_horizontal .ipsWidget_title {
        color: #bbbbaa;
    }


    /* footer */
    #ipsLayout_footer a, #ipsLayout_footer p {
        color: #bbbbaa;
    }


    /* topic pager */
    .ipsPager_type,
    .ipsPager .ipsPager_next a::after, .ipsPager .ipsPager_prev a::after {
        color: #bbaa66;
    }


    /* breadcrumbs */
    .ipsBreadcrumb [data-action="defaultStream"], .ipsBreadcrumb [data-action="markSiteRead"],
    .ipsBreadcrumb > ul > li > a {
        color: #eedd88;
    }
    .ipsBreadcrumb > ul > li {
        color: #eecc44;
    }


    /* pagination */
    .ipsApp .ipsButtonBar .ipsPagination .ipsPagination_page a,
    .ipsApp .ipsButtonBar .ipsPagination .ipsPagination_next a,
    .ipsApp .ipsButtonBar .ipsPagination .ipsPagination_prev a,
    .ipsApp .ipsButtonBar .ipsPagination .ipsPagination_first a,
    .ipsApp .ipsButtonBar .ipsPagination .ipsPagination_last a {
        color: #ffeeaa;
        text-shadow: 1px 1px 1px rgba(0, 0, 0, 0.75);
    }
    .ipsApp .ipsButtonBar .ipsPagination .ipsPagination_page:hover a,
    .ipsApp .ipsButtonBar .ipsPagination .ipsPagination_next:hover a,
    .ipsApp .ipsButtonBar .ipsPagination .ipsPagination_prev:hover a,
    .ipsApp .ipsButtonBar .ipsPagination .ipsPagination_first:hover a,
    .ipsApp .ipsButtonBar .ipsPagination .ipsPagination_last:hover a {
        color: #ffffff !important;
    }
    .ipsPagination.ipsPagination_mini {
        color: #fff4cc !important;
    }
    .ipsPagination .ipsPagination_page a,
    .ipsPagination .ipsPagination_next a,
    .ipsPagination .ipsPagination_prev a,
    .ipsPagination .ipsPagination_first a,
    .ipsPagination .ipsPagination_last a {
        text-shadow: 1px 1px 0 rgba(0, 0, 0, 0.5);
    }
    .ipsPagination.ipsPagination_mini a {
        color: #fff4cc !important;
    }
    .ipsPagination a {
        color: #fff4cc !important;
    }



/* ########################################################################### */
/* ####################  GLUEING IT ALL TOGETHER SOMEHOW  #################### */
/* ########################################################################### */


    /* ########## special elements, widgets, snippets, leftovers, and further "Web 2.0" shnimbimbatz ########## */

    /* ########## This stuff has NOT been split up into separate layout and coloring rules ########## */


    /* input */
    .ipsApp .ipsField_autocomplete, .ipsApp textarea, .ipsApp input[type="text"], .ipsApp input[type="password"], .ipsApp input[type="datetime"], .ipsApp input[type="datetime-local"], .ipsApp input[type="date"], .ipsApp input[type="month"], .ipsApp input[type="time"], .ipsApp input[type="week"], .ipsApp input[type="number"], .ipsApp input[type="email"], .ipsApp input[type="url"], .ipsApp input[type="search"], .ipsApp input[type="tel"] {
        background: #556677;
    }


    /* tooltips */
    .ipsTooltip {
        background: #000000 linear-gradient(to bottom, #88aacc 0%, #6688aa 100%) repeat scroll 0 0;
        color: #ffffff;
        text-shadow: 1px 1px 1px #001122;
        line-height: 20px;
        padding: 0 5px;
    }
    .ipsTooltip.ipsTooltip_top::after {
        border-color: #6688aa transparent transparent;
    }
    .ipsTooltip.ipsTooltip_bottom::after {
        border-color: transparent transparent #88aacc;
    }


    /* tags */
    html[dir="ltr"] .ipsTags a::before,
    html[dir="ltr"] .ipsTag_prefix::before {
        border-color: transparent #113355 transparent transparent;
    }
    .ipsTags a, .ipsTag_prefix {
        color: #ffc !important;
        background: #113355;
    }
    /* tag dots */
    .ipsTags a::after, .ipsTag_prefix::after {
        background: #ffc !important;
    }
    /* hover styles */
    html[dir="ltr"] .ipsTags a:hover::before,
    html[dir="ltr"] .ipsTag_prefix:hover::before {
        border-color: transparent #335577 transparent transparent;
    }
    .ipsTags a:hover {
        color: #ffe !important;
        background: #335577;
    }


    /* quotes */
    .ipsQuote {
    background: #808080;
        background: rgba(127,127,127,0.33);
        border-color: #aabbcc #667788 #445566 #8899aa !important;
        border-width: 1px;
    }
    .ipsQuote_citation {
    background: #c0c0c0;
        background: rgba(191,191,191,0.33);
        padding: 2px 15px;
    }
    .ipsQuote_citation [data-action="toggleQuote"] {
        vertical-align: -1px;
    }


    /* code blocks */
    .ipsCode {
        background: rgba(127, 127, 127, 0.33);
        border-color: #aabbcc #667788 #445566 #8899aa !important;
        border-style: solid !important;
        border-width: 1px 1px 1px 1px !important;
        overflow-x: hidden;
        overflow-y: auto;
        margin: 0;
        padding: 5px;
    }
    .ipsCode:hover {
        /* background: rgba(127, 127, 127, 0.66); */
    }
    .ipsPos_right {
        margin-left: 5px;
    }
    .ipsCode,
    .ipsCode .pln {
        color: #33ff77;
    }


    /* spoiler boxes */
    .ipsSpoiler, .ipsStyle_spoiler {
        background: #556677;
        border: none;
    }
    .ipsSpoiler_contents {
        margin: 10px 0;
    }
    .ipsSpoiler_header {
        background: #778899;
        padding: 2px 15px;
    }
    .ipsSpoiler_header [data-action="toggleSpoiler"] {
        vertical-align: -1px;
    }


    /* editor */
    .cTopicPostArea .ipsComposeArea {
        opacity: 0.85;
    }
    .ipsComposeArea_editor {
        background-color: #224466;
    }
    html[dir="ltr"] .ipsComposeArea_withPhoto .ipsComposeArea_editor::before {
        color: #224466;
        border-color: transparent #224466 transparent transparent;
    }
    .cke_top {
        background: #aabbcc;
    }
    a.cke_button_disabled {
        background: #8899aa;
        opacity: 0.75;
    }
    a.cke_button_on {
        background: #bbccdd;
    }
    a.cke_button_off:hover, a.cke_button_off:focus, a.cke_button_off:active, a.cke_button_disabled:hover, a.cke_button_disabled:focus, a.cke_button_disabled:active {
        background: #ddeeff;
    }
    .cke_wysiwyg_frame, .cke_wysiwyg_div,
    .ipsComposeArea_dummy {
        background-color: #001122;
    }
    .ipsComposeArea_dummy {
        color: #667788;
    }
    .ipsComposeArea_editor .ipsButton_primary {
        background: #aabbcc;
        color: #012;
    }
    .ipsComposeArea_editor .ipsComposeArea_dropZone {
        background: #223344;
    }


    /* rep point symbols */
    .ipsPip {
        color: #667788;
    }
    .ipsRating .ipsRating_on .fa-star-o,
    .ipsRating .ipsRating_on .fa-star {
        color: #667788;
    }
    .ipsRating .ipsRating_off .fa-star-o,
    .ipsRating .ipsRating_off .fa-star {
        color: #445566;
    }
    .ipsRating_hover .ipsRating_on .fa-star-o,
    .ipsRating_hover > .ipsRating_hover .fa-star {
        color: #66aaee;
    }


    /* profile page and related UI stuff */
    .ipsPageHead_special {
        background-color: #113355;
    }
    #elProfileStats {
        background-color: #002244;
    }
    .ipsAreaBackground_reset {
        background-color: #223344;
    }
    .ipsStreamItem.ipsStreamItem_actionBlock {
        background-color: #112233;
    }
    .ipsApp .ipsSpacer_bottom, .ipsApp .ipsSpacer_both {
        margin-bottom: 15px;
    }
    .ipsTabs .ipsTabs_item {
        background: #556677;
