css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.d3go.tv") {


body { overflow:  hidden; background: black; overflow-x: hidden; height: 1800px;}
    
/*balkanika*/ 
    #gmSomeID p {position:relative;z-index:4; top:50px; left:92%; /*background:red;*/ color:#fff; height: 50px; border: 1px solid #2E2E2E; border-radius:5px; 
    background: #3B3B3B url("http://mylittleworkspace.com/aca/d3-primer/balkanika.png") no-repeat; }
/*planeta*/
    #gmSomeID p.planeta {position:relative;z-index:4; top:50px; left:92%; /*background:red;*/ color:#fff; height: 50px; border: 1px solid #2E2E2E; border-radius:5px; 
    background: #3B3B3B url("http://mylittleworkspace.com/aca/d3-primer/planeta.png") no-repeat; }
  
#gmSomeID p a {width:100%; height:100%; display: block; color: transparent}
#gmSomeID p:hover {border-color: white}
       
html body div#PlayerDialog div#PlayerChannels div#PlayerNavDown.NavCell {/*background: red;*/ height: 40px;}
html body div#PlayerDialog div#PlayerChannels div#PlayerNavUp.NavCell {/*background: red;*/ height: 25px; }
    
#PlayerChannels {display: block !important; z-index: 900 !important;} 
html body div#PlayerDialog div#PlayerDisplay div#PlayerFrameCreator {position: fixed; z-index: 5;}
    
.Channel:hover { background: url("https://www.d3go.tv/img4.7.1.24/channel_button_nc.png") no-repeat;}
.ChannelButtonCatchUp:hover { background-image: none;}
.ChannelButton:active { background-image: none;}
.ChannelButtonCatchUp:active {background-image: none;}
.ChannelAction, .ChannelCatchUp{display: none;}
  
#PlayerInfo, #PlayerControls{background-image: none; }
#PlayerInfo, #PlayerControls {font-size: 42px !important; background: black; position: fixed;top: 0px;z-index: 9999;} 
        
#dolebre {border: 0px solid #fff; width: auto; height: 50px; color: white; position: absolute; bottom: -10px; left: 0; z-index: 9999;}
#gorebrebre { border: 0px solid #fff; width: auto; height: 50px; color: white; position: absolute; bottom: 0; left: 0; top: 0px !important; z-index:9999; }
    
/*promena kursora*/   
*{cursor: url(http://mylittleworkspace.com/aca/d3-primer/cursors/white.cur), auto !important;}
html{cursor: url(http://mylittleworkspace.com/aca/d3-primer/cursors/white.cur), auto !important;}
body{cursor: url(http://mylittleworkspace.com/aca/d3-primer/cursors/white.cur), auto !important;}
    
#PlayerDialog {height:1800px; overflow: auto; position: fixed;}
#PlayerFrame { position: fixed; left: 180px; width: 1320px !important; margin-top: 20px !important;;}
object, embed, a:link, a:visited { outline:0 !important; }
#PlayerControls {display: none !important;}
    
#gmSomeID { border: 0px solid red; position: fixed; left: 83.5%; width: 130px;} 
}
