css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("forum.blackdesertonline.com") {
    body {
        background: #424242;
        color: rgba(255, 255, 255, 0.4);
    }
    a {
        color: #607d8b;
    }
    .ggb_bar {
        background: #212121;
    }
    /* Header */
    /* Black header */
    
    #ipsLayout_header header {
        background: rgb(33, 33, 33) none repeat scroll 0% 0%;
        padding: 36px 0px 5px;
    }
    /* Pulls up the forum */
    
    header .ipsLayout_container {
        margin: 9px auto 5px;
    }
    /* Hides the text "FORUM" */
    
    .tit_forum {
        display: none;
    }
    #elHeaderSubLinks a,
    .ipsBreadcrumb > ul a {
        color: #546e7a;
    }
    .ipsBreadcrumb > ul > li:last-child {
        color: RGBA(255, 255, 255, 0.4);
    }
    #ipsLayout_header nav a.ipsNavActive {
        display: none;
    }
    /* End of Header */
    /* Content */
    
    .ipsTabs {
        background: #212121;
    }
    .ipsTabs_activeItem {
        background: #424242;
        color: rgba(0, 0, 0, 0.65);
    }
    .ipsAreaBackground_reset {
        background: #212121;
    }
    .ipsPagination.ipsPagination_mini a {
        color: rgba(255, 255, 255, 0.75);
    }
    .ipsPagination .ipsPagination_page a {
        text-shadow: none;
    }
    .ipsDataList.ipsDataList_zebra .ipsDataItem:nth-child(2n):not(.ipsDataItem_selected):not(.ipsModerated) {
        background: #424242 !important;
    }
    .ipsDataItem {
        border-color: #424242;
    }
    .ipsButtonBar .ipsPagination .ipsPagination_page.ipsPagination_active a {
        background: #757575;
        border-color: #757575;
        color: rgba(0, 0, 0, 0.65);
    }
    .ipsType_light {
        color: rgba(255, 255, 255, 0.45);
    }
    .ipsApp .ipsButton_link {
        background: #757575;
        color: rgba(255, 255, 255, 0.55);
    }
    .ipsApp .ipsButton_light {
        background: #757575;
        border-color: rgba(0, 0, 0, 0);
    }
    .ipsFollow .ipsCommentCount {
        background: #9E9E9E;
    }
    html[dir="ltr"] .ipsFollow .ipsCommentCount::after {
        border-color: transparent #9E9E9E transparent transparent;
    }
    .ipsPageHeader{
        background: #212121;
        box-shadow: rgba(0,0,0,0.2) 0px 1px 1px;
        padding: 5px;
    }
    /* Search */
    
    #elSearchNavContainer,
    #elMobileNav {
        background: none;
    }
    #elSearch input[type="search"] {
        color: rgba(0, 0, 0, 0.65);
    }
    /* Breadcrumb textshadow and background */
    
    .ipsApp .ipsSubNavigation {
        text-shadow: none;
        background: #212121;
    }
    /* Forum board background */
    
    #ipsLayout_mainArea,
    #ipsLayout_sidebar {
        background: #626262;
    }
    .ipsBox:not(.ipsBox_transparent) {
        background-color: #212121 !important;
    }
    .ipsType_pageTitle {
        color: rgba(255, 255, 255, 0.55);
    }
    .ipsType_sectionTitle {
        background: #212121;
        color: rgba(255, 255, 255, 0.4);
    }
    /* Hides the nasty border */
    
    .ipsBox {
        border-width: 0px;
        border-radius: 0px 0px 3px 0px;
        box-shadow: rgba(0, 0, 0, 0.2) 0px 1px 1px;
    }
    /* Side panel posts */
    
    .ipsAreaBackground_light {
        background: #212121 !important;
    }
    .ipsUserPhoto::after {
        border: 1px solid #626262;
        box-shadow: 0px 0px 0px 1px #626262;
    }
    .ipsType_sectionHead {
        color: #607d8b;
    }
    /* Post */
    
    html[dir="ltr"] .ipsColumns > .ipsColumn:last-child:not(.ipsPad),
    html[dir="ltr"] .ipsColumns.ipsColumns_noSpacing > .ipsColumn:not(.ipsPad) {
        background: rgba(0, 0, 0, 0.2);
    }
    .ipsLikeRep {
        background: transparent;
    }
    .ipsLikeRep .ipsButton, .ipsReputation_count {
        padding: 4px 7px;
    }
    /* Reply to area */
    
    .ipsAreaBackground {
        background: #212121;
    }
    .ipsFaded.ipsFaded_more {
        opacity: 0.6;
    }
    .ipsComposeArea_editor {
        background: #424242;
    }
    .ipsComposeArea_dummy {
        background: #424242;
    }
    html[dir="ltr"] .ipsComposeArea_withPhoto .ipsComposeArea_editor::before {
        border-color: transparent #424242 transparent transparent;
    }
    .cke_top {
        background: #212121;
    }
    .cke_combo_button {
        border: 1px solid #757575;
        border-bottom-color: #757575;
        background-image: none;
        background: #757575;
    }
    .cke_combo_text {
        color: rgba(0, 0, 0, 0.65);
        text-shadow: none;
    }
    .cke_bottom {
        background: #626262;
    }
    /* Quote */
    
    blockquote.ipsQuote div.ipsQuote_citation {
        color: rgba(255, 255, 255, 0.55);
        background: #424242;
        font-weight: 400;
    }
    blockquote.ipsQuote {
        background: #212121;
        border-color: #212121;
        border-width: 0px 0px 1px 0px;
    }
    blockquote.ipsQuote div.ipsQuote_citation a {
        color: #607D8B;
        font-weight: bold;
    }
    /* Thumbnail */
    
    .ipsImage_thumbnailed {
        border: 1px solid #626262;
    }
    .ipsHovercard {
        background: #424242;
        border: none;
        border-radius: 0 0 3px 3px;
        border-bottom: 1px solid #212121;
        box-shadow: none;
    }
    .ipsHovercard_stemTop .ipsHovercard_stem {
        border-color: #212121 transparent transparent transparent;
    }
    .ipsTabs_panels.ipsTabs_contained {
        background-color: #212121;
        border-color: #212121;
    }
    .ipsTabs_panel {
        background: #212121;
    }
    .ipsStyle_spoiler,
    .ipsStyle_spoilerFancy_inner {
        background: #424242 !important;
    }
    .ipsStyle_spoilerFancy_text {
        color: rgba(255, 255, 255, 0.55);
    }
    .ipsStyle_spoilerFancy {
        background: #212121;
    }
    .ipsMenu {
        background: #424242;
    }
    .ipsMenu.ipsMenu_bottomCenter::after {
        border-color: transparent transparent #424242 transparent;
    }
    .ipsMenu_title {
        background: #212121;
        color: rgba(255, 255, 255, 0.55);
    }
    .ipsMenu_item > a {
        color: #607D8B;
    }
    .ipsMenu_item:not(.ipsMenu_itemClicked):not(.ipsMenu_itemDisabled) a:hover,
    .ipsMenu_item[data-selected] a {
        background: #626262 !important;
      	color: rgba(255, 255, 255, 0.45) !important;
    }
    .ipsMenu_sep {
        border-top: 1px solid #757575;
    }
    hr.ipsHr {
        border-color: #757575;
    }
    .ipsComment {
        background: #212121;
    }
    .ipsComment:not(.ipsModerated) .ipsComment_header {
        background: #424242 !important;
    }
    html[dir="ltr"] .cMessage_members > ol > li {
        border-right: 2px solid #757575;
    }
    .cMessage_active {
        background: #424242 !important;
    }
    .ipsApp .ipsField_autocomplete,
    .ipsApp textarea,
    .ipsApp input[type="text"],
    .ipsApp input[type="password"],
    .ipsApp input[type="datetime"],
    .ipsApp input[type="datetime-local"],
    .ipsApp input[type="date"],
    .ipsApp input[type="month"],
    .ipsApp input[type="time"],
    .ipsApp input[type="week"],
    .ipsApp input[type="number"],
    .ipsApp input[type="email"],
    .ipsApp input[type="url"],
    .ipsApp input[type="search"],
    .ipsApp input[type="tel"] {
        background: #424242;
        border-color: #424242;
    }
    .ipsApp textarea:focus,
    .ipsApp input[type="text"]:focus,
    .ipsApp input[type="password"]:focus,
    .ipsApp input[type="datetime"]:focus,
    .ipsApp input[type="datetime-local"]:focus,
    .ipsApp input[type="date"]:focus,
    .ipsApp input[type="month"]:focus,
    .ipsApp input[type="time"]:focus,
    .ipsApp input[type="week"]:focus,
    .ipsApp input[type="number"]:focus,
    .ipsApp input[type="email"]:focus,
    .ipsApp input[type="url"]:focus,
    .ipsApp input[type="search"]:focus,
    .ipsApp input[type="tel"]:focus,
    .ipsApp input[type="color"]:focus {
        border-color: #424242;
        box-shadow: none;
    }
    .cProfileRepScore_neutral {
        background: #212121;
    }
    .ipsCode {
        background: #212121;
        border-left: 4px solid #757575 !important;
    }
    #ipsLayout_contentArea {
        border: none;
    }
    .ipsDialog > div {
        background: #212121;
    }
    .ipsDialog_title {
        color: rgba(255, 255, 255, 0.65);
    }
    .ipsDialog_close {
        color: rgba(255, 255, 255, 0.65);
    }
    .cke_button .cke_button_icon {
        filter: grayscale(5%) contrast(25%);
        -webkit-filter: grayscale(5%) contrast(25%);
    }
    .ipsFieldRow_section {
        background: #626262;
        color: rgba(0, 0, 0, 0.65);
    }
    .ipsButtonBar {
        background: #212121;
    }
    .ipsGrid_span6.ipsType_left.ipsPager_prev, 
    .ipsGrid_span6.ipsType_right.ipsPager_next {
        background: #212121;
        box-shadow: rgba(0,0,0,0.2) 0px 1px 1px;
    }
    .ipsPager_type {
        color: rgba(255, 255, 255, 0.55);
    }
  	.ipsCommentUnreadSeperator {
        border-color: #607d8b;
    }
  	html[dir="ltr"] .cSearchActivity_info{
    	border-left: 4px solid #757575;
    }
    iframe[data-embedContent] {
        background-color: #212121;
    }
    .ipsEmbedded_content {
        background-color: #212121;
    }
    .ipsEmbedded_headerArea {
        background: #424242;
    }
    .cPollVoteBar {
        background: #757575;
    }
    .cPollVoteBar > span {
        background: #455A64;
    }
    .cPollVoteBar > span.cPollVoteBar_inside::after {
        color: rgba(255, 255, 255, 0.75);
    }
    .cPollVoteBar > span::after {
        color: rgba(0, 0, 0, 0.55);
    }
}
