css:
@-moz-document domain(csgodouble.com) {
  
  body {
    background-color: #333;
  }
  .navbar-default {
    background-color: #777;
    border-color: #555;
  }
  .navbar-default .navbar-brand {
    color: lightgray;
  }
  .navbar-default .navbar-nav>li>a {
    color: lightgray;
  }
  .panel-default {
    border-color: #5c5c5c;
  }
  .panel {
    background-color: #777;
    border: 1px solid rgba(0, 0, 0, 0.67);
    border-radius: 4px;
  }
  .panel a{
	color:#EAD110;
  }
  .panel-default>.panel-heading {
    color: #333;
    background-color: #bbb;
  }
  .form-control {
    background-color: #ccc;
  }
  #pullout {
    width: /*[[width]]*/px !important;
    background-color: #777;
    border-right: 1px solid #555;
  }
  .divchat {
      background-color: #aaa;
  }
  #sidebar {
    background-color: #888;
    border-right: 1px solid #555;
  }
  .progress {
    background-color: #aaa;
  }
  .well {
    min-height: 20px;
    padding: 19px;
    margin-bottom: 20px;
    background-color: #777;
    border: 1px solid #555;
    border-radius: 4px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.05);
    box-shadow: inset 0 1px 1px rgba(0,0,0,.05);
  }
  .list-group-item {
    background-color: #aaa;
	color: #333;
  }
  .panel.text-danger {
    color: #822725;
  }
  .panel.text-success {
    color: #0A920A;
  }
  #chatArea.text-sucess{
    color: #006500;
  }

  .total-row {
    background-color: #888;
	color: #333;
  }
  

  .well {
    margin-bottom: 0px;
  }
  .btn-default {
    text-shadow: 0 1px 0 darkgray;
    border-color: #333;
    background-image: -webkit-gradient(linear, to bottom, to top, color-stop(0%, #aaa), color-stop(100%, #777));
    background-image: -webkit-linear-gradient(to bottom, #aaa 0%, #777 100%);
    background-image: -moz-linear-gradient(to bottom, #aaa 0%, #777 100%);
    background-image: -o-linear-gradient(to bottom, #aaa 0%, #777 100%);
    background-image: linear-gradient(to bottom, #aaa 0%, #777 100%);
  }
  
  
  .btn-default:hover, .btn-default:focus, .btn-default:active, .btn-default.active, .btn-default.active:hover {
    background-color: #777;
    color: #aaa;
    text-shadow: 0 1px 0 #5F5F5F;
    border-color: #333;
  }
  .btn-default.active:hover {
    background-color: #888;
    color: #bbb;
    text-shadow: 0 1px 0 #5F5F5F;
    border-color: #333;
  }

  
  .table-bordered {
    color: #ccc;
  }
  .pagination>.disabled>a, .pagination>.disabled>a:focus, .pagination>.disabled>a:hover, .pagination>.disabled>span, .pagination>.disabled>span:focus, .pagination>.disabled>span:hover {
    color: #aaa;
    background-color: #777;
    border-color: #333;
  }
  .table-striped>tbody>tr:nth-of-type(odd) {
    background-color: #777;
  }
  

  .panel-body {
    padding: 15px;
    color: #aaa;
  }
  
  .list-inline a{
	color:#EAD110;
  }
  .pull-left a{
    color:#EAD110;
  }

}
