css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@-moz-document url("about:addons") {
/*====================全局 #addons-page====================*/
#addons-page {
 padding:0 !important;
 background:rgb(255, 255, 255) !important;
}

    
/*====================顶部 #nav-header====================*/
#nav-header {
 position: fixed;
 top: 0;
 left: 54px;
 padding-left: 16px;
 height: 41px !important;
 background-color: transparent !important;
}
/*====================左侧 #categories====================*/
/*左侧分类列表*/
#categories {
 padding: 0 !important;
}
/*分类项样式*/
.category {
 color:rgb(255, 255, 255) !important;
}
/*分类名?*/
.category-name{
    font-size:14px!important;
}
/*可升级数字*/
.category-badge {
 min-width:8px !important;
 max-width:8px !important;
 min-height:8px !important;
 max-height:8px !important;
 border-radius: 4px !important;
 background-color: rgb(231, 86, 86) !important;
 font-size: 0 !important;
 text-indent:-9999px;
}
.category[selected="true"] .category-badge {
 background-color: rgb(255, 255, 255) !important;
}
/*分类图标定义*/
.category-icon {
 width:16px !important;
 height:16px !important;
 -moz-margin-start: 0px !important;
 list-style-image: url("data:image/png;base64,") !important;
 opacity: 0.7 !important;
}
.category[selected="true"] .category-icon {
 opacity: 1 !important;
}
#category-search > .category-icon {/*搜索*/
 -moz-image-region: rect(0, 16px, 16px, 0);
}
#category-discover > .category-icon {/*获取附加组件*/
 -moz-image-region: rect(0, 32px, 16px, 16px);
}
#category-locale > .category-icon {/*语言*/
 -moz-image-region: rect(0, 112px, 16px, 96px);
}
#category-searchengine > .category-icon {
}
#category-extension > .category-icon {/*扩展*/
 -moz-image-region: rect(0, 48px, 16px, 32px);
}
#category-service > .category-icon {/*服务*/
 -moz-image-region: rect(0, 208px, 16px, 192px);
}
#category-theme > .category-icon {/*外观*/
 -moz-image-region: rect(0, 64px, 16px, 48px);
}
#category-plugin > .category-icon {/*插件*/
 -moz-image-region: rect(0, 80px, 16px, 64px);
}
#category-dictionary > .category-icon {/*字典*/
 -moz-image-region: rect(0, 96px, 16px, 80px);
}
#category-experiment > .category-icon {/*实验*/
 -moz-image-region: rect(0, 224px, 16px, 208px);
}
#category-availableUpdates > .category-icon {/*可用更新*/
 -moz-image-region: rect(0, 176px, 16px, 160px);
}
#category-recentUpdates > .category-icon {/*最近更新*/
 -moz-image-region: rect(0, 192px, 16px, 176px);
}
#category-userstyle > .category-icon {/*用户样式*/
 -moz-image-region: rect(0, 128px, 16px, 112px);
}
#category-greasemonkey-user-script > .category-icon,/*用户脚本*//*greasemonkey扩展*/ #category-userscript > .category-icon {/*userscript扩展*/
 -moz-image-region: rect(0, 144px, 16px, 128px);
}
#category-userchromejs > .category-icon {/*uc 脚本*/
 -moz-image-region: rect(0, 160px, 16px, 144px);
}

/*分类项排序*/
#category-userstyle {
-moz-box-ordinal-group: 2;
}
#category-greasemonkey-user-script,/*greasemonkey扩展*/ #category-userscript {
-moz-box-ordinal-group: 3;
}/*userscript扩展*/
#category-userchromejs {
-moz-box-ordinal-group: 4;
}
#category-availableUpdates {
-moz-box-ordinal-group: 5;
}
#category-recentUpdates {
-moz-box-ordinal-group: 6;
}
#category-service {
-moz-box-ordinal-group: 7;
}
#category-experiment {
-moz-box-ordinal-group: 8;
}


/*====================右侧 #header====================*/
#header {
 margin:0 !important;
 padding-left: 84px !important;
 padding-right: 16px !important;
 height:41px !important;
 border:none !important;
 background-color:rgb(255, 255, 255) !important;
}
#header-utils-btn, #header-search {
 margin: 0 !important;
 padding: 0 !important;
 height:26px !important;
 border:none !important;
 background-color: #F5F5F5 !important;
}
/*检查更新按钮*/
#header-utils-btn {
 width:26px !important;
 border-right: none !important;
 border:none !important;
 background-color: transparent !important;
 list-style-image: url("data:image/png;base64,") !important;
 cursor: pointer;
}
#header-utils-btn:not([disabled="true"]):hover {
 list-style-image: url("data:image/png;base64,") !important;
}
#header-utils-btn .toolbarbutton-icon {
 -moz-margin-end: 0 !important;
}
#header-utils-btn .toolbarbutton-menu-dropmarker {
 display:none !important;
}
/*搜索框*/
#header-search {
 padding: 0 4px !important;
 width:200px !important;
 border:none !important;
}
#header-search:hover {
}
#header-search[focused] {
}
.textbox-input-box {
 min-width: 100% !important;
}
.textbox-search-icon {
 list-style-image: url("data:image/png;base64,") !important;
}
.textbox-search-clear {
 list-style-image: url("data:image/png;base64,") !important;
}
    
/* 隐藏元素*/
#updates-container {
display:none !important;
}
    
/* 显示计数*/
.addon {
counter-increment: addoncount !important;
content: counter(addoncount) !important;
}
.addon:first-child {
counter-reset: addoncount !important;
}
.addon[active="false"] {
counter-increment: disabled !important;
}
#addon-list:after {
 display: block !important;
 width: 120px !important;
 content: "[ 启用 "counter(addoncount)" | 禁用 "counter(disabled)"  ]" !important;
 color: rgb(196, 196, 196) !important;
 text-align: center !important;
 white-space: nowrap !important;
 position: fixed !important;
 top: 11px !important;
 left: calc(50% - 112px) !important;
 cursor: default;
}
@media (max-width: 500px) {
 #addon-list:after {
 display: none !important;
}
}
    
    
/*====================右侧 #view-port-container====================*/
.main-content {
 margin: 0 !important;
 padding: 0 !important;
 border: none !important;
 border-radius: 0 !important;
 overflow-x: hidden !important;
}
/*列表顶部信息栏*/
.view-header, #update-selected-btn:not([hidden="true"]) + #update-actions {
 margin: 0 !important;
 padding: 0 !important;
 min-height: 32px !important;
 border-bottom: none !important;
 background-color: transparent !important;
}
/*列表顶部信息栏按钮*/
.view-header button {
 -moz-appearance: none !important;
 margin: 0 !important;
 padding: 0 !important;
 height: 32px !important;
 background-color: transparent !important;
 border: none !important;
 border-radius: 0 !important;
 box-shadow: none !important;
 text-decoration: none !important;
 color: rgb(128, 128, 128) !important;
 cursor: pointer;
}
.view-header button:hover {
 background-color: rgb(245, 245, 245) !important;
}
/*排序按钮*/
.view-header .sorter {
 margin-right: -1px !important;
 border-left:1px solid rgb(245, 245, 245) !important;
}
.view-header .sorter .button-box {
 margin: 0 !important;
 padding: 2px 8px !important;
}
.view-header .sorter[checked="true"], .view-header .sorter:hover {
 background-color: rgb(245, 245, 245) !important;
}

/*========列表 .list========*/
.list {
 border: none !important;
}
.view-pane > .list > scrollbox {
 -moz-padding-end: 0 !important;
}
/*紧凑列表*/
.list * {
 border: none !important;
 box-shadow:none !important;
 text-shadow: none !important;
 -moz-transition: none !important;
}
/*列表所有项目放到同一行*/
#view-port .addon {
 margin:0 !important;
 padding: 2px 16px !important;
 border: none !important;
}
#view-port .addon * {
 font-size:101%!important;
 margin-right: 0 !important;
 white-space: nowrap !important;
}
#view-port .addon .description:not([hidden="true"]) {/*详情*/
 margin-top: 2px !important;
 margin-bottom: 5px !important;
}
#view-port .addon .content-inner-container, #view-port .addon .description-outer-container {
 -moz-box-orient: vertical !important;
 -moz-box-align: start !important;
}
#view-port .addon .status-control-wrapper * {
 -moz-box-orient: horizontal !important;
 -moz-box-align: center !important;
}
#view-port .addon .basicinfo-container {/*基础信息*/
 padding-top: 4px !important;
}
/* 横排列表图标收缩 */
.addon[selected] .icon-container,
.addon[selected] .icon{
width: 32px !important;
height: 32px !important;}
.addon:not([selected]) .icon-container,
.addon:not([selected]) .icon{
width: 16px !important;
height: 16px !important;}
.icon-container{
margin: 0 !important;
margin-top: 2px !important;}
/*列表按钮*/
#view-port .list button, #view-port .list menulist, #view-port #detail-view button {
 -moz-appearance:none!important;
 height: 26px !important;
 min-height: 26px !important;
 margin:0 !important;
 margin-left: -1px !important;
 padding:0 4px !important;
 background: none !important;
 border:1px solid rgb(225, 225, 225) !important;
 border-radius: 0 !important;
 box-shadow: none !important;
 color:rgb(64, 64, 64) !important;
 text-decoration:none !important;
 cursor: pointer;
}
/*隐藏'更多'*/
.addon .details {
 display:none !important;
}
/*隐藏描述信息（默认）*/
.addon .description-outer-container {
 display:none !important;
}
/*隐藏列表按钮（默认）*/
.addon .status-control-wrapper {
 display:none !important;
}
/*显示列表按钮（选中）*/
.addon[selected] .status-control-wrapper {
 display:-moz-box !important;
 position:absolute !important;
 right:12px;
 top:1px;
}
/*显示描述信息*/
.addon.addon-view[selected] .description-outer-container {/*选中*/
 display:-moz-box !important;
}
.addon.addon-view[selected], .addon.addon-view[pending] {/*选中或禁用*/
 position:relative !important;
}
.addon.addon-view[selected] .content-container {/*选中*/
 margin-left: 0 !important;
}
.addon.addon-view[pending] .pending {/*禁用*/
 margin-left: 0 !important;
}
.addon.addon-view[selected][pending] .pending {/*选中并禁用*/
 margin-left: -1px !important;
}
.addon.addon-view[pending] .description-outer-container {/*禁用*/
 display: none !important;
}
.addon.addon-view .error {
 margin-left: 0 !important;
}

/*选中项背景（选中或禁用）*/
#view-port .addon[selected] {
background-color: rgb(241, 248, 255) !important;
}
/*悬停项背景*/
#view-port .addon:hover {
background-color: rgb(241, 248, 255) !important;
}
/*禁用项背景*/
#view-port .addon[pending] {
}
/*已禁用项背景*/
.addon.addon-view[active="false"]:not([notification="warning"] ) {
background: rgb(245, 245, 245) !important;
}
/*不兼容项背景*/
richlistitem[notification="warning"] {
background: transparent !important;
}

/*========扩展、外观、插件等列表面板#list-view========*/
/*按钮样式*/
#list-view .control-container button, #list-view .addon-control.update {
 width: 16px !important;
 min-width: 16px !important;
 height: 16px !important;
 margin-right:16px !important;
 padding: 0 !important;
 background-repeat: no-repeat !important;
 background-position: left center !important;
 border: none !important;
 transform: translate(-2px, -2px) !important;
}
#list-view .control-container button.remove {
 margin-right: 0 !important;
}
#list-view .list button:not(:active):hover {
 background-position: right center !important;
}
#list-view .addon-control.update .button-text, #list-view .control-container .button-text {/*隐藏按钮文字*/
 visibility: hidden !important;
 width: 0 !important;
}
#view-port .list menulist .menulist-label {
 -moz-margin-start: 0 !important;
 -moz-margin-end: 0 !important;
}
/*立即更新按钮样式*/
#list-view .list button[class="addon-control update"] {
 padding-left:10px !important;
 background-image:url("data:image/png;base64,") !important;
}
/*编辑按钮样式*/
#list-view .list button[class="addon-control"] {
 background-image:url("data:image/png;base64,") !important;
}
/*选项按钮样式*/
#list-view .list button[class="addon-control preferences"] {
 background-image:url("data:image/png;base64,") !important;
}
/*启用按钮样式*/
#list-view .list button[class="addon-control enable"] {
 width: 26px !important;
 min-width: 20px !important;
 background-image:url("data:image/png;base64,") !important;
}
/*禁用按钮样式*/
#list-view .list button[class="addon-control disable"] {
 width: 26px !important;
 min-width: 20px !important;
 background-image:url("data:image/png;base64,") !important;
}
/*移除按钮样式*/
#list-view .list button[class="addon-control remove"] {
 background-image:url("data:image/png;base64,") !important;
}
/*总是激活|总是禁用按钮样式*/
#list-view .addon-control.state {
}

/*========详细面板#detail-view========*/
/*详细面板按钮样式*/
#detail-view #detail-controls button {
}

/*========更新面板#updates-view========*/
/*安装选中的更新&立即更新*/
#updates-view #update-selected-btn {
 background: none !important;
 color:rgb(64, 64, 64) !important;
 border:none !important;
 border-radius: 0 !important;
 border:1px solid rgb(225, 225, 225) !important;
}
/*隐藏兼容检查警告*/
.view-header.global-warning-container, .global-warning-container.global-warning, .addon.addon-view .warning {
 display:none !important;
}
/*隐藏作者*/
#search-list .creator, #addon-list .creator, #updates-list .creator {
 display: none !important;
}
/*进度条*/
.download-progress {
 background-image: none !important;
}
.progress-bar {
 -moz-appearance:none !important;
 background:-moz-repeating-linear-gradient(0 -45deg, #48A 0px, #89F 5px, #FAFAFA 5px, #FAFAFA 10px) !important;
}
.progress-remainder {
 -moz-appearance:none !important;
 background:#FFF !important;
}
progressmeter {
 -moz-appearance:none !important;
 border:1px solid rgb(245, 245, 245) !important;
 box-shadow:0 0 4px -1px #49F;
}
/*隐藏底部信息*/
#userstyle-footer {
 display:none !important;
}
/*隐藏水平滚动条*/
#view-port scrollbox {
 overflow-x:hidden!important
}
}
