css:
@-moz-document domain("www.dcode.fr") {
h1, h2
{
    margin: 10px 0 5px 0 !important;
    padding: 0 0 0 10px !important;
    width: initial !important;
}

p 
{
    text-indent: 0!important; 
}

p.info, p.aide
{
    background: none !important;
    border-left: 6px solid #e0d5b5 !important;
    margin: 0 0 0 15px !important; 
    padding: 0 0 0 10px !important; 
}

label
{
    display: inline-block !important;
    margin-bottom: 0.5em !important;
    border-radius: 0 !important;
}

input[type="checkbox"] 
{
    vertical-align: middle;
    margin-left: 5px !important;
    margin-right: 5px !important;
}

button, input[type="button"]
{
    border-radius: 5px !important;
    border: #D0BE90 solid 1px !important; 
    background: linear-gradient(#fcf8ea, #f8efd3) !important;
    color: #63562E !important;
    font: normal 11px/22px Arial, Helvetica, sans-serif !important;
    margin-top: 3px !important; 
    padding: 1px 4px 1px 4px !important;
    text-transform: uppercase !important;
    height: initial !important;
}

button:hover, input[type="button"]:hover
{
    background: #fcf8ea !important;
    cursor: pointer !important;
    border-color: #63562E !important;
}

label.nostar
{
    margin-bottom: 0 !important;
}

table
{
    border-collapse: collapse !important;
    margin-bottom: 5px !important;
}

td
{
    padding: 0 !important;
    vertical-align: top !important;
}

img[src="images/dcode.png"], #announcement, .ad, .pub
{
    display: none !important;
}

#top, #haut
{
    background: transparent !important;
    height: 0px !important;
}

#bottom, .bas, #body
{
    background: transparent !important;
}

#bottom, .bas
{
    margin-top: 10px !important;
}

#left, #gauche, #float_left, #float_gauche
{
    width: 320px !important;
}

#center, #milieu
{
    padding: 0px 15px;
    margin: 0px;
}

img[src="/include/img/href.gif"]
{
    margin-left: 5px !important;
}

span[itemprop="applicationCategory"]
{
    font-style: italic !important;
}

p.def
{
    margin: 8px !important;
    width: initial !important;
}

#right, #droite
{
    padding: 0px !important;
}

#right ul, #droite ul
{
    padding: 0 0 0 10px !important;
}

#logo, #lang_select
{
    height: 22px !important;
}

#logo, #title, #titre, #lang_select
{
    line-height: 22px !important;
    margin-bottom: 10px !important;
}

#logo, #lang_select 
{
    margin-top: 10px !important;
}
}
