css:
@-moz-document domain("torrentfreak.com"), regexp("https?://disqus\\.com/embed/.*=torrentfreak.*") {
#discovery {
padding-left:16px!important;
}

#entry-111343 > div.entry-content > table > thead > tr > th:nth-child(2) {
padding:.75em 0 .75em .4em !important;
}

#feature {
border-style:none!important;
}

#feature-box {
background:rgba(0,0,0,.9)!important;
border-bottom-right-radius:4px!important;
border-color:red!important;
}

#logo {
background:rgba(0,0,0,.9)!important;
border-bottom-left-radius:6px!important;
border-bottom-right-radius:6px!important;
}

#main {
background:rgba(0,0,0,.9)!important;
border-top-left-radius:16px!important;
border-top-right-radius:16px!important;
}

#sidebar > aside.widg.widget_text,.hidden,.nav-toggle {
display:none!important;
}

* {
color:hsl(0,0%,78%)!important;
font-weight:bold!important;
transition:none!important;
}

.btn {
box-shadow:none!important;
}

.btn,.postpage-nav a>span:not(:hover),.btn-icon {
background:#ooo!important;
text-shadow:none!important;
}

.context-card h3 {
padding-bottom:2px!important;
padding-left:2px!important;
}

.entry,.hentry,.pbx {
border-color:transparent!important;
}

.entry-kilo .entry-title-wrapper {
padding-left:1em!important;
}

.entry-link h1,a .flag {
color:#fff!important;
}

.entry-link:hover {
transform:none!important;
}

.entry-link:visited h1 {
color:#777!important;
}

.entry-title,.dropdown-menu .active>a,.dropdown-menu .active>a:hover,.dropdown-menu li>a:hover {
background:none!important;
}

.entry-title-wrapper,.entry-nano .entry-title {
background:rgba(0,0,0,.7)!important;
border-top-right-radius:12px!important;
}

.flag-dark {
background:#222!important;
}

.flag-dark .flag-end:after {
border-bottom:15.3px solid #222!important;
}

.flag-dark .flag-end:before {
border-top:15.3px solid #222!important;
}

.flag-dark:after {
border-top:9px solid #111!important;
}

.flag-featured {
background:red!important;
}

.flag-featured .flag-end:after {
border-bottom:15.3px solid red!important;
}

.flag-featured .flag-end:before {
border-top:15.3px solid red!important;
}

.flag-featured:after {
border-top:9px solid darkred!important;
}

.textarea-wrapper,.post-actions,body.dark .input-wrapper input {
background:#141414!important;
}

.tooltip {
background:#000!important;
box-shadow:0 0 5px rgba(255,0,0,.3)!important;
}

.upvoters-outer:before,.upvoters-outer:after {
z-index:-1!important;
}

.widg-popular-link:hover {
transform:none;
}

.widg-popular-post-title {
display:block!important;
line-height:22px!important;
}

.widg-topcomments {
background:#000!important;
border-radius:4px!important;
box-shadow:0 0 5px rgba(255,0,0,.5)!important;
}

.widg-topcomments-post-title {
padding-left:8px!important;
}

.widg-topcomments-post-title:hover,.nav-link:hover,.stats a:hover,.widg-popular-link:hover .widg-popular-post-title,.entry-link:hover h1 {
transform:scale(1.04)!important;
}

:not(html):not(body):not(textarea):not(input):not([class*="flag"]):not(#feature-box):not(.caret):not(.entry):not(.hentry):not(.pbx) {
border-color:rgba(255,0,0,.2)!important;
}

a:not(:before) {
display:inline-block!important;
}

a,a *,html body.dark .full-profile,html body.dark .post-content .post-menu>li>a,html body.dark .post-content header .parent-link,.publisher-anchor-color a,html body.dark .nav>ul>li>a,html body.dark .nav>ul>li>div>a {
color:red!important;
text-decoration:none!important;
transition:none!important;
}

a.widg-topcomments-comments {
background:url(http://i947.photobucket.com/albums/ad312/anagrammarlogos/TF-Red-Bubble_zps8k5o4spm.png) no-repeat!important;
background-size:40.5px 46px!important;
color:#fff!important;
opacity:.7!important;
}

a.widg-topcomments-comments:hover,a.btn:hover {
opacity:.9!important;
transform:scale(1.06)!important;
}

a.widg-topcomments-comments:hover,a:hover .flag {
color:#fff!important;
text-shadow:none!important;
}

a:focus {
outline:none!important;
}

a:hover {
transform:scale(1.03);
}

a:hover,a:hover *,body .nav-link:hover,html body.dark .full-profile:hover,html body.dark .post-content .post-menu>li>a:hover,html body.dark .post-content header .parent-link:hover,.entry-link:visited:hover h1,.publisher-anchor-color a:hover,html body.dark .nav>ul>li>a:hover,html body.dark .nav>ul>li>div>a:hover {
color:#151312!important;
text-shadow:-1px -1px 0 red,1px -1px 0 red,-1px 1px 0 red,1px 1px 0 red,0 0 6px hsla(360,100%,50%,.9)!important;
}

body .post-content .realtime .btn .indicator {
background:red!important;
top:0!important;
}

body.dark #footer .logo a {
height:16.5px!important;
margin-right:6px!important;
}

body.dark .alert.info,body.dark .alert.realtime {
background:#000!important;
box-shadow:inset 0 0 5px rgba(255,0,0,.3)!important;
}

body.dark .nav {
padding-left:6px!important;
padding-right:6px!important;
}

html body.dark a .label-count {
color:red!important;
}

html body.dark a:hover .label-count {
color:#000!important;
}

html,body,body.dark .label-count,.postpage-nav a>span,.postpage-nav>span,#sidebar,#wrap-page,#topbar,.pbx-alt,#message,.dropdown-menu,.upvoters-outer:before,.upvoters-outer:after,body .follow-btn.private {
background:#111213!important;
}

html,body,textarea:focus,select:focus,input[type="date"]:focus,input[type="datetime"]:focus,input[type="datetime-local"]:focus,input[type="email"]:focus,input[type="month"]:focus,input[type="number"]:focus,input[type="password"]:focus,input[type="search"]:focus,input[type="tel"]:focus,input[type="text"]:focus,input[type="time"]:focus,input[type="url"]:focus,input[type="week"]:focus {
border-color:red!important;
}

input[type="submit"] {
background-color:red!important;
border-color:red!important;
}

input[type="submit"]:hover {
opacity:.9!important;
}

p a {
display:inline-block!important;
margin:0 2px!important;
}

table.css {
border-top:1px solid!important;
}

table.css * {
background-color:#000!important;
}
}
