css:
@-moz-document domain("www.nova.org.au") {
/* WiP things
** navbar (search button, text borders, more
** back to top button
** about block
** related block
** subscribe block
** I don't really like the category pages most popular colors... might change
*/







/* Body 
*  - Changes essay background to a dark grey
*  - Changes text to a light grey
*/
body {
    color: #777;
    background-color: #171717;
}
/****** FRONT PAGE ******/
#main > div.hero.Dark > div.bg-stretch > span > span:nth-child(1) > img {
	height: 5000 !important;
    width: 5000 !important;
    /* these numbers match the new image's dimensions */
    padding-left: 5000px !important;
    padding-top: 5000px !important;
    background: #252525;
}
/****** END FRONT PAGE ******/


/****** CATEGORY PAGES ******/
.content-form .form-control {
	/* What are you curious about? */
    border: 1px solid #333;
    color: #FFF;
    background: #333;
}
.content-form .submit {
    background: #2D2D2D;
}
.content-form .submit:hover {
    background: #3C3C3C;
}
.section-wrap {
	/* Latest topics */
    background: #252525;
}
.gray {
    background: #252525;
}
/* Most popular */

.most-popular .caption {
    background: #252525;
    border: solid #252525;
}
.most-popular .caption .caption-bar {
    background: #252525;
}
.most-popular .caption .caption-bar .cell.link-holder {
    background: #252525;
}
body .most-popular .slide-wrap .caption-bar .link-holder a {
    color: #ccc !important;
}
.most-popular .caption:hover, .most-popular .caption.hover {
    background: #252525;
}
body .pager-next a {
    border: 1px solid #777777;
    color: #272727;
}
.load-more {
    border: 1px solid #171717;
}
.pager li > a:hover, .pager li > a:focus {
    text-decoration: none;
    background-color: #232323;
    color: #CCC;
}
.pager li > a, .pager li > span {
    background-color: #818181;
}
/****** videos ******/
.visual-gallery .video-caption .video-bar .time, .video-slider .video-caption .video-bar .time {
    background: #252525;
	border-top: 0px solid #777;
}
.btn-watch {
    border: 1px solid #777;
    color: #777;
    background: rgba(0, 0, 0, 0.8);
}
.btn-watch:hover, .btn-watch.hover {
    padding: 0 18px 0 32px;
    color: #777;
}
.section-heading .link-holder a {
    color: #777;
}
/****** end videos ******/

/****** our picks ******/
.visual-gallery a, .video-slider a {
    color: #777;
}
.green {
    background: #171717;
}
.green.caption .caption-bar .cell.link-holder {
    background: #171717;
}
.visual-gallery .caption.green .caption-bar, .video-slider .caption.green .caption-bar {
    background: #171717;
}

.royalblue {
    background: #171717;
}
.royalblue.caption .caption-bar .cell.link-holder {
    background: #171717;
}
.visual-gallery .caption.royalblue .caption-bar, .video-slider .caption.royalblue .caption-bar {
    background: #171717;
}

.purple {
    background: #171717;
}
.purple.caption .caption-bar .cell.link-holder {
    background: #171717;
}
.visual-gallery .caption.purple .caption-bar, .video-slider .caption.purple .caption-bar {
    background: #171717;
}
/****** end our picks ******/
/****** END CATEGORY PAGES ******/

/****** SNEAK PEAK ******/
/* This also applies some things to the infographics section*/
.info-blocks .caption {
    border: 0px solid #ccc;
    background: #252525;
}
.info-blocks .caption .title {
    color: #777
}
.info-blocks .caption .bar {
    background: #252525;
}
/****** END SNEAK PEAK ******/

/****** NAVBAR ******/
	/* SEARCH BUTTON */
	nav[class*="search"] #header.affix .search-opener {
    background: #777777;
	}


/****** END NAVBAR ******/



/****** QUOTE BOX ******/
div[class*="surround cat"] .quote-box p span {
    background: #333;
}
.quote-box {
    color: #818181;
}
div[class*="surround cat"] .quote-box:after {
    background: #333333;
}
/****** END QUOTE BOX ******/

/****** PAGE HEADING ******/
.cat_17 .page-heading {
    color: #818181;
}
/****** END PAGE HEADING ******/

/****** GREEN SECTION ******/
div[class*="surround cat"] .green-section, div[class*="surround cat"] .green-section:after, div[class*="surround cat"] .green-section:before, div[class*="surround cat"] .faq:after, div[class*="surround cat"] .faq:before, div[class*="surround cat"] .faq {
    background: #282828;
}
.section-heading .heading, .section-heading .heading:after {
    color: #818181;
	border-bottom: 1px solid #818181;
}
.section-heading .heading:after {
    background: #818181;
}
.section-heading {
    border-bottom: 1px solid #818181;
}
.cat_17 .content-list .icon-bullet {
    color: #818181;
}
/****** END GREEN SECTION ******/

/****** BACK TO TOP BUTTON ******/
a[class*="back-to-top"] .back-to-top {
    background: #333;
}
#footer a {
    color: #CCC;
}
a[class*="back-to-top"] .back-to-top:hover {
    background: #282828 !important;
    color: #777777 !important;
}
/****** END BACK TO TOP BUTTON ******/

/****** ACCORDIAN ******/
.accordion .slide {
    background: #232323;
}
div[class*="surround cat"] .accordion li.active .opener {
    background: #171717 !important;
	border-color: #232323;
}
div[class*="surround cat"] .accordion .opener {
    border-color: #232323;
    color: #818181;
}
div[class*="surround cat"] .accordion .opener:hover {
    background: #333;
    color: #818181;
}
div[class*="surround cat"] .accordion .opener:after, .accordion .opener:before {
    background: #333;
}
/****** END ACCORDIAN ******/

/****** INTERACTIVE ******/
.stem-cell-interactive {
    background-color: #333;
    border: 3px solid #333;
}
/****** END INTERACTIVE ******/

/****** THUMBNAIL ******/
.thumbnail .caption {
    background: #333;
	color: #CCC;
}
.thumbnail {
    background-color: #333;
}
.thumbnail .caption a {
    color: #CCC;
}
/* Below is for a figure */
.article-section figcaption {
    background: #171717;
}
.article-section figcaption a {
    color: #CCC;
}
/****** END  THUMBNAIL ******/

/****** SOUNDCLOUD ******/
element.style {
    background-color: #171717;
}
/****** END SOUNDCLOUD ******/

/****** SIDEBAR ******/

	/* Set navigation sidebar while reading to dark grey */
	div[class*="surround cat"] .side-menu .slide-block {
		background: #171717;
	}
	/* Set sidebar text to light grey */ 
	div[class*="surround cat"] .side-menu .side-nav-list .point {
		color: #777;
	}
	/* Set current sidebar location to white-grey */
	div[class*="surround cat"] .side-menu .side-nav-list .point.anchor-active {
		color: #A7A7A7;
	}

	div[class*="surround cat"] .side-menu .side-nav-list li:after {
		background: #333;
	}

	div[class*="surround cat"] .side-menu .side-nav-list .point:after {
		background: #777777;
	}

	div[class*="surround cat"] .side-menu .side-nav-list li:first-child:before, .side-menu .side-nav-list li:last-child:before {
		background: #333;
	}
	/* *BEGIN* Page at a glance box */
		div[class*="surround cat"] .side-menu .opener-box .opener {
			color: #333;
		}
		div[class*="surround cat"] .side-menu .opener-box {
			background: #777777;
		}
		div[class*="surround cat"] .side-menu .opener-box {
							background: #777777;
																				}
		div[class*="surround cat"] .side-menu .opener-box {
			color: #333;
		}
		div[class*="surround cat"] .side-menu .opener-box .opener:hover {
			background: #616161;
		}
	/* *END* page at a glance box */
		
	/* *BEGIN* Share */
		div[class*="surround cat"] .side-menu .title {
			color: #333;
		}
		div[class*="surround cat"] .side-menu .title {
			background: #777777;
		}
	/* *END* Share */

/****** END SIDEBAR ******/

/****** BEGIN GLOSSARY ******/
div[class*="surround cat"] .glossary {
    background: #282828;
}
.glossary .glossary-popup .main-title {
    background: #333;
}
.glossary:hover .glossary-popup .main-title {
    background: #282828;
}
div[class*="surround cat"] .glossary .glossary-popup:after {
    background: #333;
}
/****** END GLOSSARY ******/

div[class*="surround cat"] h3, h4 {
    color: #777777;
}
div[class*="surround cat"] .intro, h3 i {
    color: #777777;
}
div[class*="surround cat"] .side-menu .list-opener, div[class*="surround cat"] .side-menu .icon-share, div[class*="surround cat"] h2.intro:after {
    background: #777777;
}

/****** BOTTOM SECTION ******/
.step-section .radio-form .col-wrap {
    background: #353535;
}
div[class*="surround cat"] .step-section:after {
    background: #232323;
}
.btn-default {
    color: #ddd;
    border-color: #ddd;
}
div[class*="surround cat"] .btn:hover {
    background: none repeat scroll 0 0 #484848;
    border-color: #ddd;
    color: #fff;
}
/****** END  BOTTOM SECTION******/

/****** TAGS ******/
div[class*="surround cat"] h3 i {
    color: #777777;
}
.content-tags a {
    color: #818181;
}
.content-tags a:hover {
    border-color: #ddd;
    color: #ddd;
}
div[class*="surround cat"] .content-tags li:after {
    background: #f8f8f8;
}
.tags-section .tags-list a {
    color: #777777;
}
/****** END TAGS ******/

/****** ABOUT ******/
.about-block:before {
    background: #131313;
}
.about-row a {
    color: #777;
    border-bottom-color: #222;
}
/****** END ABOUT ******/

/****** SUBSCRIBE NOW ******/
#footer {
    border-top: 0px solid #8E8E8E;
}
.subscribe-row {
    background: #171717;
}
/****** END SUBSCRIBE NOW ******/

/****** BOTTOM LINE ******/
.bottom-line {
    background: #171717;
}
/****** END BOTTOM LINE *****/

.back-to-top {
    background: #313131 !important;
}
.back-to-top:hover {
    background: #171717 !important;
}
#footer a:hover {
    color: #777 !important;
}
}

