css:
@-moz-document domain("booklog.jp") {
/* * * * * * * * * * * * * * * * *
 　　ブログ型レビューを見やすく・改
* * * * * * * * * * * * * * * * */

#lockArea > aside.ad-side-top,
.ad-footer,
.ad-side-bottom,
#users-header-ad {
    display: none !important;
}
/* プロフィール */

#users-header,#users-header #users-header-profile-wrapper {
    height: 2px !important;}

#users-header #users-header-profile-wrapper #users-header-profile {
    padding: 10px 20px 0;
    height: 550px;
    min-width: 300px;
    width: 30%;
    margin-right: 0px;
    position: absolute;
    background-color: #f0f0f0;
    display: block;
    top: 60px;
    right: 35px;
    z-index:9999;
}
}

@-moz-document regexp("http://booklog.jp/users.+display=blog.*") {
#shelf {
    background-color: #fefef7 !important;
}
#users-header,
.infomation-banner a img,
.disT.w100
/* レビュー下部_項目名 */

,
li.item-area-info-title,
div.item-area > div.item-area-info > ul > li:nth-child(4)
/* Amazonカテゴリ */

,
div.item-area > div.item-area-info > ul > li:nth-child(5)
/* Amazonリンク */

,
div.memo-area > h3,
span.title.vaT {
    display: none !important;
}
#shelf.shelf-blog-area .blog .item-wrapper h2,
#shelf.shelf-blog-area .blog .item-wrapper .review-area,
#shelf.shelf-card-area #card #shelf.shelf-blog-area .blog .item-wrapper .reread-area,
#shelf.shelf-blog-area .blog .item-wrapper .reread-area,
#shelf.shelf-blog-area .blog .item-wrapper .memo-area,
#shelf.shelf-blog-area .blog .item-wrapper .item-header,
#shelf.shelf-blog-area .blog .item-wrapper .review-area .rating-area {
    position: relative;
    margin: 0 0 5px !important;
}
#shelf.shelf-blog-area .blog .item-wrapper h2 {
    background-color: #f5efd4;
    padding: 5px 10px;
    border-radius: 7px;
}
#shelf.shelf-blog-area {
    width: 100% !important;
}
#shelf.shelf-blog-area .blog .item-wrapper h2 a {
    font-size: 18px;
    line-height: 1.8;
}
#shelf.shelf-blog-area .blog .item-wrapper .item-area .item-area-info {
    position: relative;
    padding: 0px 0px 0;
    display: block;
}
#shelf.shelf-blog-area .blog .item-wrapper {
    margin: 0;
    padding: 20px 0 20px;
    border-bottom: 2px #b5b5b5 solid;
}
div.review-area {
    display: inline;
}
/* アイテム画像 */

#shelf.shelf-blog-area .blog .item-wrapper .item-area {
    margin: -5px 0 0px;
    padding: 5px;
    border: 0px #f0f0f0 solid;
    display: block;
    width: 110px;
    height: 140px;
    float: left;
    /*
	position: fixed;
    left: 100px;*/
}
div.more-info-area {
    clear: both;
}
.memo-area {
    display: block !important;
    margin: 0 0 0px;
}
#shelf.shelf-blog-area .blog .item-wrapper .item-area .item-area-image img {
    border: 1px #f0f0f0 solid;
    width: 90px;
}
/* 非公開メモ */

.memo-text,
.review-text {
    color: #666 !important;
    border: none !important;
    display: block;
    font-size: 14px !important;
    line-height: 1.8 !important;
    font-family: "游明朝";
    font-weight: 600;
    line-break: strict !important;
    padding: 3px 15px !important;
}
#shelf.shelf-blog-area .blog {
    margin-left: 140px;
    padding: 10px;
    max-width: 980px;
    width: 70% !important;
}
#shelf.shelf-blog-area .blog .item-wrapper .more-info-area {
    padding: 8px;
    background: #fff;
}
/* レビュー下部 */

div.more-info-status {
    float: left;
}
#shelf.shelf-blog-area .blog .item-wrapper .more-info-area {
    padding: 8px;
    background: rgba(255, 255, 255, 0);
}
#shelf.shelf-blog-area .blog .item-wrapper .more-info-area .more-info-category {
    display: table-cell;
    padding: 0px 10px;
    text-decoration: underline;
    color: #7e7e7e;
    line-height: 1em;
}
div.more-info-tags {
    float: right;
}
/* 書誌情報Popup */

div.item-area {
    position: relative;
    overflow: hidden;
    width: 200px;
}
div.item-area-info {
    position: absolute;
    top: -70px;
    right: 0px;
    background-color: rgba(255, 255, 255, 0.8);
    z-index: 9;
    width: 100%;
    height: 100%;
    -webkit-transition: .3s;
    transition: .3s;
    opacity: 0;
}
div.item-area:hover div.item-area-info {
    opacity: 1;
}
}

@-moz-document regexp("http://booklog.jp/users.+\\?display=front.*"), regexp("http://booklog.jp/users/.+[^/]") {
#shelf {
    position: relative;
    width: 100%;
    min-width: 1000px;
    height: 100%;
    min-height: 100%;
    margin: 0px 300px 0 20px;
}

#shelf.shelf-front-area .shelf-wrapper {
    width: 72% !important;
    max-width: 950px;
}


#front {
    width: 700px !important;
}
#shelf.shelf-blog-area .blog .item-wrapper .item-area .item-area-image img {
    border: 1px #f0f0f0 solid;
    width: 90px;
}
}

@-moz-document regexp("http://booklog.jp/users/.+/archives/1/\\d+") {
#mainArea > div.itemUnit.posR {
    display: block;
    position: absolute !important;
    width: 35%;
    left: 650px;
    top: 30px;
}
}

@-moz-document url("http://booklog.jp/input") {
/* ISBMで登録ページ */

#mainArea h1,
.tab-menu,
#mainArea p.lead,
#input_form > table > tbody > tr:nth-child(4),
aside.ad-footer {
    display: none;
}

}

@-moz-document domain("archive.org") {
/* * * * * * * * * * * * * * * * *
 　　ブログ型レビューを見やすく
* * * * * * * * * * * * * * * * */

#profileArea,
div.favArea {
    display: none;
}
#shelf > div.autopagerize_page_element > div {
    margin-top: 35px;
}
.tableType2 tr td {
    background-color: transparent;
    border-bottom: none;
    padding: 15px;
}
#mainArea {
    float: none;
    width: 90%;
    margin: 0 auto;
}
.tableType2 tr td.thumb {
    width: 70px;
    text-align: center;
    background: transparent;
    cursor: pointer;
}
.ftR {
    float: right !important;
    position: relative;
    bottom: 40px;
    left: 0px;
}
.reviewerinfo {
    height: 0;
    margin: 0 0 0;
}
.ftL {
    display: none;
}
div.reviewDes {
    margin: 10px 0;
    padding: 15px;
    border: none;
    display: block;
    width: 110%;
    background-color: rgba(240, 240, 240, 0.6);
    border-radius: 15px;
}
.reviewTxt,
.tcgray {
    line-height: 1.8;
    font-size: 14px;
    font-family: "游明朝";
    font-weight: 600;
}
.tcgray {
    text-align: right;
}
#shelf.shelf-blog-area .blog div.article h2.title {
    border-radius: 10px;
}
.ftR {
    float: left !important;
    margin-right: 14px;
}
p {
    line-break: strict !important;
}
#shelf.shelf-blog-area {
    width: 760px;
}
#sideArea,
#footer,
#gmocommonheader,
#comment_write,
#socialbtnArea02,
aside.ad-footer {
    display: none;
}
div.b20M.clearFix {
    float: left;
}
#shelf.shelf-blog-area .blog div.article div.image {
    float: none;
}
#shelf.shelf-blog-area .blog div.article div.bookinfo {
    width: 130px;
    margin: 20px 10px 0 0;
}
/* レビュー・メモ 部分 */

#shelf.shelf-blog-area .blog div.article p.description,
#shelf.shelf-blog-area .blog div.article p.memo {
    color: #666;
    background-color: rgba(240, 240, 240, 0.6);
    border-radius: 15px;
    display: inline-block;
    width: 75%;
    font-size: 14px;
    line-height: 1.8;
    font-family: "游明朝";
    font-weight: 600;
    line-break: strict !important;
    padding: 15px;
}
div.article ul.option {
    font-size: 10px;
    width: 95%;
    padding: 0 0 5px 0;
    text-align: right;
    color: #EFDF96;
    clear: both;
}
#shelf.div.autopagerize_page_element.div.div:nth-child(3).b,
.adnetArea,
#div-gpt-ad-1432265609134-4 {
    display: none !important;
}
}

@-moz-document url-prefix("http://booklog.jp/edit/") {
/* * * * * * * * * * * * * * * * *
 　　非公開メモを書きやすく
* * * * * * * * * * * * * * * * */

body {
    overflow: hidden;
}
#page {
    width: 90%;
}
#page .breadcrumbs {
    margin: 0px -40px 10px;
    text-align: left;
}
#page > ul > li:nth-child(3),
#page > ul > li:nth-child(2) {
    font-size: 20px;
}
#page .breadcrumbs li span.delimiter {
    vertical-align: text-top;
}
.tableType4 td {
    font-size: 13px;
    padding: 5px !important;
}
#mainArea {
    float: left;
    padding-left: 30px;
    width: 850px;
}
.tableType4 th {
    padding: 0px 15px 0px;
}
#memo {
    line-height: 1.5;
    /* font-family: "02うつくし明朝体";*/
}
textarea#review,
textarea#memo {
    min-height: 250px !important;
    min-width: 550px !important;
}
#edit,
#editItemArea,
#editItemArea > div.itemInfo.clearFix > ul,
#mainArea > div.tab-menu.b0M.posR > div,
.adnetArea,
.tcblack,
#tab1 > form > table > tbody > tr:nth-child(7) > td > ul,
/*#tab1 > form > table > tbody > tr:nth-child(2),/* 評価★ */

#tab1 > form > table > tbody > tr:nth-child(4),
/* 全体公開レビュー *
#tab1 > form > table > tbody > tr:nth-child(5)/* タグ */

#recommentTagsArea > p,
#tab1 > form > table > tbody > tr:nth-child(5) > td > ul
/*タグ周り*/

,
#tab1 > form > table > tbody > tr:nth-child(6)
/* 登録日時 */

{
    display: none;
}
textarea#tags {
    height: 18px;
}
div#recommentTagsArea {
    margin: 0;
    padding: 5px 0 0px;
    border: none;
    font-size: 10px;
    width: 95%;
}
.inputselect {
    float: left;
}
h4 {
    margin: 9px 10px 0px;
}
#read_at > div.t10M {
    position: absolute;
    top: 10px;
    right: 14px;
}
.t10M,
.t15M {
    margin-top: 0px !important;
    margin: 10px;
    float: right;
    height: 0px;
}
.t80P {
    padding-top: 65px !important;
}
#mode-edit > div.global-navi-login.clearFix {
    position: absolute !important;
    height: 0px;
}
.tab-menu {
    margin: -5px 0 20px;
    width: 100%;
    height: 30px;
    border-bottom: 3px #1e1e1e solid;
}
.buttons {
    display: block;
    bottom: -90px;
    right: 9px;
    position: absolute;
}
div.message {
    width: 85%;
    border: 2px solid #4ea6cc;
    border-radius: 25px;
    margin: -15px 0 10px;
    padding: 5px 40px;
    font-size: 15px;
}
}
