css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.marca.com") {
  body{
    font-family: sans-serif !important;
  }
  .news-item{
    height: 232px !important;
  }
  .news-item > figure {
    margin-bottom: 0px;
    width: 300px;
    position: absolute;
    top:14px;
    max-height: 190px;
    right:20px;
    
  }
  .news-item > figure img{
    border-radius:3px;
    border: 1px solid #bbb;
  }
  
  .section-title-group, .js-headline, .summary-lead{
    padding-right: 315px !important;;
    text-align: left !important;;
  }
  .content .byline-tools{
    float:right !important;
    padding-left: 20px !important;
    margin-right: 20px !important;
    margin-top: 20px !important;
  }
  
  .content.cols-30-70 > *{
    float:none ;
  }
  
  .js-headline{
    font-size: 48px !important;;
  }
  .inside.fixed-down, .inside.fixed-up{
    display: none;
  }
  
  aside.js_fixed[style*="fixed"], aside.js_fixed[style*="absolute"]{
    display: none !important;
  }
  aside.js_fixed{
    margin-top: 0px !important;
  }
  .tab-title{
    display: none !important;
  }
  .futbol-section .kicker {
    display: none !important;
  }
  .futbol-section, .motor-section, .baloncesto-section{
    background-color: #f2f2f2 !important;;
  }
  .container{
    padding: 0px 15px;
    border-right: 1px solid #ddd;
    border-left: 1px solid #ddd;
  }
  .item-tags li a{
    border-radius:3px !important;
  }
  .section-title-group{
    margin-top: 25px;
  }
  .suscripciones-noticias{
    display:none;
  }
  .date {
    margin-top: 2px !important;
  }
  p{
    font-family: sans-serif !important;
  }
  .nav-toggle{
    display:none;
  }
  .promo-header{
    display: none
  }
  .site-logo a{
    height:50px !important;;
    width:150px !important;
  }
  .site-logo .logo{
    width:170px !important;
  }
  .site-logo{
    margin:10px !important;
  }
  .site-logo time{
    margin-top:8px !important;;
  }
  .site-highlights {
    margin: 8px 0px 0px !important;
  }
.kicker{
    display:none !important;
  }
.content-ad{
    display: none !important;
  }
}
