css:
@-moz-document url-prefix("https://ing.dk/") {
/* front page */

body
{
	background-color: #000000 !important;
}

#page
{
	background-color: #403830 !important;	
	color: #909090 !important;
}

#page #header a
{
	color: #909090;
}
	
.frontpage
{
	background-color: #000000 !important;
	color: #E0D8D0;
}

#main
{
	background-color: #403830 !important;
	color: #E0D8D0;
}

#main a
{
	color: #E0D8D0;
}

#main a:hover
{
	text-decoration: underline;
}

.frontpage-section
{
	background: #484440;
}

.flex-horizontal-viewport ul.slides li
{
	background: #504C48 !important;
}

.mdl-grid
{
	background: #404754 !important;;
}

.aq-body
{
	background-color: #504840 !important;
	color: #C0B8B0;
}

#mini-panel-campaign_focus_zone a
{
	color: #304050 !important;	
}

#advertorial-page .with-sidebar:nth-child(-n+2)>.panel-col-first
{
	background-color: #403830;
}

.section.blogs-deck
{
	background-color: #303030 !important;	
}

div.article-quoted-pane.grid
{
	background-color: #003040 !important;
}

.box_border_shadow
{
	background-color: #383028 !important;
}	

/* annoyances */
section.banner
{
	display: none !important;
}

#eas_layerpop_image_content
{
	display: none !important;
}

.eas_layerpop
{
	display: none !important;
}

/* article page */

section.teaser p
{
	color: #A4988C;
}

section.body p
{
	color: #E0D8D0;
}

section.body p a
{
	color: #F0B070 !important;
}

.submitted a
{
	color: #F0B070 !important;
}

h2.comment-title a
{
	color: #F0B070 !important;
}

.comment-wrapper p
{
	color: #F0B070;
}

textarea, input
{
	background-color: #504840 !important;
	color: #F0B070;
}

section div.article-link
{
	display: none;
}

section aside p
{
	color: #C0C4C8 !important;	
}

/* blog page */

#main div div div section div div div.body p
{
	color: #E0D8D0 !important;
}

div.body p
{
	color: #E0D8D0 !important;
}

div.body p a
{
	color: #F0F0FF !important;
}
}
