css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fastpic.ru") {
  html,
  body,
  div[id^='b_pr'],
  div[id*='skin_'] {
	background: none !important;
	width: 100% !important;
	padding-top: 0 !important;
	margin: 0 auto 0 auto !important;
  }
  
  #content {
	background: none !important;
	margin: 0 auto 0 auto !important;
	padding: 0 !important;
  }
  
  #mainContainer {
    width: 900px !important;
    background: #fff !important;
	margin: 10px auto 1em !important;
  }
  
  #footerContainer,
  #brndfooter,
  img[style*="absolute"],
  div[id*='skin_'],
  div[id^='b_pr'],
  #rightside,
  #footer-container,
  [id^=left],
  [id^=right],
  #left-bottom,
  #right-bottom,
  #begun-area,
  noindex,
  iframe,
  [id='content'] > :nth-child(1) {
    display: none !important;
  }
  
}
