css:
@-moz-document domain('www.goodreads.com') {

body {color:#000; background-color:#fffffa;}
body, ol, ul, td, th {font-size:14px;}
body, p, ol, ul, td, th, .bookTitle, .readable, .readable p, .readable ol, .readable ul, .readable td, .readable th, .aboutRightColumnWide, .aboutRightColumnWide section ul, .comment .reviewText, .bigGreyBox, .grey500Box, .big450Box, .updateAction, .authorName, .aboutAuthorInfo {font-family: Georgia, serif;}
.mainContentFloat, tr.update td, .authorName, .content620 .smallText, .content300 ul, #updateList .authorBlogPost .body, .authorBlogPost .body, .mediumText, .aboutRightColumnWide section ul,.aboutRightColumnWide section, .comment .reviewText {font-size:16px;}
input[type=text], input[type=search], input[type=email], input[type=password], textarea {font: 14px Arial, serif;}
#siteheader .nav li {font-size:15px;}
.secondaryTextBottomPadding, .secondaryTextBlog {color:inherit;}
.wtrButtonContainer .wtrShelfMenu>.wtrShelfList>ul.wtrExclusiveShelves>li a.wtrExclusiveShelf, .wtrButtonContainer .wtrShelfMenu>.wtrShelfList>ul.wtrExclusiveShelves>li button.wtrExclusiveShelf, .wtrButtonContainer .wtrShelfMenu ul, .wtrButtonContainer .wtrShelfMenu {font-family:Arial;}
h2.brownBackground a, .bigBox .h2Container h2 {font-size:15px;}
}
