css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("marktplaats.nl") {
#page-wrapper{background-color:#aaaaaa}@media  (min-width: 46.875em){#page-wrapper{background-color: rgb(30, 40, 60);}}
#search-content input[type="text"] {font-size: 0.813em; line-height: 1.2em; background-color:#FFFFFF;}

#search-content {background-color: rgb(147, 164, 194);}

#header-edge {height: 0px;}

#main-content {overflow: hidden;}

.aanbieding-widget-container {height: 0px; position: relative; visibility: hidden;}

.handigeLinks {height: 0px; visibility: hidden;}

#main-content, #seller-own-ads-cas, #seller-own-ads, .box-content, #search-attributes.filters, .l-main-left .listing {
    background-color: rgb(225, 232, 245);
}

#footer {
    clear: both;
    overflow: hidden;
    border-top: 1px solid rgb(147, 164, 194);
    background-color: rgb(147, 164, 194);
}

.fixed-group-item .image, .listings a, .image-viewer-wrapper, #large-image-viewer{border: none;}

#overlay, .overlay {
    background-color: rgb(0, 0, 0);
}
 
a {color: rgb(103, 131, 166);}
html, body {color: rgb(121, 121, 121);}


                              
}
