css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gletschersau.de") {

body {
background: none repeat scroll 0% 0% rgb(27, 12, 17);
color: rgb(202, 202, 202);
font-family: "Titillium",Arial,sans-serif;
font-weight: 0;
}

#header {
background: url(http://www.gletschersau.de/wp-content/uploads/2015/04/ac10_9286.jpg);
background-size: 105%;
background-position: 15% 45%;
}

.page-title {
    background: none;
    border-bottom: 1px solid rgb(102, 102, 102);
    color: rgb(102, 102, 102);
    font-size: 16px;
    font-weight: 600;
    text-transform: uppercase;
}

.s1 .sidebar-top {
    background: none repeat scroll 0% 0%  rgb(224, 120, 35);
}

.col-2cl .main-inner, .content, .sidebar-content, .main-inner.group, .themeform input[type="text"], .themeform input[type="password"], .themeform input[type="email"], .themeform textarea {background: none repeat-y scroll right 0px rgb(33, 19, 5);}

.themeform input[type="text"]:focus, .themeform input[type="password"]:focus, .themeform input[type="email"]:focus, .themeform textarea:focus {color: rgb(201, 201, 201);}

.wpcf7 p {color: rgb(134, 134, 134);}

#wrapper {
border-color: rgb(189, 190, 190);
border-right: 2px solid rgb(224, 120, 35);
border-left: 2px solid rgb(224, 120, 35);
}

.container-inner {
border-right: none;
border-left: none;
}

#page .container-inner {
background: none repeat-y scroll right 0px rgb(33, 19, 5);
border-bottom: 2px dashed rgb(224, 120, 35);
}

h1, h2, h3, h4, h5, h6 {color: rgb(255, 160, 21);}

.entry table tr.alt {background: none repeat scroll 0% 0% rgb(41, 41, 41);}
.entry table td {border-bottom: 0px solid rgb(241, 241, 241);}

.sidebar .post-nav li.previous a, .sidebar .post-nav li.next a, .widget_archive ul li, .widget_categories ul li, .widget_links ul li, .widget_meta ul li, .widget_recent_comments ul li, .widget_recent_entries ul li, .widget_rss ul li, .widget_pages ul li a, .widget_nav_menu ul li a, .alx-tab li, .alx-posts li, .post-list .post-row  {
    border-bottom: 1px solid rgb(55, 42, 25);
}

.sidebar .post-nav li a{
border-top: 1px solid rgb(55, 42, 25);
}

#commentform {background: none repeat scroll 0% 0% rgb(48, 34, 19);}
.themeform input[type="text"], .themeform input[type="password"], .themeform input[type="email"], .themeform textarea {
    border: 2px solid rgb(63, 49, 35);
}

#footer {
width: 104%;
margin-left: -2%
}

#footer-bottom {
background: none repeat scroll 0% 0% rgb(27, 12, 17);
z-index: -14;
}

#footer-bottom #back-to-top {background: none;}

.boxed #page {
    background: none repeat scroll 0% 0% rgb(234, 234, 234);
    padding-bottom: 0px;
}

#fancybox-overlay {background-color: rgb(10, 10, 10) !important;}

}
