css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dobrochan.com"), domain("dobrochan.ru") {
  body {
    color: #111;
    font-family: sans-serf;
    line-height: 1.618em;
  }
  
  .reflink {
    font-weight: inherit;
    font-size: inherit;
  }
  
  a {
    text-decoration: none;
  }
  
  .postername {
    color: inherit;
  }
  
  .reply {
    padding: 4px;
    border-radius: 3px;
  }
  
  .message {
    padding: 0 20px;
  }
  
  .thread {
    width: 70em;
    max-width: 100%;
    margin: auto;
  }
  
  hr {
    width: 20em;
  }
}
