css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.yad2.co.il/Nadlan/ViewImage.php"), url-prefix("http://www.yad2.co.il/Cars/ViewImage.php") {
    table[style="direction: rtl; margin-bottom: 0px; width: 792px; margin-right: auto; margin-left: auto; border: 0px; border-collapse: collapse;"] > tbody > tr:first-child > td:first-child
    {
        display:none !important;
    }
}

@-moz-document url-prefix("http://www.yad2.co.il/Nadlan/sales.php") {
    div#lastsearch_block + table,
    div.right_column > script + div
    {
        display: none !important;
    }
}

@-moz-document domain("www.yad2.co.il") {
  	#noContainer_0, #noContainer_1, #noContainer_2,
    #Hotpics, .bannerBetweenTables_main,
    .right_column img[alt~=Platinum], .platinum.pie,
    #tiv_main_table .main_table_wrap,
    #tiv_main_table .show_results_from,
    #tiv_main_table .mainTable_top,
    .walla_strip,
    a[name=מדריכים] + div, a[name=מדריכים] + div + div,
    iframe,
    div[id^=Fusion_holder],
    div.right_column > div:first-child:not(.clear),
    div[style="width: 980px;height: auto;margin: 5px auto;"],
    div#lastsearch_block + script + div,
    div#top_logo_sponsor,
    div#top_logo_sponsor + div,
    div#GamboBanner + div,
    div.Desktop_Toster + div,
    #top_banners_spacer + div,
    #top_banners_spacer + div[style^="background-color: rgb(221, 221, 221)"],
    table.innerDetails_table > tbody > tr:first-child > td:nth-child(2),
    table.innerDetails_table > tbody > tr:first-child > td:first-child > div > table > tbody > tr:first-child > td:first-child > div:nth-child(3),
    div.Desktop_Prestitial + div
    {
      display: none !important;
    }
    table.main_table > tbody > tr:not([id^="tr_Ad"]):not([id^="tr_Info"]){
      display: none !important;
    }
    table.main_table > tbody > tr:not([id^="tr_Ad"]):not([id^="tr_Info"]):first-child {
      display: table-row !important;
    }
    .Info iframe {
      display: block !important;
    }/*
    #mainFrame .right_column {
      width: 100%;
    }*/
    #lastsearch_block + script + table[width="980"] {
      position: absolute;
      z-index: -100;
      opacity: 0;
    }
    #lastsearch_block + script + table[width="980"]:after {
      content: '';
      position: absolute;
      z-index: -99;
      width: 100%;
      height: 70px;
      top: 0;
      background-color: white;
    }
    div.left_column > div:not(.block_ad_options):not(#top_login_block):not(.login_block),
    div#ad_martef,
    div#ad_martef + div + div + div + style + table,
    div.left_column + div.clear + div,
    div.clear,
    div[id^=bottom_banner] > div > iframe
    {
        display: none !important;
    }
    #wrap > div:first-child,
    #wrap > div:first-child[style*='display: inline-table'],
    #GamboBanner,
    #rtower
    {
      display: none !important;
    }
    #mainIndex > .right_column + .left_column + div[class='clear'] + div {
      position: absolute;
      z-index: -100;
      opacity: 0;
    }
    #mainIndex > .right_column + .left_column + div[class='clear'] + div:after {
      content: '';
      position: absolute;
      z-index: -99;
      width: 100%;
      height: 70px;
      top: 0;
      background-color: white;
    }
    div.logo {
        position: relative;
    }
    div.logo:after
    {
        content: '';
        background: transparent url('http://78.media.tumblr.com/09755eaf172f07c29ead8404fbc16c35/tumblr_mk4kfpcXCm1s8b3bqo1_500.png') no-repeat;
        background-size: 100px 100px;
        position: absolute;
        width: 100px;
        height: 100px;
        top: -20px;
        left: 120px;
        transform: rotate(-15deg);
    }
    div.mador_index_block_wrap
    {
        margin-bottom: 0;
    }
    div.yad1_banners {
        display: none;
    }
}
@-moz-document domain("my.yad2.co.il") {
  script + script + table[width='770'] > tbody > tr:nth-child(2),
  #GamboBanner,
  #rtower,
  #GamboBanner + div[style*='z-index: 5'],
  script[src='//www.googletagservices.com/tag/js/gpt.js'] + script + div
  {
    display: none !important;
  }
}




