/* My Inside Style
   name inside.tensor.ru - Brackets (dark gray)
   url  https://userstyles.org/styles/121920/inside-tensor-ru-brackets-dark-gray
   git  https://github.com/iNovozhilov/InsideTensorUserStyle
*/

@-moz-document domain(inside.tensor.ru) {
   #leftInfoBlocks {
      height: auto !important;
   }
   .event-block-bg,
   .staff-StaffCatalog-ListComponent_eventRibbon,
   .Contragents-DailyInfo__box {
      background-color: #f7f7f7 !important;
   }
   .tape_task_item:hover,
   .tape_task_item_w_hover:hover,
   .employee-EventItem_Component-hover:hover,
   .contacts-MessageListOnStrip__messcont .messageRow:hover,
   .contacts-MessageListOnStrip__messcont .messageRow.row-hovered {
      background-color: #f0f0f0 !important;
   }
   .employee-EventItem_Component-hover .employee-EventItem-msg {
      background-color: #f0f0f0 !important;
      box-shadow: 0 0 6px #f0f0f0 !important;
   }
   .contacts-MessageListOnStrip__messcont .messageText,
   .contacts-MessageListOnStrip__messcont .ws-browser-row-options-block {
      background-color: #ededed !important;
   }
   .contacts-MessageListOnStrip__messcont .ws-browser-row-options-block {
      box-shadow: -2px -3px 5px 1px #ededed !important;
   }
   .contacts-MessageListOnStrip__messcont .ws-browser-row-options-block .ws-browser-row-options-container {
      background-color: #ededed !important;
      box-shadow: 0 0 6px 7px #ededed !important;
   }
   .contacts-MessageListOnStrip__messcont .messageText:before {
      border-top-color: #ededed !important;
   }
   #accordionBg,
   .sideBarBackground:before,
   #sideLeft,
   #sideRight,
   #logoBlock,
   .sideBarBackground,
   #howEasy {
      background: #47484b;
   }
   div[data-component="SBIS3.Navigation.LeftNavigation"],
   .navigation-LeftNavigation__block,
   .newsBanners .ad-news-bg {
      background: #3c3f41 !important;
   }
   #logoBlock,
   .navigation-LeftNavigation__level-0,
   .navigation-LeftNavigation__level-0>.navigation-LeftNavigation__block>.navigation-LeftNavigation__item,
   .accordeonMenu .header,
   .accordeonMenu .container,
   .accordeonMenu>.navigation-LeftNavigation__eventHandler>.navigation-LeftNavigation__event {
      border-color: #47484b !important;
   }
   .navigation-LeftNavigation__level-0>.navigation-LeftNavigation__block>.navigation-LeftNavigation__item {
      border-top-color: #3c3f41 !important;
      background: #3c3f41 !important;
   }
   .navigation-LeftNavigation__level-0>.navigation-LeftNavigation__block>.navigation-LeftNavigation__item:hover {
      border-top-color: #515255 !important;
   }
   .navigation-LeftNavigation__item {
      color: #f0f0f0 !important;
   }
   .navigation-LeftNavigation__level-1 .navigation-LeftNavigation__counter[is-set]:before {
      background: linear-gradient(to right, rgba(45, 46, 48, 0) 0, rgba(45, 46, 48, 1) 100%);
   }
   .navigation-LeftNavigation__level-1 .navigation-LeftNavigation__counter[is-set],
   .navigation-LeftNavigation__block__activeHolder,
   .navigation-LeftNavigation__block__activeHolder .navigation-LeftNavigation__block,
   .navigation-LeftNavigation__level-0>.navigation-LeftNavigation__block:before,
   div.calendarInAccordion,
   div.calendarInAccordion div[name="КалендарьВыборМесяца"] a.ui-state-default,
   div.calendarInAccordion div[name="КалендарьВыборМесяца"] div.ui-datepicker-inline,
   div.calendarInAccordion div[name="КалендарьВыборМесяца"] th span,
   div.calendarInAccordion div[name="РеестрМоиКалендари"] .ws-browser.ws-browser-hasZebra tbody tr.ws-browser-table-row,
   .WTM-AccordionCalendar#popup-calendar {
      background: #2d2e30 !important;
   }
   .navigation-LeftNavigation__level-1 .navigation-LeftNavigation__block>.navigation-LeftNavigation__item:hover .navigation-LeftNavigation__counter[is-set] {
      background: #515255 !important;
   }
   .navigation-LeftNavigation__level-1 .navigation-LeftNavigation__block>.navigation-LeftNavigation__item:hover .navigation-LeftNavigation__counter[is-set]:before {
      background: linear-gradient(to right, rgba(81, 82, 85, 0) 0, rgba(81, 82, 85, 1) 100%) !important;
   }
   .accordeonMenu .header,
   .accordeonMenu .container {
      background: #343638 !important;
   }
   .navigation-LeftNavigation__level-0>.navigation-LeftNavigation__block>.navigation-LeftNavigation__item:hover,
   .navigation-LeftNavigation__block>.navigation-LeftNavigation__item:hover,
   .accordeonMenu .header:hover,
   .accordeonMenu .navigation-LeftNavigation__subMenuOverLink>a:hover,
   div.calendarInAccordion div[name="РеестрМоиКалендари"] .ws-browser-table-row:hover,
   div.calendarInAccordion div[name="РеестрМоиКалендари"] .ws-browser-table-row:hover>td,
   div.calendarInAccordion div[name="РеестрМоиКалендари"] .ws-browser.ws-browser-hasZebra tbody .ws-browser-table-row>.ws-browser-cell:hover,
   .accordeonMenu>.navigation-LeftNavigation__eventHandler>.navigation-LeftNavigation__event:hover {
      background: #515255 !important;
   }
   .navigation-LeftNavigation__expand,
   .navigation-LeftNavigation__event,
   .navigation-LeftNavigation__counter span,
   .navigation-LeftNavigation__expandActive,
   .navigation-LeftNavigation__event .icon-View,
   .ws-accordeon-close {
      color: #cecece !important;
   }
   .navigation-LeftNavigation__counter {
      color: #cc6022 !important;
   }
   .accordeonMenu>.navigation-LeftNavigation__counterHandler>.navigation-LeftNavigation__counter:hover,
   .navigation-LeftNavigation__counter:hover,
   .navigation-LeftNavigation__counter:hover span,
   .navigation-LeftNavigation__event:hover,
   .navigation-LeftNavigation__event:hover .icon-View,
   .ws-accordeon-close:hover,
   .navigation-LeftNavigation__expand:hover,
   .navigation-LeftNavigation__expandActive:hover {
      color: #ee7033 !important;
   }
   .navigation-LeftNavigation__level-0>.navigation-LeftNavigation__block__active>a:before,
   .navigation-LeftNavigation__level-1 .navigation-LeftNavigation__block__active>a:before,
   .navigation-LeftNavigation__level-1 .navigation-LeftNavigation__block__activeVisual>a:before {
      background-color: #884000 !important;
   }
   div.calendarInAccordion div[name="КалендарьВыборМесяца"] div.ui-datepicker-header,
   div[sbisname="РеестрМоиКалендари"] .ws-browser-row-selected>.ws-browser-cell:first-child {
      background: none;
   }
   .accordeonMenu .header[data-url][data-action="1"]:hover:before,
   .accordeonMenu .header[data-url][data-action="1"]:hover+.navigation-LeftNavigation__counterHandler>.navigation-LeftNavigation__counter:before,
   .navigation-LeftNavigation__level-0>.navigation-LeftNavigation__block__activeHolder .navigation-LeftNavigation__counter[is-set]:before,
   .navigation-LeftNavigation__level-1 .navigation-LeftNavigation__counter[is-set]:before,
   .navigation-LeftNavigation__block__activeHolder .navigation-LeftNavigation__item .navigation-LeftNavigation__counter,
   .accordeonMenu .header[data-url][data-action="1"] .navigation-LeftNavigation__HeadTitle:hover:before,
   .accordeonMenu .header[data-url][data-action="1"] .navigation-LeftNavigation__HeadTitle:hover,
   .accordeonMenu .navigation-LeftNavigation__counterHandler>.navigation-LeftNavigation__counter:before,
   .accordeonMenu .header:before,
   .navigation-LeftNavigation__level-0>.navigation-LeftNavigation__block>.navigation-LeftNavigation__item:hover:after,
   .navigation-LeftNavigation__level-0 .navigation-LeftNavigation__item:hover>.navigation-LeftNavigation__counter,
   .navigation-LeftNavigation__level-0 .navigation-LeftNavigation__counter[is-set]:before,
   .navigation-LeftNavigation__item .navigation-LeftNavigation__counter:hover,
   .navigation-LeftNavigation__item .navigation-LeftNavigation__counter,
   .navigation-LeftNavigation__level-0 .navigation-LeftNavigation__item:hover>.navigation-LeftNavigation__counter[is-set]:before,
   .navigation-LeftNavigation__level-0>.navigation-LeftNavigation__block>.navigation-LeftNavigation__item .navigation-LeftNavigation__title>span:hover:before,
   .navigation-LeftNavigation__level-0>.navigation-LeftNavigation__block>.navigation-LeftNavigation__item .navigation-LeftNavigation__title>span:hover {
      background: none;
   }
   .accordeonMenu.container {
      background-color: #47484b !important;
   }
   .accordeonMenu .header[data-url][data-action="1"]:hover+.navigation-LeftNavigation__counterHandler>.navigation-LeftNavigation__counter {
      background-color: #515255 !important;
   }
   .accordeonMenu>.navigation-LeftNavigation__counterHandler>.navigation-LeftNavigation__counter,
   .accordeonMenu>.navigation-LeftNavigation__counterHandler>.navigation-LeftNavigation__counter.ws-disabled:hover {
      background-color: #343638 !important;
   }
   .navigation-LeftNavigation__allCounter-separator:before {
      background-color: #555555 !important;
   }
   .navigation-LeftNavigation__block__activeHolder>.navigation-LeftNavigation__level-1 {
      border-bottom: 1px solid #515255;
   }
}
