css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("de.nachrichten.yahoo.com") {
    
    /**************************************
    /*                                    *
    /*  TIPP: Verstecke Nachrichten mit   *
    /*  "Yahoo Nachrichten Cleanup":      *
    /*  https://goo.gl/3YMhBk             *
    /*                                    *
    /**************************************
    
    /* Header scrollt nicht mit */
    #atomic .Pos\(f\) {
        position: absolute;
    }
    
    /* Ganze Sidebar ausblenden */
    #atomic .W\(1\/3\) {
        display: none;
    }
    
    /* Entferne die Kommentare vor den folgenden zwei,
       um das Wetter in der Sidebar anzuzeigen /*
    
    /* "Lesen Sie auch" */
    /*#mrt-node-tgtm-Col2-4-Stream {
        display: none;
    }*/
    
    /* Verstecke Channels-Ad */
    /*#tgtm-Col2-2-Channels {
        display: none;
    }*/

    /* Größeren Platz benutzen */
    #atomic .Maw\(600px\) {
        min-width: 670px;
    }
    
    #atomic .P\(20px\) {
        padding: 0;
    }
    
    /* "Sehen sie auch" ausblenden */
    p.canvas-text:nth-child(10), .canvas-yahoovideo {
        display: none;
    }
    
    #tgt1-Col2-1-HeightContainer-Proxy {
        display: none;
    }
    
    #tgt1-SIDE0-0-HeightContainer-Proxy {
        display: none;
    }
    
    #tgt1-SIDE1-0-HeightContainer-Proxy {
        display: none;
    }
    
    #tgt1-SIDE2-0-HeightContainer-Proxy {
        display: none;
    }
    
    
    #YDC-Col2 {
        display: none;
    }
    
    /* Blendet Kommentare aus */
    /*#Col1-2-CanvassComments-Proxy {
        display:none;
    }*/
    
    /* "Folgen Sie uns" ausblenden */
    #atomic .Px\(navPaddings\) {
        display: none;
    }
    
    /* "Mehr zu" ausblenden */
    #tgt1-BottomExt-0-Stream-Proxy {
        display: none;
    }
    
    /* "Über-Navbar" ausblenden */
    #masterNav {
        display: none;
    }

}
