css:
@-moz-document domain("winehq.org") {
body {
    background-image: none;
}
div#pagewell,
#main_content {
	padding: 10px 10px 10px 170px !important;
}
#winehq_body {
    margin: 10px 10px 10px 170px !important;
	padding: 10px 10px 10px 10px !important;
}
div.content {
	padding: 10px 10px 10px 10px !important;
}
#pagecontent,
#winehq_body,
.cornerround {
    -moz-border-radius: 0px !important;
    -webkit-border-radius: 0px !important;
    border-radius: 0px !important;
}
div.rbox b.rtop, b.rbottom {
    display: none !important;
}
#tabs LI ,
#winehq_tabs LI {
    background-image: none !important;
}
#tabs LI:hover,
#winehq_tabs LI:hover {
    background-image: none !important;
}
#tabs LI.s,
#winehq_tabs LI.s{
    background-image: none !important;
}
#logo_glass img,
#logo_glass_big img,
#logo_text img,
#winehq_logo img,
#winehq_title img{
	display: none !important;;
}
.zoom_overlay {
	display: none !important;;
}
.winehq_menu>div>.icon {
	background-image: url("http://i.imgur.com/bigQI5q.png") !important;
}
#footer,
#logo_blurb, 
#winehq_blurb {
    display: none !important;
}
#sidebar,
#sidewell,
#winehq_sb {
    top: 26px !important;
}
#sidebar LI ,
#winehq_sb LI{
	background-color: #C0392B !important;
    background-image: none !important;
}
#sidebar LI:hover,
#winehq_sb LI:hover {
	background-color: #D64541 !important;
    background-image: none !important;
}
#sidebar UL,
#winehq_sb UL {
    margin: 0 0 0 0;
}
#sidebar A,
#winehq_sb A {
    text-decoration: none !important;
}
div.sidetitle,
#sidebar LI.top ,
#winehq_sb LI.top {
	background-color: #96281B !important;
    background-image: none !important;
}
div.sidebottom,
#sidebottom,
#sidebar LI.bot ,
#winehq_sb LI.bot {
	display: none !important;
}
div.sidepanel {
    margin: 0px;
}
select,
INPUT,
input,
input.button,
.button,
.btn,
td>form>input,
td>input {
    -moz-box-shadow: none !important;
    -webkit-box-shadow: none !important;
    box-shadow: none !important;
    box-sizing: border-box !important;
	color: #96281B !important;
    background-color: #D2D7D3 !important;
	text-decoration: none !important;
    border: none;
    border-radius: 3px;
	margin: 3px 3px 2px 3px;
    padding: 3px 3px 2px 3px;
}
select:hover,
INPUT:hover,
input.button:hover {
    background-color: #ABB7B7 !important;
}
#tabs,
#tabs ul,
#tabs ul li,
#tabs ul li a,
#winehq_tabs,
#winehq_tabs ul,
#winehq_tabs ul li,
#winehq_tabs ul li a {
	position: relative;
	box-sizing: border-box;
	height: 23px;
}
#tabs:after,
#tabs > ul:after,
#winehq_tabs:after,
#winehq_tabs > ul:after{
	content: ".";
	display: block;
	clear: both;
	visibility: hidden;
	height: 0;
}
#tabs,
#winehq_tabs {
	width: auto;
	border-bottom: 26px solid #C0392B;
}
#tabs ul,
#winehq_tabs ul{
	background: #ffffff;
	height: 23;
}
#tabs > ul > li:active,
#winehq_tabs > ul > li:active{
	color: #2D2D2D;
}
#tabs > ul > li > a,
#winehq_tabs > ul > li > a {
	z-index: 2;
	color: #2D2D2D;
}
#tabs > ul > li.active > a,
#tabs > ul > li.current > a,
#tabs > ul > li.s > a,
#tabs > ul > li:hover > a,
#tabs > ul > li > a:hover,
#winehq_tabs > ul > li.active > a,
#winehq_tabs > ul > li.current > a,
#winehq_tabs > ul > li.s > a,
#winehq_tabs > ul > li:hover > a,
#winehq_tabs > ul > li > a:hover{
	color: #ffffff;
}
#tabs > ul > li > a:after,
#winehq_tabs > ul > li > a:after{
	position: absolute;
	left: 0;
	bottom: 0;
	right: 0;
	z-index: -1;
	width: 100%;
	height: 100%;
	content: "";
}
#tabs > ul > li.active > a:after,
#tabs > ul > li.current > a:after,
#tabs > ul > li.s > a:after,
#winehq_tabs > ul > li.active > a:after,
#winehq_tabs > ul > li.current > a:after,
#winehq_tabs > ul > li.s > a:after{
	background: #C0392B;
}
#tabs > ul > li:hover > a:after,
#tabs > ul > li > a:hover:after,
#winehq_tabs > ul > li:hover > a:after,
#winehq_tabs > ul > li > a:hover:after{
	background: #96281B;
}
#search_box {
	z-index: 7;
    top: 0px;
}
#search_box>form>span {
	color: #96281B !important;
}
#search_box>form>input {
	background-color: #FFFFFF !important;
    box-shadow: none !important;
}
.platinum,
tr.platinum {
    background-color: #E5E4E2 !important;
}
.gold,
tr.gold {
    background-color: #FFD700 !important;
}
.silver,
tr.silver {
    background-color: #C0C0C0 !important;
}
.bronze,
tr.bronze {
    background-color: #CD7F32 !important;
}
.garbage,
tr.garbage {
    background-color: #AF9B60 !important;
}
td>form>input,
td>input {
	width: 100%;
}
table.historyTable {
    border: none !important;
}
table {
    border: none !important;
	border-spacing: 0px;
}
td.color0,
td.color1,
td.color3,
td.color4,
tr.color0,
tr.color1,
tr.color3,
tr.color4,
td.color4>table>tbody>tr.platinum,
td.color4>table>tbody>tr.gold,
td.color4>table>tbody>tr.silver,
td.color4>table>tbody>tr.bronze,
td.color4>table>tbody>tr.garbage {
    background-color: #FFF !important;
}
table.htmlarea,
html {
    background-color: #FFF !important;
    color: #000 !important;
}
tr.color4 {
	font-weight: bold;
}
div.imgarea>a {
    visibility: collapse;
}
div.imgarea>a>img {
	visibility: visible;
}
div.imgarea>br {
    display: none;
}
div.info_container,
div.info_container>div.title_class, 
div.info_contents {
    border: none !important;
    background-color: #FFF;
}
div.note_container,
div.note_title,
div.note_body {
    border: none !important;
    color: #D91E18;
    background-color: #FFF;
}
.tbl-h-l,
.tbl-h-c,
.tbl-h-r {
    background-image: none !important;
    background-color: #96281B;
}
.tablebg th {
	background-image: none !important;
}
.tbl-f-c,
.cat-bottom {
    display: none !important;
}
#titles,
#header .links,
.cornerround {
    -moz-border-radius: 0px !important;
    -webkit-border-radius: 0px !important;
    border-radius: 0px !important;
}
}
