css:
@-moz-document domain("winclub.pl") {
.maintitle {
    background-image: url(http://data.whicdn.com/images/30687413/original.jpg);
    -webkit-border-top-left-radius: 10px;
    -webkit-border-top-right-radius: 10px;
    -moz-border-radius-topleft: 10px;
    -moz-border-radius-topright: 10px;
    border-top-left-radius: 10px;
    border-top-right-radius: 10px;
    text-align: center;
    opacity: 0.7;
    border: none
}
.main_width {
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    border-radius: 5px;
    opacity: 0.9;
    border: none
}
body {
    background-image: url(http://wallpapers.wallhaven.cc/wallpapers/full/wallhaven-120518.jpg);
    background-color: black;
    /* efekt paralax: */
    
    min-height: 100%;
    background-size: cover;
    background-attachment: fixed;
    background-repeat: no-repeat;
    background-position: center center;
    border: none
}
#content {
    background: none;
    margin-top: 0px;
    border: none
}
ipsBox_container {
    background: none;
    border: none
}
div {
    border: none
}
td {
    -moz-border-radius: 10px;
    -webkit-border-radius: 10px;
    border-radius: 10px;
    opacity: 1;
    border: none
}
.popupWrapper {
    border: none;
    background: transparent;
    padding: 0px
}
#board_statistics {
    display: none;
}
.statistics {
    display: none;
}
#primary_nav {
    background: none;
    border: none
}
#index_stats {
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    border-radius: 5px;
    text-align: center;
    opacity: 1;
    border: none
}
p {
    display: block
}
#header_bar {
    background: none;
    border: none;
    padding: inherit;
}
#user_navigation.not_logged_in {
    padding: inherit
}
#branding {
    background: none;
    border: none
}
.ipsItemStatus.ipsItemStatus_large {
    background: #3498db;
    color: #ecf0f1;
    border-radius: 180px
}
#user_navigation #register_link {
    background: none;
    border: none;
    color: #fff
}
.table.ipb_table {
    border: none;
    visibility: hidden
}
.ipsBox_container {
    background: #ecf0f1
}
.ipsItemStatus.ipsItemStatus_large.ipsItemStatus_read {
    background: #3498db;
    border-radius: 30px
}
#announcements td {
    border: none
}
.ipsBox {
    background: #fff;
    padding: inherit
}
.ipsSideBlock h3 {
    background-image: url(http://data.whicdn.com/images/30687413/original.jpg);
    -webkit-border-top-left-radius: 10px;
    -webkit-border-top-right-radius: 10px;
    -moz-border-radius-topleft: 10px;
    -moz-border-radius-topright: 10px;
    border-top-left-radius: 10px;
    border-top-right-radius: 10px;
    opacity: 0.7;
    text-transform: uppercase;
}
.ipsSideBlock {
    border-radius: 10px
}
}
