css:
@-moz-document url-prefix("https://discordapp.com/channels/") {
body{
	background: url("https://i.imgur.com/T2xHZtt.jpg");
}

.guilds-wrapper{
	background:transparent;
	width:72px;
	
}
.guilds-separator{
	margin: 10px 0;
	height: 0px !important;
	left:0;
	border-radius: 5px;
	border-style: solid;
	border-width: 2px;
	border-color: rgba(30, 30, 30, 1);
}
.guilds-separator:after{
	display:none;

}
.guilds{
	padding: 18px 8px !important;
	overflow-x:hidden;
}
.help-container{
	display: none;
}
.guilds li{
	transition:left 0.15s, opacity 0.15s;
	opacity:0.5;
	width:auto;
	left:11px;
}
.guilds li:first-of-type,.guilds li#bd-pub-li{
	width:60px;
	left:0;
}
.guilds li.active{
	left:0;
	width:60px;
}
.guilds li.active .guild-inner{
	width:50px;
}
.guilds li .guild-inner {
    border-radius: 5px;
	border-style: solid;
	border-width: 4px;
	border-color: rgba(30, 30, 30, 1);
}
.guilds li .guild-inner:before{
	display:none;
	left:0;
	background: #F90;
	width: 4px;
	margin-top: -3px;
}
.guilds li.active,.guilds li:hover{
	opacity:1;
}
.guilds li.active .guild-inner,.guilds li:hover .guild-inner {
    border-color: #738bd7;
	border-radius: 5px;
	left:0;
}
.guilds li .guild-inner a{
	
	left:0;

}
.guilds li.unread .guild-inner:before{
	display:initial;
	border-radius:initial;
	background: #F90;
	margin-top: -3px;
}
.guilds li.unread.active .guild-inner:before{
	margin-top: -16px;
}
.guilds-add{
	border-radius: 5px;
	width: 50px;
	border-width: 4px;
	border-color: rgba(100, 100, 100, 1) !important;
}
/*channels*/

.guild-header header{
    background-color:rgba(30, 30, 30, 0.7);
	padding: 8px 24px;
	margin: 8px 0px;
	border-style: solid;
	border-width: 5px;
	border-color: rgba(30, 30, 30, 1);
	border-radius: 5px;
}
.guild-header ul{
	background-color: transparent;
}
.guild-header ul a{
	visibility:hidden;
	background-color:rgba(30, 30, 30, 1);
	border-style: solid;
	border-width: 4px;
	border-color: rgba(200, 30, 30, 1);
	border-radius: 5px;
	padding:2px;
	margin: 14px 22px;
}
.guild-header-open ul a{
	visibility:visible;
}
.account{
	height:50px;
	margin-top:5px;
	background-color:rgba(30, 30, 30, 0.7);
	border-style: solid;
	border-width: 5px;
	border-bottom-width:0;
	border-color: rgba(30, 30, 30, 1);
	border-top-left-radius: 5px;
	border-top-right-radius: 5px;
}
.links{
	background-color:rgba(30, 30, 30, 0.7);
	border-style: solid;
	border-width: 5px;
	border-top-width:0;
	border-color: rgba(30, 30, 30, 1);
	border-bottom-right-radius: 5px;
	border-bottom-left-radius: 5px;
	margin-bottom:5px;
}
.btn-group .btn{
	background: transparent;
}
.guild-channels{
	background-color:rgba(30, 30, 30, 0.7);
	border-style: solid;
	border-width: 5px;
	border-color: rgba(30, 30, 30, 1);
	border-radius: 5px;
}
.guild-channels ul .channel-text{
	transition: opacity .15s ease;
	opacity: 0.3;
	background-color:rgba(30, 30, 30, 0.8);
	border-style: solid;
	border-width: 2px;
	border-color: rgba(200, 200, 200, 1);
	border-radius: 5px;
}
.guild-channels ul .channel-text.unread:not(.selected):not(.channel-muted):before{
	display:initial;
	width: 4px;
	border-radius:initial;
	left:-2px;
	background: #F90;
	margin-top: -3px;
}
.win-buttons{
	background-color:rgba(30, 30, 30, 0.7);
	border-style: solid;
	border-width: 5px;
	border-color: rgba(30, 30, 30, 1);
	border-radius: 5px;
}
.guild-channels ul .channel-text:hover{
	opacity:1;
	background-color:rgba(30, 30, 30, 1);
	border-style: solid;
	border-width: 2px;
	border-color: rgba(30, 30, 30, 1);
	border-radius: 5px;
}
.guild-channels ul .channel-text.selected{
	opacity:1;
	background-color:rgba(30, 30, 30, 0.3);
	border-style: solid;
	border-width: 2px;
	border-color: rgba(30, 30, 30, 0.7);
	border-radius: 5px;
}
.guild-channels ul .channel-text a{
	padding: 4px 3px 5px 10px;
}
.guild-channels ul .channel-text,.guild-channels ul .channel-text-selected{
	margin: 4px 3px 5px 10px;
}

/*Chat*/
.theme-dark .chat .title-wrap, .theme-light .chat .title-wrap{
	margin: 6px 20px;
	margin-right: 110px;
	width:auto;
	padding:0;
	border-bottom:2px;
	background-color:rgba(30, 30, 30, 0.7);
	border-style: solid;
	border-width: 5px;
	border-color: rgba(30, 30, 30, 1);
	border-radius: 5px;
}
.chat .new-messages-bar{
	background-color:rgba(30, 30, 30, 0.7);
	border-style: solid;
	border-width: 5px;
	border-radius: 5px;
	border-color:rgba(115,139,215,.9)
}
.chat>.content{
	background-color:rgba(30, 33, 36, 0.2) !important;
}

.chat .message-group .comment{
	background-color:rgba(30, 30, 30, 0.7);
	border-style: solid;
	border-width: 5px;
	border-color: rgba(30, 30, 30, 1);
	border-radius: 5px;
}
.channel-textarea{
	opacity: .9;
	}
.channel-textarea-inner{
	background-color:rgba(70, 70, 70, 0.7) !important;
	border-style: solid;
	border-width: 3px;
	/*border-color: rgba(150, 150, 150, 1);*/
	/*border-radius: 5px;*/
}
#twitchcord-button{
	background-color:rgba(30, 30, 30, 0.7);
}
/*Members*/
.channel-members{
	background-color:transparent !important;
}
.theme-dark .channel-members .member{
	margin: 0 5px;
	padding: 5px 15px;
	transition: opacity .15s ease;
	background-color:rgba(30, 30, 30, 0.3);
	border-style: solid;
	border-width: 0;
	border-color: rgba(30, 30, 30, 1);
	border-radius: 0;
	border-left-width: 5px;
	border-right-width: 5px;
}

.theme-dark .channel-members h2{
	margin: 5px;
	margin-bottom: 0px;
	padding: 0 15px;
	transition: opacity .15s ease;
	background-color:rgba(30, 30, 30, 0.8);
	border-style: solid;
	border-width: 5px;
	border-color: rgba(30, 30, 30, 1);
	border-bottom-width:0;
	border-top-left-radius: 5px;
	border-top-right-radius: 5px;
}
.theme-dark .channel-members .member-status-idle>*{
	opacity:0.3;
}
.theme-dark .channel-members .member-status-online{
	opacity:1;
	background-color:rgba(30, 30, 30, 0.7);
	border-style: solid;
	border-color: rgba(30, 30, 30, 1);
}
.theme-dark .channel-members .member:hover{
	background-color:rgba(30, 30, 30, 1);
	border-style: solid;
	border-width: 2px;
	border-left-width: 5px;
	border-right-width: 5px;
	padding: 3px 15px;

}
.tex .hljs-formula {
    background-color:rgba(7, 54, 66, 0);
}
}
