css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.sherdog.com")
{
/* ===== GLOBAL EDITS ======= */

/* forum width and hide sidebar */
div.sidebar {
    display: none !important;
}
div.mainContent {
    margin-left: -10px !important;
    margin-right: -10px !important;
}

/* bigger alerts icon */
i.fa.fa-map-marker {
    font-size: 24px !important;
}

/* text size on alerts and alerts page */
div.listItemText h3 {
    font-size: 14px !important;
}
div.alertText h3 {
    font-size: 14px !important;
}

/* text colours */
h3.title {
    color: #f2f2f2 !important;
}
a.username {
    color: #ffffff !important;
}
a.PreviewTooltip {
    font-weight: normal !important;
    color: #d6d6d6!important;
}
h3.nodeTitle a {
    color: #e8e8e8 !important;
}
a.menuRow {
    color: #e0e0e0 !important;
}
a.externalLink {
    border-color: #ffffff !important;
    color: #db8709 !important;
}

/* ============ BOARD INDEX ============== */

/* recolour sticky and locked icons */
i.fa.fa-lock {
    color: #cd300c!important;
    font-size: 12px!important;
}
i.fa.fa-thumb-tack {
    color: #cd300c!important;
    font-size: 12px!important;
}

/* hide sticky/normal headers*/
li.sectionHeaders {
    display: none !important;
}

/* hide weird circle*/
i.fa.fa-circle {
    display: none !important;
}
li.discussionListItem.visible.locked.sticky {
    background-color: #0f0f0f !important;
}
li.discussionListItem.visible.sticky {
    background-color: #0f0f0f !important;
}
i.fa.fa-comments {
    color: #cd300c!important;
}

/* fixed collapsed forum sections */
div.nodeInfo.categoryNodeInfo.hasNew.toggleMEtrigger.CategoryStripCollapsed {
    background: #404040 !important;
    border: none !important;
    border-radius: 0px !important;
}
.nodeList .CategoryStripCollapsed .nodeTitle a {
    font-weight: bold !important;
    text-transform: uppercase !important;
    font-size: 14px !important;
}

/* ================= MESSAGE PAGES =================== */

/* ==username/avatar mods== */
a.avatar img {
    border-style: none !important;
    border-radius: 10px !important;
}
div.messageUserBlock {
    height: 275px !important;
    border-style: dashed!important;
    border-radius: 5px !important;
    min-height: 320px !important;
    border-color: #7d5e00!important;
}
div.messageUserBlock.online {
    min-height: 320px !important;
}

/* swap username and avatar */
div.avatarHolder {
    margin-top: 50px!important;
}
h3.userText {
    margin-bottom: 130px!important;
    margin-top: -185px!important;
    border-style: none!important;
}

/* lline up online marker */
span.Tooltip.onlineMarker {
    margin-left: -5px!important;
    margin-top: 3px!important;
}

/* ==message body mods== */

/* quotes */
div.bbCodeBlock.bbCodeQuote {
    margin-right: 50px!important;
    margin-left: 50px!important;
    margin-top: 20px!important;
    border-radius: 5px !important;
    margin-bottom: 20px!important;
}
div.quote {
    font-size: 13px!important;
    font-style: italic!important;
}
blockquote.quoteContainer {
    background-color: #262626!important;
}

/* message body */
li.message {
    background-color: #1a1a1a !important;
    border-radius: 5px !important;
    border-style: dashed!important;
    border-color: #3b3b3b!important;
}

/* likes box */
div.likesSummary.secondaryContent {
    border-style: dashed !important;
    border-color: #383838 !important;
    background-color: #1f1f1f !important;
    border-radius: 5px !important;
}

/* new post indicator */
strong.newIndicator {
    color: #ffffff !important;
    background-color: #3b3b3b !important;
}

/* spoilers */
div.SpoilerTarget.bbCodeSpoilerText {
    background-color: #1a1a1a!important;
    border-color: #ff7800!important;
    border-style: dashed!important;
    border-width: 1px!important;
}

/* remove capitals from thread title */
div.titleBar h1 {
    text-transform: capitalize!important;
}

/* == Polls == */

/* change poll bar colour */
span.bar {
    background-color: #ff7800!important;
}

/* like, quote, report etc */

/* like button */
span.LikeLabel {
    color: #919191 !important;
}
span.LikeLabel:hover {
    color: #ff9008 !important;
    text-decoration: none !important;
}

/* report button */
a.OverlayTrigger.item.control.report {
    color: #919191 !important;
}
a.OverlayTrigger.item.control.report:hover {
    color: #ff9008 !important;
    text-decoration: none !important;
}

/* quote button */
span.symbol {
    color: #919191 !important;
}
span.symbol:hover {
    color: #ff9008 !important;
    text-decoration: none !important;
}

/* reply button */
a.ReplyQuote.item.control.reply {
    color: #919191 !important;
}
a.ReplyQuote.item.control.reply:hover {
    color: #ff9008 !important;
    text-decoration: none !important;
}

/* =============== BUTTONS ================ */

/* new thread button */
a.callToAction span {
    border-radius: 5px !important;
}
a.callToAction span:hover {
    color: #141414!important;
}

/* reply button */
input.button.primary {
    border-radius: 5px !important;
}
input.button.primary:hover {
    color: #141414!important;
}

/* attachments button */
input.button.OverlayTrigger.DisableOnSubmit {
    border-radius: 5px !important;
}
input.button.OverlayTrigger.DisableOnSubmit:hover {
    color: #141414!important;
}

/* more options button */
input.button.DisableOnSubmit {
    border-radius: 5px !important;
}
input.button.DisableOnSubmit:hover {
    color: #141414!important;
}

/* change vote button */
a.button.PollChangeVote.nonOverlayOnly {
    border-radius: 5px!important;
}
a.button.PollChangeVote.nonOverlayOnly:hover {
    color: #141414!important;
}

/* poll results button */
input.button.OverlayCloser {
    border-radius: 5px!important;
}
input.button.OverlayCloser:hover {
    color: #141414!important;
}

/* cancel button */
input.button.OverlayTrigger.JsOnly {
    border-radius: 5px!important;
}
input.button.OverlayTrigger.JsOnly:hover {
    color: #141414!important;
}

/* preview button */
input.button.PreviewButton.JsOnly {
    border-radius: 5px !important;
}

/* preview button */
input.button.PreviewButton.JsOnly:hover {
    color: #141414!important;
}

/* ============ NAVIGATION ITEMS ========= */
.itemPageNav {
    visibility: visible !important;
}
span.itemPageNav a {
    border-style: dotted !important;
    border-radius: 5px !important;
    visibility: show !important;
}
a.PageNavNext {
    width: 19px !important;
    background-color: #383838 !important;
    border-style: none !important;
}
div.PageNav nav a {
    border-style: none !important;
    border-radius: 5px !important;
}

/* highlight navigation orange on hover */
a.crumb span:hover {
    color: #ff7800!important;
}

/* match submenu hover to rest of site */
ul.secondaryContent.blockLinksList li a:hover {
    color: #ff7800!important;
}
a.currentPage {
    border-style: dotted !important;
    border-radius: 5px !important;
}

/* ========= MISC STUFF ======= */

/* misc fixes */
a.text.distinct.unreadLink {
    border-radius: 5px !important;
    height: 17px !important;
}
a.text.unreadLink {
    border-radius: 5px !important;
}
a.text {
    border-style: none !important;
}
a.item.control.delete.OverlayTrigger {
    color: #919191 !important;
}
a.item.control.edit.OverlayTrigger {
    color: #919191 !important;
}
div.sectionFooter.InlineMod.SelectionCountContainer {
    background-color: #141414 !important;
}
div.pageNavLinkGroup {
    margin-right: 10px !important;
    margin-left: 10px !important;
}
div.attribution.type {
    background-color: #1a1a1a!important;
}
div.messageMeta.ToggleTriggerAnchor {
    margin-top: 150px !important;
}
span.items a {
    border-style: none !important;
    border-radius: 5px !important;
}
li.discussionListItem.visible.unread {
    background-color: #141414 !important;
}
div.nodeInfo.forumNodeInfo.primaryContent.unread {
    background-color: #171717 !important;
}

/* LATEST EDITS */

/* permalink button */
a.item.muted.postNumber.hashPermalink.OverlayTrigger {
    color: #707070 !important;
}

/* post date and time button */
abbr.DateTime {
    color: #a3a3a3 !important;
}

/* Remove weird fading on quotes */
.bbCodeQuote .quoteContainer .quoteExpand.quoteCut {
    background: none !important;
}

/* fix user role banner position */
div.extraUserInfo {
    margin-top: -5px !important;
    padding-top: 15px !important;
}
em.userBanner {
    margin-top: 120px !important;
    margin-bottom: -120px !important;
    border: none !important;
}

/* make orange banner match serhdog orange */
em.userBanner.bannerOrange.wrapped {
    background-color: #ff7800;
}
.userBanner.bannerOrange.wrapped span {
    background-color: #ff7800 !important;
}

/* remove superflous user roles */
em.userBanner.bannerHidden.wrapped {
    display: none !important;
}
em.userBanner.bannerStaff.wrapped {
    display: none !important;
}

/* make user banner ends match banner */
.userBanner.bannerRed.wrapped span {
    background-color: red !important;
}
a.button.moreOptions.Tooltip {
    border-radius: 5px;
}

/* thread moved icon */
i.fa.fa-eye {
    color: #cd300c;
}
i.fa.fa-share {
    color: #cd300c;
    font-size: 12px;
}
input.button.JsOnly {
    border-radius: 5px;
}

/* hide new ugly prefixes */
span.prefix {
    display: none;
}
ul.UserRankImages {
    display: none;
}

a.button.moreOptions.Tooltip {
    border-radius: 5px;
}


i.fa.fa-eye {
    color: #cd300c;
}

i.fa.fa-share {
    color: #cd300c;
    font-size: 12px;
}

input.button.JsOnly {
    border-radius: 5px;
}

span.prefix {
    background-color: #4c4c4c;
    border-style: none;
    color: #141414;
    height: 13px;
    line-height: 13px;
    padding-top: 3px;
}

ul.UserRankImages {
    display: none;
}

/* hide anti adblock */

div._3LlSyW5J-1- {
    display: none;
}

div._5FKnYytR-1-.cNwKe6lW-1- {
    display: none;
}

div.f-NJD1R8-1-._2nq-YCkI-1- {
    display: none;
}

div.Popup {
    margin-top: -10px;
}
}
