css:
@-moz-document domain("twitch.tv") {
.card__img:hover .card__meta,
.card__img:hover .card__boxpin,
.tw-aspect--overflow:hover .live-channel-card__boxart,
.tw-aspect--overflow:hover .video-preview-card__meta {
    visibility: hidden!important;
    opacity: 0!important;
    -webkit-transition: visibility 0s linear 200ms, opacity 200ms!important;
    -moz-transition: visibility 0s linear 200ms, opacity 200ms!important;
    -ms-transition: visibility 0s linear 200ms, opacity 200ms!important;
    -o-transition: visibility 0s linear 200ms, opacity 200ms!important;
    transition: visibility 0s linear 200ms, opacity 200ms!important;
}
.card__meta,
.card__boxpin,
.live-channel-card__boxart,
.video-preview-card__meta,
.card__img:hover .card__meta:hover,
.card__img:hover .card__boxpin:hover,
.tw-aspect--overflow .live-channel-card__boxart:hover,
.tw-aspect--overflow .video-preview-card__meta:hover {
    visibility: visible!important;
    opacity: 1!important;
    -webkit-transition: visibility 0s linear 0s, opacity 200ms!important;
    -moz-transition: visibility 0s linear 0s, opacity 200ms!important;
    -ms-transition: visibility 0s linear 0s, opacity 200ms!important;
    -o-transition: visibility 0s linear 0s, opacity 200ms!important;
    transition: visibility 0s linear 0s, opacity 200ms!important;
}
}
