css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("play.google.com") {
  
  /*App Bar*/
  #material-app-bar {
    background-color: /*[[accent]]*/;
    color: #FFF;
    z-index: 700;
    margin-top: -64px;
    font-size: inherit;
  }
  
  paper-toolbar.paper-toolbar-0 {
    background: /*[[accent]]*/ none repeat scroll 0% 0%;
    color: #FFF;
  }
  
  /*Side Bar Icons and Text*/
  .material .nav-item-container.selected {
    color: /*[[accent]]*/;
    background-color: #F0F0F0;
  }
  
  .nav-item-container.selected core-icon, .nav-item-container.selected iron-icon {
    color: /*[[accent]]*/;
  }

  .material-card .explicit {
    height: 16px;
    width: 16px;
    background-color: /*[[accent]]*/;
    color: #FFF;
    border-radius: 3px;
    font-size: 11px;
    line-height: 17px;
    text-align: center;
    vertical-align: top;
    margin: 0px;
    font-weight: 500;
    text-transform: uppercase;
    position: absolute;
    bottom: 1px;
    right: 0px;
    z-index: 1;
    border: medium none;
    padding: 0px;
  }
  
  /*Player*/
  
  #player.material .material-player-middle paper-icon-button[data-id="play-pause"]:not([disabled]) {
    color: /*[[accent]]*/;
  }
  
  .material-container-details paper-fab {
    right: 28px;
    top: -28px;
    position: absolute;
    background: /*[[accent]]*/ none repeat scroll 0% 0%;
  }
  
  #player.material .material-player-middle paper-icon-button.active[data-id="repeat"], #player.material .material-player-middle paper-icon-button.active[data-id="shuffle"], #player.material #material-player-right-wrapper paper-icon-button.opened[data-id="queue"] {
    color: /*[[accent]]*/;
  }
  
  .paper-progress-0 #primaryProgress.paper-progress {
    background-color: /*[[accent]]*/;
  }

}
