css:
@-moz-document url-prefix("https://vk.com/"), url-prefix("http://vk.com/") {
* {border-radius:0 !important; border-color:#BDC3C7 !important}
body {background:#2C3E50 !important; font-family:Open Sans, Segoe UI !important; font-size:13px !important; color:#ECF0F1 !important; overflow-x:hidden; min-height:100% !important}
/* Let's get this party started */
::-webkit-scrollbar {
    width: 12px;
}
 
/* Track */
::-webkit-scrollbar-track {
    background: rgba(0,0,0,.2);
    box-shadow:#000 0 0 10px;
}
 
/* Handle */
::-webkit-scrollbar-thumb {
    background: rgba(255,255,255,.2);
}
::-webkit-scrollbar-thumb:window-inactive {
	background: rgba(255,0,0,0.4); 
}
a {color:#ECF0F1 !important; font-family:Open Sans, Segoe UI !important;}
button {font-size: 13px !important; font-family:Open Sans, Segoe UI !important; line-height: 13px; color:#ECF0F1 !important; width:auto !important}
button:hover {background-color:#7F8C8D !important; color:#fff !important;}
b {font-weight:500 !important}
input, textarea {background-color:rgba(0,0,0,.0) !important; border:solid 1px rgba(0,0,0,.3) !important; color:#ECF0F1 !important}
.chat_onl_wrap, #fmenu {display:none !important}
.selector_container {background-color:rgba(0,0,0,.0) !important}
.selector_container table.selector_table {border:none !important}
.selector_container td.selector_dropdown {border:solid 1px rgba(0,0,0,.3) !important; border-left:none !important; height:21px !important; background-color:rgba(0,0,0,.0) !important}
.result_list ul {color:#ECF0F1 !important}
.result_list ul li {border:solid 1px rgba(0,0,0,.3) !important; color:#ECF0F1 !important}
