/*This is a small tweak for the currently reading update box. Change it if it conflicts with anything else, or you simply don't like it.*/
  .userStatusForm {font-size:12px}
  
/*This is the User Profile info box*/
  .userInfoBox .infoBoxRowTitle {padding-left:10px}
  .userInfoBoxContent {border:2px solid #ccc;float:left;width:90%}
  
/*This is the review page box*/
  .big450BoxContent {border: 2px #ccc solid;padding:2px;}
  
/*This is the circle avatar. Setting the border radius to 0 results in a square*/  
  .circleHeaderImage {border-radius: 0px;}
  
  

