/* i really want this to be global */
/* ---------------Hackerplace.de CSS Style von Dimaa--------------- */
/* ---------------Main--------------- */
*{vertical-align:baseline;font-family:Asenine;font-style:inherit;font-size:16pt;letter-spacing:1px;border:none;padding:0;margin:0;color:#00CC00;} /* Variante 1 */
/* *{vertical-align:baseline;font-family:Asenine;font-style:inherit;font-size:16pt;letter-spacing:1px;border:none;padding:0;margin:0;color:white;} */ /* Variante 2 */
/* *{vertical-align:baseline;font-family:Asenine;font-style:inherit;font-size:16pt;letter-spacing:1px;border:none;padding:0;margin:0;color:#00AFC2;} */ /*Variante 3 */
.bodybackground {
    background: #161616 url("https://www.trypox.net/images/hackerplace/schwarz/bg.png") top left repeat;
}
body {
    background: #161616 url("https://www.trypox.net/images/hackerplace/schwarz/bg.png") top left repeat;
}
div.main {
    background: #161616 url("https://www.trypox.net/images/hackerplace/schwarz/bg2.png") top left repeat;
}
a {
    color: #0034CC;
    text-decoration: none;
    font-weight: bold;
}
a:hover {
    text-shadow: 0em 0em 0.01em;
}
h1 {
    text-decoration: none;
    color: #FF0000;
}
h2 {
    color: #68e02e;
    text-decoration: none;
}
h3 {
    color: #68e02e;
}
textarea {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/textarea.png");
}
.textbox {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/textbox.png");
}
.button {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/button_new.png");
}
.button_back {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/button_back_new.png");
}
.button_logout {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/button_logout_new.png");
}
.button_big {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/button_big.png");
}
div#messagebeginnertutorial[style] {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/tutorial.png") right center no-repeat !important;
}
div#messageattack {
    /*display: block !important; /* //  https://www.trypox.net/images/hackerplace/schwarz/anivote.gif */
    font-family: cursive;
    font-size: smaller;
    color: #008FE5;
}
fieldset {
    border: 1px solid #68E02E;
}
.statusbarback {
    display: inline-block;
    width: 200px;
    height: 20px;
    border: 1px solid #353535;
    border-radius: 18px;
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/status_bar_back.png");
}
.statusbarfront {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/status_bar_front.png");
    border-radius: 18px;
}
#statusbarfront span[style] {
    color: white !important;
}
div.screens {
    background-color: rgba(104, 224, 46, 0.25);
    border: 1px solid #68e02e;
}
div.screens img {
    border: 1px solid rgb(104, 224, 46);
}
div.footer div {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/footer.png") repeat-x;
}
tr.background:hover {
    background-color: #0d0d0d;
}
select {
    background-color: #0f110f;
}
/* ------------------------------------------- */
/* ---------------Computerverwaltung--------------- */

.start_pc_work {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/pc_on.png");
    width: 34px;
    height: 34px;
    background-position: 0px -78px;
}
.start_pc_active {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/pc_on.png");
    width: 34px;
    height: 34px;
    background-position: 0px -78px;
}
.start_pc_inactive {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/pc_on.png");
    width: 34px;
    height: 34px;
    border: 0px;
}
.stop_pc_work {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/pc_off.png");
    width: 34px;
    height: 34px;
    background-position: 0px -78px;
}
.stop_pc_active {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/pc_off.png");
    width: 34px;
    height: 34px;
    background-position: 0px -78px;
}
.stop_pc_inactive {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/pc_off.png");
    width: 34px;
    height: 34px;
}
.turn_power_off {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/power_off.png");
}
/* ------------------------------------------- */
/* ---------------Box--------------- */

div.boxhead {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/box_ol.png") left top no-repeat;
}
div.boxhead div {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/box_or.png") right top no-repeat;
}
div.boxhead div div {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/box_o.png") repeat-x;
}
div.boxbody {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/box_l.png") left repeat-y;
}
div.boxbody2 {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/box_r.png") right repeat-y;
}
div.boxbody3 {
    background: none;
    background-color: #000;
    margin-left: 20px;
    margin-right: 20px;
}
div.boxfoot {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/box_ul.png") left bottom no-repeat;
}
div.boxfoot div {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/box_ur.png") right bottom no-repeat;
}
div.boxfoot div div {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/box_u.png") bottom repeat-x;
}
div.selecthead1 {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/select_ol.png");
}
div.selecthead2 {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/select_or.png");
}
div.selecthead3 {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/select_o.png") repeat-x;
}
div.selectbody1 {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/select_l.png") repeat-y;
}
div.selectbody2 {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/select_r.png") repeat-y;
}
div.selectbody3 {
    background-color: #0f110f;
}
div.selectfoot1 {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/select_ul.png");
}
div.selectfoot2 {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/select_ur.png");
}
div.selectfoot3 {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/select_u.png");
}
/* ------------------------------------------- */
/* ---------------Menü--------------- */

div.menu {
    height: 70px;
    font-size: 32pt;
    text-align: center;
    text-shadow: 0em 0em 0.1em #68e02e;
    background: url("https://www.trypox.net/images/hackerplace/schwarz/menu_ul.png") bottom no-repeat;
}
ul.menu li {
    display: block;
    text-align: center;
    height: 37px;
    width: 146px;
    cursor: pointer;
    padding: 13px 0 0 45px;
    margin-left: 34px;
    background-position: 0px 0px;
    background: url("https://www.trypox.net/images/hackerplace/schwarz/menue_button_new.png") top no-repeat;
}
div.startvmenue {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/v_menue_left.png");
}
div.stopvmenue {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/v_menue_right.png");
}
ul.vmenue li {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/v_menue_top.png") repeat-x;
}
div.vmenuecut {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/v_menue_cut.png") right bottom no-repeat;
    padding-right: 2px;
}
ul.vmenue li:hover div div div div {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/v_menue_left_hover.png") left bottom no-repeat;
}
ul.vmenue li:hover div div div div div {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/v_menue_right_hover.png") right bottom no-repeat;
}
ul.vmenue li:hover div div div div div div {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/v_menue_top_hover.png") repeat-x;
}
ul.vmenue li div div div div div div {
    color: #0034CC;
    font-weight: bold;
}
ul.vmenue li ul li {
    background-color: #272727;
    border-left: 2px solid #2f2f2f;
    border-right: 2px solid #2f2f2f;
    border-bottom: 2px double #2f2f2f;
}
ul.vmenue li ul li:hover div {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/v_menue_left_hover.png") left bottom no-repeat;
}
ul.vmenue li ul li:hover div div {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/v_menue_right_hover.png") right bottom no-repeat;
}
ul.vmenue li ul li:hover div div div {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/v_menue_top_hover.png") repeat-x;
}
div.footvmenue {
    background-color: #272727;
    border-left: 2px solid #2f2f2f;
    border-right: 2px solid #2f2f2f;
    border-bottom: 2px solid #2f2f2f;
}
/* ------------------------------------------- */
/* ---------------Remotedesktop--------------- */

#screen {
    background-color: #5A5A5A;
    border: 1px solid #262B26;
}
div#startmenu[style] {
    border-left-width: 1px !important;
    border-left-color: #235dfb !important;
    border-right-width: 1px !important;
    border-right-color: #235dfb !important;
    background-color: rgba(0, 0, 0, 0.8) !important;
}
#prgdl {
    background-color: #000;
}
#prgscn {
    background-color: #000;
}
#prgbnc {
    background-color: #000;
}
#prghkt {
    background-color: #000;
}
#prgdtm {
    background-color: #000;
}
#prgsmg {
    background-color: #000;
}
#prghkttodo {
    color: #000;
}
.head {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/programm_leiste_head.png");
}
.mini {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/ablegen_grau.png");
}
.mini:hover {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/ablegen_blau.png");
}
.maxi {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/maximieren_grau.png");
}
.maxi:hover {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/maximieren_blau.png");
}
.maxi2 {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/minimieren_grau.png");
}
.maxi2:hover {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/minimieren_blau.png");
}
.close {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/schliessen_grau.png");
}
.close:hover {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/schliessen_blau.png");
}
.taskbar {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/inaktiv.png");
}
.taskbar:hover {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/inaktiv_hover.png");
}
.taskbar2 {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/aktiv.png");
}
.taskbar2:hover {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/aktiv_hover.png");
}
.bar {
    position: absolute;
    top: 475px;
    height: 25px;
    width: 100%;
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/start_bar.png");
}
.startbutton {
    color: transparent;
	float: left;
    height: 22px;
    width: 0px;
    padding-top: 3px;
    padding-left: 30px;
    margin-right: 1px;
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/start.png");
}
.startbutton:hover {
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/start_hover.png");
}
.borderleft {
    width: 1px;
    background-color: #235dfb;
}
.borderright {
    width: 1px;
    background-color: #235dfb;
}
.borderbottom {
    height: 1px;
    background-color: #235dfb;
}
.borderleftbottom {
    width: 1px;
    height: 1px;
    background-color: #235dfb;
}
.borderrightbottom {
    left: 197px;
    width: 1px;
    height: 1px;
    background-color: #235dfb;
}
.contextmenu {
    background-color: #1f1f1f;
    border: 1px solid #235dfb;
}
input {
    border: 1px solid green;
    background-color: #0d0d0d;
}
.remotebutton {
    background-color: #1f1f1f;
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/nope.png"); /* Gibt es nicht */
}
#checkcommand input[style] {
	background-color: #1f1f1f !important;
}
.statusbarfront span[style] {
	color: #CA64CA !important;
}
.reiter_active {
    border-bottom: 1px solid;
}
/* ------------------------------------------- */
/* ---------------Xmas--------------- */
div.xmasheader {
    background: black;
}
div.xmasheader div {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/hackerplace_xmas.png") bottom no-repeat;
}
div.xmasheader div div {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/head_cycle.png") right bottom no-repeat;
}
div.xmasheader div div div {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/head_top.png") top repeat-x;
}
div.xmasheader div div div div {
    background: url("https://www.trypox.net/images/hackerplace/schwarz/1.png") bottom no-repeat;
    /*Gibt es nicht */
}
div.advent {
	border: 1px solid #262B26;
    background-image: url("https://www.trypox.net/images/hackerplace/schwarz/adventcalendar.png");
}
div.advent_door_open {
    border: 1px solid rgba(0,12,165,0.5);
    border-right: 4px solid rgba(0,12,165,0.5);
}
div.advent_door p {
    color: rgb(51, 61, 187);   
}
div.advent_door24 p {
    color: rgb(24, 34, 156);
    text-shadow: 0em 0em 0.1em #fff;
}
/* ------------------------------------------- */
::-webkit-scrollbar{width: 12px;height: 12px;}
::-webkit-scrollbar-track{background: rgba(202, 202, 202, 0.1);}
::-webkit-scrollbar-thumb{background: rgba(202, 202, 202, 0.5);}
::selection{background: #9C9C9C;} /* WebKit/Blink Browsers */
::-moz-selection{background: #9C9C9C;} /* Gecko Browsers */
