css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ruthbosch.com") {

 body, div#header.navbar, footer
	{
	color : #e8e8e8 !important;
	background-color: #212121 !important;

	}
	.navbar .brand, h1, h2,h3,h4,h5,h6
	{
	color : #e8e8e8 !important;
	}

	.load-more li a
	{
	background-color : #a30837 !important;
	}

	a, 
	.page-template-page_contact-php .contact-info i,
	.post-title a:hover,
	ul.bullet li:before,
	blockquote small {
		color: #a30837;
	}
	
	.section-head,
	.btn,
	.btn-group > .btn,
	.parallax .btn,
	input[type="submit"] {
		background: #a30837;
	}
	
	.section-head h1:after {
		border-top-color: #a30837;
	}
	
	input[type="submit"]{
		color: #fff;
	}
	
	.btn:hover,
	.btn:focus,
	.btn:active,
	.btn.active,
	input[type="submit"]:hover {
		background: #e00b3c;
	}
	
	.pagination ul > li > a,
	.pagination ul > li > span {
		background: #a30837;
	}
	
	.pagination ul > li > a:hover,
	.pagination ul > li > a:focus,
	.pagination ul > .active > a,
	.pagination ul > .active > span {
		background: #e00b3c			}
	#header.navbar .nav > .active > a,
	#header.navbar .nav > .active > a:hover,
	#header.navbar .nav > .active > a:focus,
	#header.navbar .nav > li > a:hover,
	#header.navbar .nav li.dropdown.open > .dropdown-toggle,
	#header.navbar .nav li.dropdown.active > .dropdown-toggle,
	#header.navbar .nav li.dropdown.open.active > .dropdown-toggle {
		color: #a30837;
	}
	
	.post .meta a:hover {
		color: #a30837			}
	.sidebox a:hover {
		color: #a30837			}
	#comments .info h2 a:hover {
		color: #a30837			}
	#comments a.reply-link:hover, #comments .comment-reply-link:hover {
		color: #a30837			}
	.shots .overlay a .more,
	.post .overlay a .more,
	.post-list .overlay a .more {
		background: #a30837;
	}
	.parallax a:hover {
		color: #a30837			}
	.filter li a.active,
	.filter li a:hover {
		color: #a30837			}
	.thumbs li a .overlay {
		background: #a30837;
	}
	.item-details span,
	.item-details a:hover,
	.pricing .plan h4 span,
	.accordion-heading .accordion-toggle:hover,
	.nav-tabs > .active > a,
	.nav.nav-tabs > li > a:hover,
	.nav.nav-tabs > li > a:focus {
		color: #a30837;
	}
	@media (min-width: 768px) and (max-width: 979px) { 
		#header .dropdown-menu li a:hover,
		#header .dropdown-menu li a.active,
		#header .dropdown-menu > li > a:hover,
		#header .dropdown-menu > li > a:focus,
		#header .dropdown-submenu:hover > a,
		#header .dropdown-submenu:focus > a,
		#header .dropdown-menu > .active > a,
		#header .dropdown-menu > .active > a:hover,
		#header .dropdown-menu > .active > a:focus,
		#header .nav-collapse .nav > li > a:hover,
		#header .nav-collapse .nav > li > a:focus,
		#header .nav-collapse .dropdown-menu a:hover,
		#header .nav-collapse .dropdown-menu a:focus {
			color: #a30837;
		}
	}
	@media (max-width: 767px) { 
		#header .dropdown-menu li a:hover,
		#header .dropdown-menu li a.active,
		#header .dropdown-menu > li > a:hover,
		#header .dropdown-menu > li > a:focus,
		#header .dropdown-submenu:hover > a,
		#header .dropdown-submenu:focus > a,
		#header .dropdown-menu > .active > a,
		#header .dropdown-menu > .active > a:hover,
		#header .dropdown-menu > .active > a:focus,
		#header .nav-collapse .nav > li > a:hover,
		#header .nav-collapse .nav > li > a:focus,
		#header .nav-collapse .dropdown-menu a:hover,
		#header .nav-collapse .dropdown-menu a:focus {
			color: #a30837;
		}
	}

	footer {
		background: #ffffff;
	}

	div#header.navbar {
		background: #ffffff;
	}

	.section-head h1 {
		color: #ffffff;
	}

	.portfolio-bannercontainer {
		width: 100% !important;
		position: relative;
		padding: 0;
		max-height: 600px !important;
		overflow: hidden;
		margin-bottom: 50px;
	}
  
  
}
