css:
@-moz-document regexp("^https?://[\\w\\d.]*?(wikia?|(wiki)?pedia)\\.\\w{2,4}.*$") {
* {
    background: none !important;
    background-color: white !important;
    border-color: black !important;
    background-image: none !important;
    box-shadow: none !important;
}
a:link {
    color: blue !important;
    font: bold !important;
    font: underline !important;
    font: arial !important;
}
a:visited {
    color: purple !important;
    font: bold !important;
    font: underline !important;
    font: arial !important;
}
a:hover {
    color: red !important
}
a:active {
    color: red !important
}
#footer,
#adsfrm,
#ads,
#frontpage,
#footer-ads,
#video-sidebar,
#ad_creative_1,
#post-supp,
#marquee-frame,
#ad panorama,
#illustration_overlay,
#PageShareContainer,
#WikiaTopAds,
#ad-skin,
#hubs,
#wikia-ad,
#WikiaFooter,
#WikiaRecentActivity,
#WikiaArticleFooter,
#WikiaTopAds,
.home-top-right-ads,
.background-image-gradient,
.ad,
.ads,
.hubs-container,
.hubs-entry-point,
.hubs-menu-wrapper,
.start-wikia-container,
.global-footer,
body.background-dynamic.skin-oasis.background-fixed::after,
body.background-dynamic.skin-oasis.background-fixed::before,
.buttons,
.header-column_header-tally,
.wikia-logo-container_table-cell,
.editsection {
    display: none !important;
}
.WikiaMainContent,
#WikiNav {
    width: 100% !important;
}
.WikiaPageBackground {
    background-image: none !important;
}
html,
html * {
    background-color: #FFFFFF !important;
    background-image: none !important;
    border-color: #cccccc !important;
    border-width: 1px !important;
    border-collapse: collapse !important;
    color: #000000 !important;
    font-size: 12px !important;
    font-family: Arial, sans-serif !important;
    text-decoration: none !important;
}
h1,
h2,
h3,
h4,
h5,
h6,
h7,
h8 {
    font-size: 12px !important;
    font-weight: bold !important;
    text-decoration: none !important;
}
input[type=text],
input[type=password],
input[type=file],
input[type=search],
textarea,
input[type=button],
input[type=submit],
#lastpass-notification button[type="button"] {
    border: 1px #AAA !important;
    background-color: #FFFFFF !important;
    color: #000000 !important;
}
html::selection,
html *::selection {
    background: #888888 !important;
    color: #fff !important;
}
object,
embed,
param,
video,
audio {
    display: none !important;
    opacity: 0.0 !important;
}

/*[[dimimages]]*/

}

style_setting_options.value:
img,
input[type=image] {
    opacity: 0.02 !important;
}
img:hover,
input[type=image]:hover {
    opacity: 1 !important;
}
