css:
/* TODO - wrap sections using a class selector to truely limit them to that section */


@-moz-document url(https://mail.notes.na.collabserv.com/verse?) {
/* MAIN STYLES */
body, .seq-window .seq-header {
    background-color: #242424;
    color: #eee;
} 
/* END -- MAIN STYLES */



/* PEOPLE ACTION BAR STYLES */
.setSideBar {
    background-color: #242424;
    border-bottom-color: #999;
}

.people-set .ic-bizcard-section1 .action {
    background-color: #000;
    border-color: #bcbcbc;
}

.set-base {
    background-color: #191919;
    border-color: #bcbcbc;
}

.avatar .initials {
    color: #cbcbcb;
}

.suggested-sets::before {
    border-right-color: #666;
}

.oti-view {
    color: #bcbcbc;
}

.keyset.inbox .set-icon svg, .keyset.needs-attention .set-icon svg, .keyset.waiting-for .set-icon svg {
    fill: #f1f1f1;
}


.keyset.inbox.show-focus-count .focus-count, .keyset.needs-attention.show-focus-count .focus-count, .keyset.waiting-for.show-focus-count .focus-count {
    background-color: #676767;
    color: #f1f1f1;
}
/* END -- PEOPLE ACTION BAR STYLES */


/* COMPOSE AND SEARCH BAR STYLES */
.seq-search-container .seq-search-bar {
    color: #f1f1f1;
    background-color: #333;
}

.placeholder {
    color: #bcbcbc;
}

.seq-window .seq-header {
    border-bottom: 1px solid #999;
}

.seq-window .compose-button, .seq-window .outgoing-button {
    border-right-color: #333;
}

.seq-search-container {
    background-color: #333;
}

.seq-search-container .searchButton, .setSideBar .add-contacts, .setSideBar .remove-contacts {
    fill: #bcbcbc;
}

.seq-search-container .clearSearchButton {
    background-color: #bcbcbc
}

.seq-search-container .clearSearchButton .close-nocircle {
    fill: #333;   
}

.chiclet  {
    background-color: #bcbcbc;
    color: #000;
}

.chiclet.selected .chiclet-label {
    color: #f1f1f1;
    background-color: #4178BE;
}
/* END -- COMPOSE AND SEARCH BAR STYLES */



/* FOLDER AND INBOX STYLES */
.seq-mcv, .seq-mcv.preview.folders-open .left-header {
    background-color: #333;
}

.seq-mcv .left-header {
    border-bottom-color: #999;
    background-color: #242424;
}

.seq-mcv.facets-open .filters, .seq-mcv.folders-open .filters {
    border-color: #666;
}

.seq-mcv .message-list-buttons .facets-toggler svg, .seq-mcv .filters .facets-toggler svg, .seq-mcv .message-list-buttons .folders-toggler svg, .seq-mcv .filters .folders-toggler svg, .seq-mcv .message-list-buttons .toggle svg, .seq-mcv .filters .toggle svg {
    fill: #bcbcbc;
}

.seq-mcv .date-header {
    color: #bcbcbc;
}

.seq-mcv .refresh-button .get-new-mail {
    fill: #bcbcbc;
}

.seq-mcv .message-display-settings .threads-state {
    background-color: #333;
    fill: #bcbcbc;
}
/* END -- FOLDER AND INBOX HEADER STYLES */



/* FOLDER FILTER STYLES */
.seq-mcv.facets-open .facets-container {
    background-color: #242424;
    color: #bcbcbc;
    border-color: #999;
}

.seq-mcv .facets-container .facets-title .results {
    color: #999;
}

.seq-mcv .facets-container .facet-lists-all .facet-list-title {
    color: #f1f1f1;
}
/* END -- FOLDER FILTER STYLES */



/* FOLDER PANEL STYLES */
.folder-tray {
    background: #242424;
    color: #bcbcbc;
    border-color: #999;
}

.folder-tray input.folder-search-input {
    background-color: #333;
    color: #f1f1f1;
}

.folder-tray input.folder-search-input::-webkit-input-placeholder { 
    color: #666; 
}

.folder-tray .cancel-search-button, .folder-tray .cancel-search-button svg {
    background-color: #333;
    color: #999;
    fill: #999;
}

.folder-view .this-folder > .this-folder-container .folder-icon {
    fill: #bcbcbc;
}

.folder-view .folder-name {
    color: #f1f1f1;
}

.edit-folder-view {
    background-color: #333;
}

.folder-view.loadedFolderSelect > .this-folder:before {
    border-right-color: #325C80;
}
