.seq-mcv.preview.folders-open .left-header {
	width: calc((40% + 200px * 0.75) - 10px);
}

.seq-mcv.facets-open.preview .preview-panel, .seq-mcv.folders-open.preview .preview-panel {
    width: calc((60% - 200px * 0.75) + 10px);
	left: calc((40% + 200px * 0.75) - 10px);
}

.seq-mcv.facets-open .inbox-container, .seq-mcv.folders-open .inbox-container {
    left: 200px;
}

.seq-mcv.facets-open .facets-toggler, .seq-mcv.folders-open .facets-toggler {
    margin-right: 131px;
}

.folder-tray, .seq-mcv.facets-open .facets-container {
    width: 200px;
}
*/
.folder-tray {
    padding-top: 0;  
}

.folder-view .this-folder {
    height: 25px;
    padding: 0 13px;
}

.folder-tray .folders-title, .folder-tray-float .folders-title {
    margin: 8px 8px 8px 13px;   
}

.folder-tray input.folder-search-input {
	margin: 0 0 8px 13px;
} 
/* END -- FOLDER PANEL STYLES */



/* INBOX STYLES */
.seq-mcv .inbox-container {
    border-color: #999;
}

.messageList {
    background-color: #333;
    color: #f1f1f1;
}

.seq-msg-row {
 	border-bottom-color: #999;   
}

.seq-msg-row.unread {
    background-color: #292929;
}

.seq-msg-row.selected {
    background-color: #131313;
}

.seq-msg-row .msg-info {
	color: #bcbcbc;
}

.seq-msg-row .date {
    color: #ccc;
}

.seq-msg-row .subject {
    color: #4178BE;
}

.seq-msg-row.unread .subject {
    color: #60BBFF;
}

.seq-msg-row .from, .seq-msg-row.unread:not(.thread) .from, .thread-people .person.unread {
    color: #f1f1f1;
}

.seq-msg-row.thread .count {
    color: #f1f1f1;
    background-color: #666;
}

.seq-msg-row .abstract {
	color: #999;
}

.triage-actions .triage-action svg {
    fill: #bcbcbc;
}

.seq-msg-row .divider {
    background-color: #444;
    color: #bcbcbc;
}
/* END -- INBOX STYLES */



/* MAIL PANEL STYLES */
.socmail-mailreadview .mail-read-view, .preview-panel.threaded > .content .thread-info {
    color: #000;
}
/* END -- MAIL PANEL STYLES */



/* PREVIEW PANEL STYLES */
.preview-panel, .preview-panel.threaded {
    background-color: #1f1f1f;
}

.preview-panel {
    margin-left: -1px;
    border-left: 1px solid #999;
}

.preview-panel .action-bar {
    background-color: #333;
    border-color: #999;
}

.socpimMailingList .socpimMailSenderLabel, .socpimMailingList .socpimToLabel, .socpimMailingList .socpimCcLabel, .socpimMailingList .socpimBccLabel {
    color: #999;   
}

.socpimMailingList .socpimNameBtn {
    color: #4178BE;   
}

.preview-panel.threaded > .content .thread-info {
    background-color: #333;
    color: #bcbcbc;
}

.preview-panel.threaded > .content .thread-info, .preview-panel.threaded > .content .read-content > .read-view {
   background-color: #333;   
}

.socmail-mailreadview .mail-read-view:not(.collapsed-mail-read-view) {
 	background-color: #1f1f1f;   
}

.preview-panel .thread-info .unread-count {
    color: #bcbcbc;
}

.has-unread.preview-panel .thread-info .subject {
    color: #f1f1f1;
}

.preview-panel.threaded > .action-bar .title, .seq-window .preview-panel .action-bar > button.action:not(.icon) {
    color: #bcbcbc;   
}

.read-view button.toggle-btn .twisty {
    fill: #666;
}

.read-view button.toggle-btn .triangle {
    fill: #f1f1f1;
}

.seq-window .preview-panel .action-bar .action {
     color: #000;   
}

.seq-window .icon svg {
    fill: #bcbcbc;
}

.seq-window .read-view > .action-bar .folder-chiclet {
    background-color: #ccc;   
    color: #000;
}

.seq-window .read-view > .action-bar .folder-chiclet .chiclet-close {
    fill: #1f1f1f;
}

.preview-panel.empty .content {
    background-color: #1f1f1f;   
}

.seq-mcv .bee {
    fill: #333;
}

.socmail-mailreadview .mail-read-view, .lotusui30 .lotusMeta, .socpimEventDetails .socpimEventFieldName, .socpimInviteeCount, .socpimInviteeList .pimHeading  {
    color: #bcbcbc;
}

.socmail-calendarreadview .pimEventChair .pimPerson, .socmail-calendarreadview .pimDelegateTo .pimPerson, .socmail-calendarreadview .pimSentBy .pimPerson, .pimInviteAction, .pimInviteState .pimTime, .pimInvitees .socpimNameBtn {
    color: #4178BE;
}

.lotusui30 .lotusHeading {
    color: #f1f1f1;
}

.socmail-mailreadview .pim-mailread-mailcontent {
    border-top: 1px solid #999;
    margin-top: 10px;
    padding-top: 20px;
}

.socmail-calendarreadview .pimEventDetails, .socmail-calendarreadview .pimInviteeList, .socmail-calendarreadview .pimMailReadHeader {
    border-bottom-color: #999;   
}

/* END -- PREVIEW PANEL STYLES */



/* CALENDAR BAR STYLES */
.calendar-Container {
    background-color: #242424;
    color: #f1f1f1;
}

.calendarDate, .eventBar {
    background-color: #333;
    color: #f1f1f1;
}

.grayEventBar {
    background-color: #232323;
}

.bar.event.unprocessed {
    background-color: #FF8843; 
}

.currentTimeBar {
    background-color: #f1f1f1;
}

.nextMeetingLabel {
    color: #bcbcbc;
}

.calendar-Container button svg, .calIcon .calendar {
    fill: #bcbcbc;
}
/* END -- CALENDAR BAR STYLES */




/* COMPOSE WINDOW STYLES */
.dockable .content-container, .socpimComposeView .socpimComposeDialog .socpimDialogBody, .socpimComposeView .socpimComposeDialog .socpimMailHeader .lotusForm2, .socpimComposeView .socpimComposeDialog .socpimSubject input, body.socpimEditorBody.cke_editable.cke_editable_themed.cke_contents_ltr {
    background-color: #131313;   
    color: #bcbcbc;
}

.socpimComposeView .socpimComposeDialog .socpimToolbarCtrl, .socpimComposeView .socpimComposeDialog .socpimDialogFooter {
    background-color: #333;
}

.socpimComposeView .socpimComposeDialog .lotusForm2 .lotusFormField.socpimTo, .socpimComposeView .socpimComposeDialog .lotusForm2 .lotusFormField.socpimCc, .socpimComposeView .socpimComposeDialog .lotusForm2 .lotusFormField.socpimBcc, .socpimComposeView .socpimComposeDialog .lotusForm2 .lotusFormField.socpimSubject, .socpimComposeView .socpimComposeDialog .lotusForm2 .lotusFormField.socpimRcptSummary, .socpimComposeView .socpimComposeDialog .socpimDialogFooter, .socpimComposeView .socpimComposeDialog .socpimToolbarCtrl, .socpimComposeView .socpimComposeDialog .socpimDialogFooter {
    border-color: #999;
}

.socpimComposeView .socpimTo .ccbcc-link, .socpimComposeView.showTo .socpimTo::before, .socpimComposeView.showCc .socpimCc::before, .socpimComposeView.showBcc .socpimBcc::before  {
    color: #bcbcbc;
}

.socpimComposeView .socpimComposeDialog .socpimSubject input::-webkit-input-placeholder {
    color: #666;
}

.socpimToolbarCtrl.sticky-header .socpimToolbarBtn.delivery-options-button, .socpimComposeView .socpimComposeDialog .socpimDialogFooter .socpimLeft {
    color: #999;
}

.socpimComposeView .socpimToolbarBtn svg {
    fill: #f1f1f1;
    background-color: #333;
}

.socpimToolbarCtrl.sticky-header .delivery-options .insert-signature-button {
	color: #4178BE;
}

.socpimComposeView .socpimComposeDialog .socpimDialogFooter .socpimDiscard {
    background-color: #666;
    border-color: #bcbcbc;
    color: #f1f1f1;
}

.socpimComposeView .socpimComposeDialog .socpimDialogFooter .socpimBtn.socpimSend {
 	background-color: #4178be;
    border-color: #999;
    color: #f1f1f1;
}
/* END -- COMPOSE WINDOW STYLES */
}


