css:
/* i really want this to be global */
button.thanks {
    border: 1px solid #fdc7c1;
}

.mint-stream-question__avatar {
    width: 32px;
    height: 32px;
}

.js-presence-answers .mint-avatar, .mint-stream-presence__answerer .mint-avatar {
    width: 24px;
    height: 24px;
}

.mint-content-box__header {
    height: 2rem;
}

.mint-content-box--spaced .mint-content-box__actions, .mint-content-box--spaced .mint-content-box__content, .mint-content-box__header--spaced {
    padding-left: 48px;
}

.mint-horizontal-separator--spaced {
    border-color: #e3e3e3;
    margin: 1rem 0;
}

body {
    margin: 0 auto;
}

.mint-subjects-filter {
    background-color: #fbfbfb;
    margin: -48px auto 0;
    max-width: 984px;
    border-top-left-radius: 11px;
    border-top-right-radius: 11px;
}

.mint-layout__container--no-margin {
    background-color: #fbfbfb;
    width: 1024px;
    margin: 0 auto 48px;
    border: 1px solid none;
    border-bottom-left-radius: 11px;
    border-bottom-right-radius: 11px;
}

.mint-subjects-filter__list-element a {
    background-color: #fbfbfb;
}

.mint-stream__heading--spaced-top {
    margin: 0px 0 24px 0;
}

.mint-stream-question__avatar .mint-breadcrumb-list, .mint-link--gray, .mint-text--gray {
    color: #999;
    font-weight: normal;
    font-size: 15px;
    text-transform: none;
}

.logged-in .mint-layout__container, .logged-out .mint-layout__container--reversed-order {
    background-color: #fbfbfb;
    width: 1024px;
    margin: 100px auto 48px;
    border: 1px solid none;
    border-top-left-radius: 11px;
    border-top-right-radius: 11px;
    padding-top: 2rem;
}

.js-presence-counter {
    font-size: 12px;
}

.mint-ranking__item-hole .mint-link {
    font-weight: 400;
}

.mint_answer__comments .mint_comments__send-button {
    top: -32px;
    left: 450px;
    height: 28px;
}

.mint_comments__send-button {
    top: -32px;
    left: 496px;
    height: 28px;
}

.mint #aside-content .task .task-box .question-box .question {
    font-size: 16px;
    line-height: 24px;
}

.mint #aside-content .task .task-box .task-info, .mint #aside-content .task .task-box .task-info li a, .mint #aside-content .task .task-box .task-info li {
    text-transform: none;
    font-size: 14px;
    font-family: ProximaNovaRegular;
    color: #999;
}

.mint:after, .logged-out:after {
    background-image: url("http://ru.static.z-dn.net/files/admin/questionpagebackground2xnew.jpg");
    background-repeat: no-repeat;
    background-position: 20% 0%;
    background-color: #eae9ee;
    bottom: 0;
    content: "";
    height: 100%;
    left: 0;
    opacity: 1;
    position: fixed;
    right: 0;
    top: 0;
    width: 100%;
    z-index: -1;
}

.mint #page-outer {
    -moz-box-shadow: 0px 0px 11px 2px rgba(117,116,117,0.22);
    -webkit-box-shadow: 0px 0px 11px 2px rgba(117,116,117,0.22);
    background-color: #fbfbfb;
    border-radius: 9px;
    box-shadow: 0px 0px 3px 2px rgba(117,116,117,0.02);
    margin: 0 auto;
    margin-bottom: 48px;
    padding-top: 0px;
    top: 100px;
    width: 1024px;
}

.mint #page-outer #page-container {
    margin-top: 24px;
}

.mint {
    background-color: #f6f6f6;
}

.mint #page-outer.stream-horizontal #content, .mint #page-outer.stream-horizontal #aside-content, #stream .task .task-box, #aside-stream .task .task-box, #brainly_stream .task .task-box {
    background-color: transparent;
}

.mint_comments__comment_content {
    color: #666;
    font-size: 15px;
}

.mint-text-block--gray, .mint_editor__timer, .mint_dummy_editor__add_answer_link, .mint_comments__comment_text, .mint_tasklines__date, .mint_tasklines__time, .mint_answer_author__nick, .mint_answer_author__rank, .mint_brain_author_bar__nick, .mint_brain_author_bar__rank, .mint_great_job__loading, .mint_great_job__error, .mint_great_job_info__text, body.mint.mint.mint.mint.mint.mint .mint_toplayer__text {
    color: #999;
    font-family: ProximaNovaRegular;
    font-size: 14px;
}

.mint_tasklines__text {
    color: #999;
    font-size: 14px;
}

.mint_tasklines__text a {
    color: #999;
    font-size: 14px;
}

.mint_actions {
    font-size: 14px;
}

.container.mint-layout__wrapper__container {
    background-color: #fff;
    border-radius: 11px;
    margin-top: 100px;
    width: 1024px;
    margin: 100px auto 0;
}

.mint-layout__container {
    padding-top: 48px;
}

/* moderate all */

body, #moderation-all .content .moderation-item .content .body .alert-error {
    font-size: 15px;
}

#moderation-all .content .moderation-item .content .footer .row {
    font-size: 15px;
}

/* moderator panel */

.mint-moderator-menu__list {
    overflow: auto;
    height: 500px;
}

.mint-menu-list__element {
    height: 2rem;
    line-height: 2rem;
    font-size: 13px;
}

#moderate-functions-panel .moderate-panel .moderate-panel-content>ul>li {
    font-size: 13px;
}

#moderate-functions-panel .moderate-panel .moderate-panel-content>ul>li.nolink {
    color: #000;
}

.moderation-toplayer div.text {
    font-size: 14px
;
}
.moderation-taskline .task_lines {
    font-size:14px;
}
