css:
@-moz-document domain("youtube.com") {
body {
    background: white;
}

#page-container {
	padding-top: 2rem;
}

.yt-card {
	box-shadow: none;
    padding: 1rem 0 !important; 
    background: none;
}

.user-photo, .yt-thumb-clip img {
	border-radius: 50%;
}

#watch7-sidebar-contents {
	padding-top: 0 !important;
}

#yt-masthead-container {
    background: #DA1928;
    border-bottom: none;
}

#theater-background, .player-api {
	background-color: white !important;
}


.yt-uix-button-default, .yt-uix-button-default[disabled], .yt-uix-button-default[disabled]:hover, .yt-uix-button-default[disabled]:active, .yt-uix-button-default[disabled]:focus, .masthead-search-terms-border {
	border: none;
}

#masthead-search-terms {
	border-radius: 5px 0 0 5px; 
	overflow: hidden;
}

.yt-uix-button {
	border-radius: 5px;
}

#placeholder-player {
	margin-bottom: 3rem;
}

#yt-masthead #logo-container .logo {
	background: none;
}

#yt-masthead #logo-container .logo::before {
	content: "youtube";
    color: white;
    font-size: 20px;
}

@media only screen and (max-width: 498px) and (min-width: 0px), only screen and (max-width: 704px) and (min-width: 499px) {
   .exp-responsive #yt-masthead #logo-container {
        display: none;
    }
}


}
