@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document
	url-prefix("http://runouw.com")
{
/* Reset backgrounds */
span.corners-top, span.corners-bottom, span.corners-top span, span.corners-bottom span, .forabg, .forumbg, .bg1, .bg2, ul.forums,
li.row:hover, #tabs a {
	background: none !important;
}

body
{ background-image: url(http://i.imgur.com/GRnclWc.png) !important;}
/* Background colors */
.forabg, .forumbg {
	background-image: url(http://i.imgur.com/cm3Rq59.png) !important;
}

.headerbar {
	background: none !important;
}

#navigation a {
	background: linear-gradient(to right, #555 0%, #666 100%) !important;
}

#navigation a:hover, #navigation #active-subsection a {
	background: linear-gradient(to right, #333 0%, #444 100%) !important;
}
html, body, select, input.search, .inputbox, .pagination span a, .pagination span a:link, .pagination span a:visited,
dl.codebox {
	background-color: #333 !important;
}
.bg1, ul.forums, .panel, #tabs a {
background-image: url(http://i.imgur.com/D9IFv4F.png) !important;
}

.navbar{
	background: none;
} 

.bg2 {
	background-image: url(http://i.imgur.com/D9IFv4F.png) !important;
}
li.row:hover {
	background-color: #5A2222 !important;
}

blockquote {
	background-color: #666 !important;
}
.pagination span a:hover, .pagination span strong {
	background-color: #888 !important;
}
.pagination span strong {
	background-color: #a96 !important;
}
.postlink:hover {
	background-color: #ddd !important;
}
#tabs a span {
	background: inherit !important;
}
/* Text colors */
html, body, .headerbar, h2, h3, .postbody, .postbody .content, #tabs .activetab a span, ul.topiclist li,
.panel li.header dd, .panel li.header dt {
	color: #ddd !important;
}
	.inputbox {
color: #bbb !important;
}
a:hover {
	color: #AF0000 !important;
}
.postprofile, dl.details dd {
	color: #aaa !important;
}
li.row strong, .postprofile strong, label, .panel, #tabs a span, dl.details dt {
	color: #999 !important;
}
select, input.search, .inputbox, .pagination span a, .pagination span a:link, .pagination span a:visited {
	color: #888 !important;
}
.postlink:hover, .pagination span a:hover, .pagination span strong {
	color: #333 !important;
}
dl.codebox code {
	color: #6a6 !important;
}
/* Border colors */
hr {
	border-color: #222 #333 #333 !important;
}
blockquote {
	border-color: #333 !important;
}
dl.codebox {
	border-color: #363 !important;
}
select, input.search, .inputbox, .pagination span a, .pagination span a:link, .pagination span a:visited, .pagination span a:hover, .pagination span strong {
    border-color: #444 !important;
}
h2, h3 {
	border-bottom-color: #222 !important;
}
ul.navlinks, #cp-main h3 {
	border-bottom-color: #333 !important;
}
dl.codebox dt {
	border-bottom-color: #363 !important;
}
#tabs .activetab a {
    border-bottom-color: #444 !important;
}
.postlink {
	border-bottom-color: #a96;
}
li.row {
	border-top-color: #333 !important;
	border-bottom-color: #222 !important;
}
.signature, ul.cplist {
	border-top-color: #333 !important;
}
ul.topiclist dd, .postprofile {
	border-left-color: #333 !important;
}
/* Other */
.forabg, .forumbg {
	border-radius: 4px;
}
#wrap {
	width: 960px;
	margin:0 auto;
	margin-top:5px;
	background-color:#202020;
	border: 2px solid #303030;
}
div#page-header a:link, div#page-header a:visited, ul.linklist a:link, ul.linklist a:visited {
    color: #C90000;
}
	a.forumtitle:link, a.forumtitle:visited, a.topictitle:link, a.topictitle:visited {
    color: #C90000;
}
	h2 a:link, h2 a:visited, .pagination a:link, .pagination a:visited, a.left-box:link, a.left-box:visited, a.right-box:link, a.right-box:visited {
    color: #C90000 !important;
}

