css:
@-moz-document domain("faceit.com") {
a,
body {
    color: #848484;
}

a:focus,
a:hover {
    color: #FF6C00;
}

body,
.main-container {
    background-color: #141616;
}

.full-hr {
    border-color: #525252;
}

hr {
    border-top: 1px solid #525252;
}

.item-card {
    border: 1px solid #525252;
}

.btn-default {
    color: #FFFFFF;
    background-color: #FF3600;
    border-color: #FF3600;
}

.btn-default.active,
.btn-default.focus,
.btn-default:active,
.btn-default:focus,
.btn-default:hover,
.open>.btn-default.dropdown-toggle {
    color: #fff;
    background-color: #f03300;
    border-color: #d12c00;
}

.dropdown-menu {
    background-color: #333;
    border: 1px solid #525252;
}

.dropdown-menu__title {
    color: #848484;
    border-bottom: 1px solid #525252;
}

.dropdown-menu>li>a {
    color: #FF3600;
}

.dropdown-menu>li>a:focus,
.dropdown-menu>li>a:hover {
    color: #2B2B2B;
    background-color: #FF3600;
}

.mini-profile .dropdown-menu .divider {
    background-color: #525252;
}

.notification-item {
    color: #E48735;
    border-bottom: 1px solid #525252;
}

.link-secondary,
.notification-item a {
    color: #848484;
}

.notification-nav__list .dropdown-menu:before {
    border-bottom: 7px solid #333;
}

.subpage-nav {
    border-bottom: 1px solid #525252;
    background-color: #262626;
}

.page-title {
    border-bottom: 1px solid #525252;
}

.subpage-nav__list__link {
    color: #848484;
}

.game-card__row:nth-child(even),
.report__menu {
    background-color: #141616;
}

.game-card__row:last-child {
    border-top: 1px solid #525252;
}

.game-card {
    border: 1px solid #525252;
}

.well--light {
    background-color: rgba(0, 0, 0, 0);
}

.well {
    background-color: #262626;
    border: 1px solid #525252;
}

.heading-border {
    border-bottom: 1px solid #525252;
}

.social-tabs .nav.nav-justified>li.disabled>a {
    background: #333333;
    color: #848484;
}

.social-tabs .nav.nav-justified>li.active:last-child>a,
.social-tabs .nav.nav-justified>li:last-child>a {
    border-right: 1px solid #525252;
}

.social-tabs .nav.nav-justified>li>a {
    border-top: 1px solid #525252;
    border-bottom: 1px solid #525252;
    border-left: 1px solid #525252;
}

.nav>li>a:focus,
.nav>li>a:hover {
    text-decoration: none;
    background-color: #262626;
}

.social-tabs .nav.nav-justified>li>a:focus,
.social-tabs .nav.nav-justified>li>a:hover {
    border-bottom-color: #525252;
    border-right-color: #525252;
}

.form-control {
    background-color: #333;
    border: 1px solid #525252;
}

.settings-menu__item,
.settings-menu__item:nth-child(3) {
    border-right: 1px solid #525252;
}

.match-configuration__players__item--separated,
.match-room__configuration,
.ongoing-match,
.settings-menu__item {
    border-bottom: 1px solid #525252;
}

.settings-menu__link:after {
    border-bottom: 11px solid #525252;
}

.settings-menu__link:before {
    border-bottom: 12px solid #525252;
}

.settings-group__heading {
    border-bottom: 1px solid #525252;
}

.settings-group .form-group,
.settings-group--border-bottom {
    border-bottom: 1px solid #525252;
}

.membership-plans__plan {
    border-right: 1px solid #525252;
}

.list-lined>li {
    border-bottom: 1px solid #525252;
}

.competitions-list__item {
    border-bottom: 1px solid #525252;
}

.link-row-highlight {
    background-color: #262626;
}

.link-row-highlight:hover {
    background-color: #333333;
}

.expander {
    background: #2B2B2B;
    border-top: 1px solid #525252;
}

.competitions-list--ladders {
    border-left: 1px solid #525252;
}

.league-cta--large {
    border-bottom: 1px dashed #525252;
}

.box-list {
    border: 1px solid #525252;
}

.box-list__item {
    border-bottom: 1px solid #525252;
}

.modal-content {
    background-color: #2B2B2B;
    border: 1px solid #525252;
}

.modal-dialog__header {
    background-color: #212121;
}

.input-group-addon {
    background-color: #2B2B2B;
    border: 1px solid #525252;
}

.modal-dialog__actions {
    border-top: 1px solid #525252;
}

.match-vote-item {
    border: 1px solid #525252;
}

.text-secondary {
    color: #FF8F00;
}

.chat {
    border: 1px solid #525252;
}

.chat,
.chat-window {
    background-color: #141616;
}

.text-gray {
    color: #848484;
}

.secondary-content__new {
    color: #FF3600;
    background-color: #333333;
}

.match-team__item,
.match-team__name {
    border-bottom: 1px solid #525252;
}

.match-team-member--right .match-team-member__report {
    border-right: 1px solid #525252;
}

.match-team-member__report {
    border-left: 1px solid #525252;
}

.info-panel__item--separated {
    border-right: 1px solid #525252;
}

.ps-container:hover>.ps-scrollbar-y-rail:hover {
    background-color: rgba(0, 0, 0, 0);
}

.users-list__item {
    border-bottom: 1px solid #525252;
}

.performance-stats__nav .nav-pills>li {
    border-bottom: 1px solid #525252;
}

.table-hover>tbody>tr:hover,
.table>tbody>tr.active>td,
.table>tbody>tr.active>th,
.table>tbody>tr>td.active,
.table>tbody>tr>th.active,
.table>tfoot>tr.active>td,
.table>tfoot>tr.active>th,
.table>tfoot>tr>td.active,
.table>tfoot>tr>th.active,
.table>thead>tr.active>td,
.table>thead>tr.active>th,
.table>thead>tr>td.active,
.table>thead>tr>th.active {
    background-color: #2B2B2B;
}

.table>tbody>tr>td,
.table>tbody>tr>th,
.table>tfoot>tr>td,
.table>tfoot>tr>th,
.table>thead>tr>td,
.table>thead>tr>th {
    border-top: 1px solid #525252;
}

.table>thead>tr>th {
    border-bottom: 2px solid #525252;
}

.match-pref-overview__top__xl__keys__single-key {
    border-bottom: 1px solid #525252;
}

.ps-container.ps-in-scrolling.ps-y>.ps-scrollbar-y-rail>.ps-scrollbar-y {
    background-color: #141616
}

.match-configuration__votables__item--separated {
    border-right: 1px solid #525252;
}

.circle-spinner__outer:after {
    background: #2B2B2B;
}

.match-vote-item--dropped {
    background-color: #2B2B2B;
}

.match-stat-row th {
    background-color: #353433;
}

.table>tbody+tbody {
    border-top: 2px solid #525252;
}

.btn-link {
    color: #FF7800;
}

.game-register__list__game:before {
    border-bottom: 12px solid #2B2B2B;
}

.coming-soon {
    border: 1px solid #525252;
}

.coming-soon__action-box--light-bg {
    background-color: rgba(0, 0, 0, 0);
}

.btn-gray-light {
    color: #FF7800;
    background-color: #141616;
    border-color: #525252;
}

.match-vs__details__main .drop-text-hide {
    border: 1px solid #525252;
}

.text-secondary {
    color: #00A000!important;
}

.popover {
    background-color: #333;
}

.share-button__divider {
    background-color: #525252;
}

.btn.focus,
.btn:focus,
.btn:hover {
    color: #FF7800;
    text-decoration: none;
}

.popover.bottom>.arrow:after {
    border-bottom-color: #333;
}

.mini-profile .dropdown-menu:before {
    border-bottom: 7px solid #333;
}

.mini-profile .dropdown-menu:before:after {
    border-bottom: 7px solid #333;
}

.profile-view .premade-teams__new__container .premade-teams__new__details a {
    color: #FF7800;
}
}
