css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.upwork.com") {
  .font-gotham-bold, .logo, .body-content a, .accordion .content h4
  {
    font-family: "Arial", sans-serif;
    font-weight: normal;
  }
  
  .oPageCentered {
width: 1280px;
} 

  .text-primary { color: #333; }
  .oBtnPrimary, .oBtnVisitorPrimary { background: #494949; }
  .oHeader .oTwizzlr .oNavTab.isCurrent { color: #00468c; }
  .oHeader .oTwizzlr .oNavTab { font-family: Arial, sans-serif; }
  .oHeader .oTwizzlr .oSecondaryNavList a { font-family: Arial, sans-serif; }
  
    .col-md-8 {
    width: 75%;
}
  .col-md-9 {
    width: 75%;
}

  .topic-name {
    width: 144px;
}
  
  /* Search Result Job Title */
  body { font-family: Arial, sans-serif; }

  /* Search Result Job Title */
  a, .oBtnTertiary, .oBtnCancel, .oBtnPopup, .oThumbList figcaption, .oMoreSmall, .oLink { color: #00468c; font-family: Arial, sans-serif; }
  
  .jsType {
    color: #333;
    font-size: 13px;
}  
  #jsTier  , .jsBudget{
    color: #333;
    font-size: 13px;
}
  .jsAutoRelativeTime{
    display: block;
    position: absolute;
    top: 16px;
    right: 0px;
    font-size: 16px;
    color: #fff;
}  

  .oSupportInfo {
    
    font-size: 13px;
    font-weight: 400;
    line-height: 16px;
}
    
  .jsPosted{display: none;}
  
  .oLinkButton, .oMore, .oMoreLarge {
    color: #00468c!important;
    font-weight: 400 !important;
    text-transform: none !important;
}
  
  
  
  /* Counter */
  .oListLite { counter-reset: section; }
  .oJobTile:before {
    color: #333333;
    float: left;
    font-size: 45px;
    height: 59px;
    margin: 24px 0;
    text-align: center;
    text-shadow: 1px 1px 1px #FFFFFF;
    width: 70px;
  }
  
   .oRowTitle {
    display: block;
    font: 700 20px "Arial";
    margin-bottom: 6px;
}
  
  body {
    background-color: #ffffff;
    color: #494949;
    font-family: "Gotham Book",sans-serif;
    font-size: 13px;
    min-height: 100%;
    min-width: 960px;
    padding-bottom: 150px;
    position: relative;
}
  .oJobResults { 
    box-shadow: 0 0 0 1px #fff;
  
}
  
  .oSupportInfo {
    font-size: 13px;
    color: #494949;
    font-weight: 400;
    line-height: 16px;
}
  
  .oJobResults .oRowTitle.oH3 { 
    width: 95%;
}
   
 
  
  .oWidget.F2470.jsFwhProfile {
    display: none;
}
  
  .jsContractorTips {
    display: none;
}
  
  .jsFeedRSS {
    display: none;
}
  .oSkill {
    background-color: #fff;
    color: #00468c;
}
  
  .air-card {
    background-color: #fff;
    box-shadow: none;
}
  
  .eo-button-save > span::before, .eo-tabset .tab-content .form-message::before, .form-message::before, .glyphicon-md {
    display: none;}
  
  .btn-primary, .btn-primary:focus {
    background-color: #00468c;
    color: #fff;}
  
  
  .job-feedback .dropdown-toggle .o-icon-thumbs-down{display:none;}
  
  .label-info {background-color: #00468c;}
  
  .label {
    border-radius: 0.25em;
    color: #fff;
    display: inline;
    font-size: 80%;
    font-weight: 500;
    line-height: 1;
    padding: 0.2em 1.6em 0.3em;
    text-align: center;
    vertical-align: baseline;
    white-space: nowrap;
}
  
  .col-md-4.p-0-right:first-child {
    margin-left: 20px;
    
}
  
  .p-0-right {
    border-bottom: 1px solid #aaaaaa;
    border-left: 1px solid #aaaaaa;
    border-top: 1px solid #aaaaaa;
    background: #f1f1f1;
    height: 90px;
    padding: 11px 7px;
}
  
  .col-md-4.p-0-right:last-child,.col-md-6.p-0-right:last-child {
    border-right: 1px solid #aaaaaa;
}
  
  .air-card + .air-card {
    border-top: 1px solid #aaa;
    margin-top: -54px;
}
  
  
  
  .oHeader .oTwizzlr .oNavTab:hover {
   color: #00468c
}
  
  .oNavLink, .oNavLink .isCurrent, .oSecondaryNavList > .isCurrent a {font-weight:600 !important;}
  
  .oHeader .oTwizzlr .oSecondaryNavList a,.header-navbar-skinny .navbar-default.secondary-navbar .navbar-nav > li > a, .header-navbar-visitor .navbar-default.secondary-navbar .navbar-nav > li > a {
   color: #00468c;
   font-family: "Arial";
   font-size: 14px;
}
  
  .oHeader .oTwizzlr .oSecondaryNavList a:hover , .header-navbar-skinny .navbar-default.secondary-navbar .navbar-nav > li > a:hover, .header-navbar-visitor .navbar-default.secondary-navbar .navbar-nav > li > a:hover {
    color: #00468c;
    
}
  
  .oHeader .oTwizzlr .oSecondaryNavList > .isCurrent a, .header-navbar-skinny .navbar-default.secondary-navbar .navbar-nav > li.active a, .header-navbar-visitor .navbar-default.secondary-navbar .navbar-nav > li.active a{
    color: #00468c;
}
  
  .oHeader .oTwizzlr .oSecondaryNavList > .isCurrent a:hover {
    color: #00468c;
}

  .oHeader .oTwizzlr .oNavTab {
    border: 0 none;
    color: #666;
    font-family: "Arial";
    font-size: 12px;
    padding: 29px 15px;
    text-transform: uppercase;
    white-space: nowrap;
}
 
  .oHeader .oTwizzlr .oNavTabpanel,.header-navbar-skinny .navbar-default.secondary-navbar, .header-navbar-visitor .navbar-default.secondary-navbar {
    background: #ffffff none repeat scroll 0 0;
    border-bottom: 1px solid #e0e0e0;
}
  .oNavTabpanel {
    background: #fff none repeat scroll 0 0;
    border-bottom: 1px solid #e0e0e0;
}
  
  .oLeftNav a, .oLeftNav .oBtnTertiary, .oLeftNav .oBtnCancel, .oLeftNav .oBtnPopup, .oLeftNav .oThumbList figcaption, .oThumbList .oLeftNav figcaption, .oLeftNav .oMoreSmall {
    color: #00468c;
    font-family: "Arial";
    font-weight: bolder;
}
    
  
}
