css:
@-moz-document url-prefix("https://www.facebook.com/") {
/*Font*/
body {
    font-family: "Comic Sans MS", cursive, sans-serif !important;
}

/*all Hyperlinks*/
a {
    color: #8BC34A;
}

/*Leftcontent Hyperlinks*/
._bui ._5afe, ._bui .subitem {
    color: #1B5E20 !important;
}

/*Login Page Layout*/
.menu_login_container .login_form_label_field label, .menu_login_container .login_form_label_field a {
    color: #4CAF50 !important;
}

/*Navbar*/
._2s1x ._2s1y {
    background-image: none !important;
    background-color: #8BC34A !important;
    border-bottom: 1px solid #7CB342 !important;
}


/*Logo Change*/
._2md {
    background-image: url("https://yt3.ggpht.com/-Z_NCcO2f0Fw/AAAAAAAAAAI/AAAAAAAAAAA/ivxE5LE9JNI/s100-c-k-no/photo.jpg") !important;
    background-size: 28px 28px !important;
    background-position: center !important;
}

/*Search Button*/
.__wu ._539-.roundedBox .uiSearchInput.reDesignedInput button {
    background-color: #CCFF90 !important;
}


/*Advertisements and Groups*/
#rightCol {
    display: none !important
}

/*Facebook Chat*/
div._50mz .fbDockChatTabFlyout {
    width: 300px !important;
}

.fbNubFlyoutTitlebar {
    background: #8BC34A !important;
    border: 1px solid #7CB342 !important;
}

/*Chat Advertisements*/
#pagelet_canvas_nav_content {
    display: none !important;
}

._50mz {
    width: 200px !important;
}
}
