css:
/*
 * Unofficial dAmn skin
 *
 * Author      : Vayde
 * Created     : Nov 18, 2008
 * Version     : 1.4
 * Website     : http://vayde.deviantart.com
 * Usage       : Use with Stylish (https://addons.mozilla.org/en-US/firefox/addon/2108)
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://chat.deviantart.com/chat") {

.damncr .read, .damncr .read a, .damncr .read b, .damncr .read span, .damncr .bodyarea .read span, div.b, html, #top, .alt0, div.damn-lo-col-left, div.damn-lo-col-right, div.damn-lo-row-bottom, div.damn-lo-row-top, div.damncrc-alertbox, div.damncrc-chat-window, div.damncrc-iconbar {background:#222 !important;}

.damnc-tabbar strong.withX, .damnc-tabbar strong.withX i {background:#333 !important;}

span.damncrc-chat, span.damncrc-chat span, input {background:#666 !important;}

.damncr .other-hl .text span, .damncr .other-hl .from span, .damncr .other-hl .from, .damncr .other-hl, div.damncrc-chat-window .alt0, div.damncrc-chat-window .alt0 span, .damncrc-chat .other-hl .inner .timestamp {background:#888 !important;}

.damncr .action, .damncr span.text, input.empty, .itext#searchInput {background:transparent !important;}

span.damncrc-chat a:hover, div.bodyarea li {color:#aaa !important;}

span.damncrc-chat a {color:#bbb !important;}

.tabbar span[style] {color:#d00 !important;}

div.damncrc-topic a:hover, .damnc-tabbar, .damnc-tabbar span {color:#ccc !important;}

div.damncrc-iconbar-ctrls {color:#fff !important;}

.damntcf-preload, .damnc-tabbar strong.damntcf-highlighted, .damnc-tabbar a.damntcf-highlighted, .damnc-tabbar strong.damntcf-highlighted i, .damnc-tabbar a.damntcf-highlighted i, a.damntcf-highlighted span {color:#0b0 !important;}

.damncr .msg {color:#111 !important;}

.damncrc-input input, textarea.damncrc-input {border-color:#111 !important;}

.damn .alt2, div.damn-lo-row-flex, .alt1-left-border {border-color:#222 !important;}

.alt1-border {border-color:#444 !important;}

div.damncrc-icon-roomname {color:#666 !important;}

div.damncrc-iconbar-ctrls a, dt, div.bodyarea a:hover {color:#888 !important;}

.damncr .join, .damncr .part {color:#2A3737 !important;}

.damncri-member {white-space:normal!important;}

.damncrc-icon-roomname {display:none!important;}

div.damncr-title {color:#aaa !important; border:1px solid #222 !important;}

div.damncrc-iconbar-ctrls a:hover {color:#ccc !important; text-decoration:none !important;}

div.bodyarea a, dd.dAmnChatMember {color:#ddd !important; text-decoration:none !important;}

div.damncr-title a:hover {color:#eee !important; text-decoration:underline !important;}

div.damncr-title a {color:#eee !important; text-decoration:none !important;}

div.bodyarea {background:#444 !important; border-bottom:0 !important;}

div.aside-left {background:#444 !important; padding-bottom:2px !important;}

div.damncrc-topic a {color:#888 !important; text-decoration:none !important;}

.read {border-left:1px solid #888 !important; padding:3px !important;}

div.damn-error, .error-join, .damncrc-error, .damncrc-alertbox
{
background:#222 !important; 
color:#888 !important; 
border-color:#222 !important;
}
div.damncrc-topic
{
background:#444 !important; 
color:#000 !important; 
border: 1px solid #222 !important; 
}
.damnc-tabbar-outer
{
border-top:0 !important;
border-right:0 !important;
border-left:0 !important;
border-bottom:2px solid #111 !important;
}
.damnc-tabbar strong, .damnc-tabbar a 
{
background-image:none !important; 
background-color:#111 !important; 
-moz-border-radius:5px 0 0 0; 
border-bottom:1px solid #111 !important;
}
.damnc-tabbar i {
background-image:none !important; 
background-color:#111 !important; 
-moz-border-radius:0 5px 0 0; 
border-bottom:1px solid #111 !important;
}
.damnc-tabbar strong  
{
background:#111 !important; 
color:#eee !important; 
border-bottom:1px solid #111 !important;
}
.ch-ctrl {background:#222 !important; border:2px #111 solid !important;}

.ch-ctrl .logout a {background-image:none !important; background-color:#222 !important;}

.ch-ctrl .friendsmenu a, .ch-ctrl .collectmenu a 
{
background-image:none !important; 
background-color:#333 !important; 
color:#aaa !important;
}

.ch-ctrl .friendsmenu a:hover, .ch-ctrl .collectmenu a:hover
{
background-image:none !important; 
background-color:#2c2c2c !important;
}

/* shrink tabs and help/more links */
.damnc-helplink,.damnc-morelink
{
width:16px;height:19px;
position:relative;
overflow:hidden;
}
.damnc-helplink {top:-3px!important;}
.damnc-morelink {top:-2px!important;}
.damncrc-chat,.damncrc-iconbar,.damncrc-topic,.damncr-title,.damnc-tabbar
{font-size:8.25pt}
.damnc-tabbar strong{padding:4px 7px !important}
.damnc-tabbar strong.withX{padding-right:20px !important}
.damnc-tabbar strong img{top:1px !important}
.damnc-tabbar a{padding:3px 7px !important}
.damnc-tabbar-outer{padding-top:0 !important}

/* Top text size */
.ch-ctrl .text a {font-size:10px!important;}

/* glink cleanup */
.ch-ctrl .friendsmenu, .ch-ctrl .collectmenu {top:1px!important;}
.ch-ctrl .text .glink span a[href="#sitemap-login-menu"] {background-position:0 1px!important;}
}
