css:
@-moz-document url-prefix("http://www.roblox.com") {
#header {
    background-color: #BD0000
}

#navigation {
    background-color: #690000
}

#vorlias-nav {
    background-color: #690000
}

#chat-header {
    background-color: #BD0000
}

.vorlias-nav-link:hover { 
	background: #290000
}

a.rbx-btn-secondary-xs.btn-more {
    background: #BD0000
}

.rbx-btn-secondary-xs {
    border-color: #BD0000
}

.rbx-btn-secondary-xs {
    background-color: #BD0000
}

.rbx-btn-secondary-xs:hover {
    background: #690000
}

.rbx-btn-secondary-xs:hover {
    border-color: #690000
}

a.rbx-btn-secondary-xs.btn-more:hover {
    background: #690000
}

a.rbx-btn-secondary-xs.btn-more:hover {
    border-color: #690000
}

.rbx-btn-primary-sm {
    background-color: #BD0000
}

.rbx-btn-primary-sm {
    border-color: #BD0000
}

.rbx-btn-primary-sm:hover {
    background-color: #690000
}

.rbx-btn-primary-sm:hover {
    border-color: #690000
}

.rbx-link, .rbx-link-sm, .rbx-link-xs, .vlist .list-item .list-body .list-content a, .rbx-link:link, .rbx-link-sm:link, .rbx-link-xs:link, .vlist .list-item .list-body .list-content a:link, .rbx-link:visited, .rbx-link-sm:visited, .rbx-link-xs:visited, .vlist .list-item .list-body .list-content a:visited, .rbx-link:active, .rbx-link-sm:active, .rbx-link-xs:active, .vlist .list-item .list-body .list-content a:active {
    color: #BD0000
}

.rbx-header .rbx-navbar-search .rbx-navbar-search-option.selected {
    box-shadow: 4px 0 0 0 #bd0000 inset;
    cursor: pointer
}

#nmr_options {
	border: 1px solid #BD0000
}

a:link, a:visited, a:active {
    color: #BD0000
}

.rbx-btn-primary-sm:visited {
    background-color: #BD0000
}

.rbx-btn-primary-sm:visited:hover {
    background-color: #690000
}

.active .rbx-lead {
    color: #BD0000
}

.rbx-tabs-vertical .rbx-tab.active .rbx-tab-heading {
    box-shadow: -4px 0 0 0 #BD0000 inset;
}

.rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading {
box-shadow: 0 -4px 0 0 #BD0000 inset;
}

span.robux, div.robux {
    background: url(http://i.imgur.com/cYeoSqt.png) no-repeat 0 1px
}

span.tickets, div.tickets {
	background: url(http://i.imgur.com/lAcov2B.png) no-repeat 0 1px
}

.btn-disabled-neutral, .btn-disabled-neutral:link, .btn-disabled-neutral:active, .btn-disabled-neutral:visited {
    border-color: rgba(255,255,255,0)
}

.btn-neutral, .btn-neutral:link, .btn-neutral:active, .btn-neutral:visited {
    border-color: rgba(255,255,255,0)
}

.rbx-btn-primary-lg {
    background-color: #BD0000;
    border-color: #BD0000
}
    
.rbx-btn-primary-lg:hover {
    background-color: #690000;
    border-color: #690000
}

.rbx-btn-secondary-sm {
    background-color: #BD0000;
    border-color: #BD0000
}

.rbx-btn-secondary-sm:hover {
    background-color: #690000;
    border-color: #690000
}

.rbx-btn-buy-xs:hover {
    background-color: #BD0000;
    border-color: #BD0000
}

.btn-disabled-primary, .btn-disabled-primary:link, .btn-disabled-primary:active, .btn-disabled-primary:visited {
    border-color: rgba(255,255,255,0)
}

.btn-primary, .btn-primary:link, .btn-primary:active, .btn-primary:visited {
    border-color: rgba(255,255,255,0)
}

.btn-primary:hover, .btn-primary:focus, .btn-primary:active, .btn-primary.active, .open>.btn-primary.dropdown-toggle {
    border-color: rgba(255,255,255,0)
}

.rbx-input-group-btn .rbx-input-dropdown-btn:hover {
    background-color: #690000;
    border-color: #690000
}

.rbx-link:hover, .rbx-link-sm:hover, .rbx-link-xs:hover, .vlist .list-item .list-body .list-content a:hover, .rbx-link:link:hover, .rbx-link-sm:link:hover, .rbx-link-xs:link:hover, .vlist .list-item .list-body .list-content a:link:hover, .rbx-link:visited:hover, .rbx-link-sm:visited:hover, .rbx-link-xs:visited:hover, .vlist .list-item .list-body .list-content a:visited:hover, .rbx-link:active:hover, .rbx-link-sm:active:hover, .rbx-link-xs:active:hover, .vlist .list-item .list-body .list-content a:active:hover {
    color: #690000
}

.notifications-count {
    border: 1px solid #C00;
    background-color: #FAE5E5
}

.dialog-container .dialog-message-container .dialog-message {
	background-color: #FF9999
}

.dialog-container .dialog-header .dialog-header-title {
	background-color: #BD0000
}

.dialog-container .dialog-header .dialog-header-title:after {
	background-image: linear-gradient(to right,rgba(0,162,255,0) 0,#BD0000 90%)
}

.dialog-container .dialog-header {
	background-color: #BD0000
}

.dialog-container .dialog-message-container .dialog-triangle:before {
    border-color: transparent transparent transparent #FF9999
}
}
