    height: 100px;
    color: #ddd;
}

.markItUpHeader ul li {
    background: #777 none repeat scroll 0 0;
    border-radius: 5px;
    float: left;
    list-style: outside none none;
    margin-right: 6px;
    padding: 3px;
    position: relative;
}

.markItUpHeader ul .markItUpSeparator {
    background-color: #212121;
    color: #212121;
    height: 16px;
    margin: 0 6px 0 0;
    overflow: hidden;
    width: 1px;
}

select {
    background: #151515 none repeat scroll 0 0;
