    color: #ddd;
    padding: 4px;
}

.help {
    background: #424242 none repeat scroll 0 0;
    border-radius: 3px;
    box-shadow: 0 0 5px #333 inset;
    color: #ddd;
    display: none;
    font-size: 11px;
    line-height: 16px;
    margin-top: 13px;
    padding: 10px 7px 13px;
    position: relative;
}

a.link-dashed {
