    -moz-border-top-colors: none;
    background: #1a1a1a none repeat scroll 0 0;
    border-color: #555;
    border-image: none;
    border-style: dashed dashed dashed solid;
    border-width: 1px 1px 1px 3px;
    margin-bottom: 5px;
    padding: 5px 10px;
}


