css:
@-moz-document domain("docs.google.com") {
#docs-chrome {
    background: #444;
    color: #fff;
}

.docs-title-input {
    background: #333;
}

#docs-docos-commentsbutton {
    background: #333;
    color: #fff;
}

#docs-titlebar-share-client-button div.jfk-button {
    background: #05a;
    color: #fff;
    border: #027;
}

#gb#gb a.gb_b {
    /* email address */
    color: #fff;
}

#docs-toolbar-wrapper, #docs-primary-toolbars, #docs-side-toolbar, #docs-toolbar, .goog-toolbar {
    color: #fff;
    border-top: #444;
    border-bottom: #333;
    -webkit-box-shadow: inset 0 0px 0 0 #444;
    -moz-box-shadow: inset 0 0px 0 0 #444;
    box-shadow: inset 0 0px 0 0 #444;
	background: #444; /* Old browsers */
	background: -moz-linear-gradient(top,  #444 0%, #333 100%); /* FF3.6-15 */
	background: -webkit-linear-gradient(top,  #444 0%,#333 100%); /* Chrome10-25,Safari5.1-6 */
	background: linear-gradient(to bottom,  #444 0%,#333 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
}

.goog-toolbar-button-selected, .goog-toolbar-button-checked, .goog-toolbar-menu-button-open, .goog-toolbar-button-hover, .goog-toolbar-button-active {
    background: #555;
    border-color: #444;
}

#docs-equationtoolbar {
    background: #444;
}

#insert-equation-button {
    background: #444;
}

.goog-toolbar-separator, .goog-toolbar-separator.goog-inline-block {
    border-left: #000;
}

.goog-toolbar-menu-button-caption, .goog-toolbar-combo-button-input {
    color: #ccc;
}

.docs-omnibox-input {
    background: #333;
    border: #333;
}

#docs-editor {
	background: #333; /* Old browsers */
	background: -moz-radial-gradient(center, ellipse cover,  #222 0%, #000000 100%); /* FF3.6-15 */
	background: -webkit-radial-gradient(center, ellipse cover,  #222 0%,#000000 100%); /* Chrome10-25,Safari5.1-6 */
	background: radial-gradient(ellipse at center,  #222 0%,#000000 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
}

.docs-menubar {
	height:28px !important;
}
}
