css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://semplice-linux.org/community/") {
  /* bigger (cleaner) layout */
    /* fix background color */     .content { background-color: #FFFFFF; }
    /* remove borders */           .content .row, #punindex.pun, .pun .punwrap, #punindex #brdmain { border: none 0px; }
    /* stretch */                  .content .row, #punindex.pun, #punviewforum.pun, #punsearch.pun { max-width: none;  }
    /* tweaks */                   #punindex #brdmain {  padding: 0px; margin-bottom: 0px; }
  
  
  /* menu */
  #brdmenu { background: #FFF none repeat scroll 0% 0%; } /*border-bottom: 4px solid #0084C6; }*/
  #brdmenu ul { display: inline-block; float: right; vertical-align: middle; height: 55px; margin: 0px; }
  #brdmenu li { display: block; float: left; text-align: center; text-transform: uppercase; }
  #brdmenu a, #brdmenu a:link, #brdmenu a:visited { background: #FFF none repeat scroll 0% 0%; color: #333; font-size: 14px; font-weight: 700; }
  #brdmenu a:hover { color: #0084C6; border-bottom: 4px solid #0084C6;}
  
  
  /* Welcome text */
  #brdwelcome { padding: 4px 0px; }


  /* announce */
  #announce { border: none 0px; }
  
  
  /* Forum table tweaks */
    /* tweaks */                     .pun .blocktable .box { border: none 0px; }
                                     .pun h2 { padding: 22px 0px; }
    /* header  */                    .pun .blocktable th { background-color: #ffffff; padding: 12px 16px 6px 8px; }
                                     .pun .blocktable th { color: #959595; font-size: 13px; font-weight: 700; height: 55px; }
                                     .pun .blocktable h2 span { color: #000000; font-size: 14px; font-weight: 700; text-transform: uppercase; }
                                     .pun .blocktable th { border-bottom: 2px solid #DDD; }
    /* body */                       .pun .blocktable td { border-bottom: 1px solid #DDD; }
  
  
  
  /* remove footer */
  #brdfooter { display: none; }
}
