@-moz-document domain("furaffinity.net") {
/*Fix Overflow*/

html, body {
    margin:0!important;
    max-width:100vw!important;
    overflow-x:hidden!important;
    min-width:0!important;
    background-color:#151518;
}

#furaffinity {
    max-width:100vw!important;
    overflow-x:hidden!important;
    min-height:100vh!important;
    min-width:0!important;
}

/* Ad and Banner Hider */

.ads, #ad-extra-flat, #ad-extra-comments, #ad-extra-submission-square {
    display:none!important;
}

#footer {
    padding-top:15px;
}

/* User Profile Tweaks */

#userpage > div > div.p10t {
    padding-top:1px!important;
}

.borderbot {
    padding-bottom:20px;
}

.container-item-top, .container-item-bot {
    margin:0!important;
}

/* Stats Page */

.lineitem {
    width:100%!important;
}

div > div.cell.p10.bg4.rounded.valigntop {
    width:100%!important;
}

.cell {
    margin:0!important;
}

/* Transition */

* {
	transition-duration:.3s!important;
    transition-delay:0!important;
}


/* Fix user page width */

#userpage > div > div.user-profile-flex {
    overflow:hidden!important;
}

.userline {
    max-width:100vw!important;
    overflow:hidden!important;
}

.submission-area {
    overflow-x:auto;
}
}
