css:
@-moz-document url("https://gabrielecirulli.github.io/2048/") {
html, body {
    background:#050710 !important;
    color:white !important;
}

.tile-inner, .grid-cell {
    /*border-radius:50% !important;*/
}

.restart-button {
 	background:#72668F !important;   
}

.app-notice {
 	background:#3F64ED !important;   
}

.score-container, .best-container {
 	background:#A3A0BB !important;   
}

.grid-cell {
    box-shadow:0px 0px 10px rgba(0,0,0,0.15) inset;
}

.game-container {
    /*border-radius:50% !important;*/
    background:transparent;
}

.tile-2 .tile-inner {
 	background:#111b25 !important;
    color:#f9f6f2 !important;
    box-shadow:none !important;
}

.tile-4 .tile-inner {
 	background:#121f37 !important;
    color:#f9f6f2 !important;
    box-shadow:none !important
}

.tile-8 .tile-inner {
 	background:#0d4e86 !important; 
    color:#f9f6f2 !important;
    box-shadow:none !important
}

.tile-16 .tile-inner {
 	background:#0a6a9c !important;
    box-shadow:none !important
}

.tile-32 .tile-inner {
 	background:#0983a0 !important;
    box-shadow:none !important
}

.tile-64 .tile-inner {
 	background:#09a1c4 !important;   
    box-shadow:none !important
}

.tile-128 .tile-inner {
 	background:#12308d !important;  
    box-shadow:0 0 30px 10px rgba(90, 100, 222, 0.2381), inset 0 0 0 1px rgba(255, 255, 255, 0.19048) !important;
    
}

.tile-256 .tile-inner {
 	background:#12339e !important;
    box-shadow:0 0 30px 10px rgba(90, 100, 222, 0.31746), inset 0 0 0 1px rgba(255, 255, 255, 0.19048) !important;
}

.tile-512 .tile-inner {
 	background:#1237af !important;
    box-shadow:0 0 30px 10px rgba(90, 100, 222, 0.39683) !important;
}

.tile-1024 .tile-inner {
 	background:#123ac0 !important;
    box-shadow:0 0 30px 10px rgba(90, 100, 222, 0.47619), inset 0 0 0 1px rgba(255, 255, 255, 0.28571) !important;
}

.tile-2048 .tile-inner {
 	background:#123dd1 !important;   
    box-shadow:0 0 30px 10px rgba(90, 100, 222, 0.55556) !important;
}
}
