css:
@-moz-document regexp("(https|http)://forumua.org/.*"), regexp("(https|http)://(www|vb).forumua.org/.*") {

#outer-wrapper {
    background: #AAAAAA !important;
}
#wrapper {
    background: #AAAAAA !important;
}
.notices,
#content {
    background: #AAAAAA !important;
}
#footer-copyright {
    background: #AAAAAA !important;
}
#footer {
    background: #AAAAAA !important;
}
/*строка навигации*/

#breadcrumbs {
    background: #AAAAAA;
}
.b-top-background__header-mainnav {
    background: #AAAAAA !important;
}
/*кто на сайте и тп*/

.canvas-layout-container .canvas-widget.search-widget,
.canvas-layout-container .canvas-widget.wol-widget,
.canvas-layout-container .canvas-widget.wollist-widget,
.canvas-layout-container .canvas-widget.memberlist-widget,
.search-results-widget .widget-content {
    background: #F8FFFF;
}
.l-row__fixed--right > .l-col__flex-6.l-col--flex-first > .l-col__flex-content {
    margin-right: -15px;
}
/*разделители*/

.view-mode .widget-tabs .widget-tabs-nav .module-title {
    background: #AAAAAA url("http://vb.forumua.org/images/css/menu.png") repeat-x !important;
}
/*закладки виджетов*/

.widget-tabs .widget-tabs-nav .ui-tabs-nav {
    background: #AAAAAA url("http://vb.forumua.org/images/css/menu.png") repeat-x;
}
.top_btn {
    background: url("http://www.ckpower.ltd.ua/forumua_org/button_up.png") !important;
}
.top_btn:hover {
    background: url("http://www.ckpower.ltd.ua/forumua_org/button_up_act2.png") !important;
}
#footer {
    border: 0;
}
#footer-tabbar {
    border-right: 1px solid #A9B8C2;
    border-left: 1px solid #A9B8C2;
}
/*фон сообщения*/

.b-post__body {
    background: #EAECEC none 0 0;
}
/*фон профиля в сообщении*/

.forum-conversation-content-widget .l-col__fixed-3.l-col__small--full.h-hide--on-preview.h-padding-right-l {
    background: #BECECE;
}
/*нижняя часть фона профиля в сообщении*/

.forum-conversation-content-widget .b-post {
    background: #BECECE;
}
/*первое сообщение темы*/

.b-post--first .b-post__body {
    background: #ECFCEC url("http://vb.forumua.org/images/css/gradient_comment.png") repeat-x;
}
#footer-copyright {
    display: block;
}
.forum-list-container .forum-list-header th,
.topic-list-container .topic-list-header th,
#subscribed-tab .table.stream-view .tr.my_subscribers_header .td {
    background: #AAAAAA url("http://vb.forumua.org/images/css/menu.png") repeat-x;
}
/*счетчики сообщений, тем и т.п.*/

.forum-activity-stream-widget #forum-tab .posts-count,
.forum-activity-stream-widget #forum-tab .topics-count,
.forum-activity-stream-widget #forum-tab .lastpost-by,
.forum-activity-stream-widget #forum-tab .post-date {
    font-size: 12px;
    color: #005555
    /*black*/
    
    ;
}
.canvas-layout-container .canvas-widget.forgot-password-widget {
    border: 1px solid #A9B8C2
}
.private-message-widget.private-message-display .topic-item td,
.private-message-widget.private-message-display .forum-item td {
    border-bottom: 1px dotted #A9B8C2
}
.korpus label {
    border: solid #A9B8C2;
    background: #AAAAAA;
}
.conversation-toolbar-wrapper .filtered-by {
    background: #FFFFFF;
    border-color: #A9B8C2;
}
.memberlist-widget .b-table__row {
    border: solid #A9B8C2;
}
.memberlist-widget .b-table__row--header {
    border: solid #A9B8C2;
}
.memberlist-widget .l-col__large-1 {
    border-left: 1px solid #A9B8C2;
}
#subscribed-tab .table.stream-view .tr .td,
#activities-tab .table.stream-view .tr .td,
#activity-stream-tab .table.stream-view .tr .td {
    border-right: 1px solid #A9B8C2;
}
#subscribed-tab .table.stream-view li.list-item+li .td,
#activities-tab .table.stream-view .td,
#activity-stream-tab .table.stream-view .tr.list-item .td {
    border-top: 1px dotted #A9B8C2;
}
.wollist-widget .b-table__cell {
    border-right: 1px solid #A9B8C2;
}
.wollist-widget .b-table__cell--header {
    border-right: 1px solid #A9B8C2;
}
.search-fields-widget .widget-content {
    border: 1px solid #A9B8C2;
}
.forum-list-container .forum-list-header .header-posts,
.forum-list-container .forum-item .posts-count,
.forum-list-container .subforum-list .posts-count {
    border-right: 1px solid #A9B8C2;
}
.forum-list-container .forum-list-header .header-topics,
.forum-list-container .forum-item .topics-count,
.forum-list-container .subforum-list .topics-count,
.topic-list-container .topic-list-header .header-posts,
.topic-list-container .topic-list-header .header-votes,
.topic-list-container .topic-list .topic-item .posts-count,
.topic-list-container .topic-list .topic-item .votes-count,
.socialgroup-home-widget #groups-tab .sg-topics,
.socialgroup-home-widget #groups-tab .sg-users,
.private-message-widget #privateMessageContainer .cell-gotopost {
    border-left: 1px solid #A9B8C2;
    border-right: 1px solid #A9B8C2;
}
.forum-list-container {
    background: #ECECEC;
    border: 1px solid #A9B8C2;
}
#channel-subtabbar {
    background: #ECECEC url("http://vb.forumua.org/images/css/menu.png") repeat-x;
    border-right: 1px solid #A9B8C2;
    border-left: 1px solid #A9B8C2;
}
#channel-tabbar ul li a:hover {
    border-top: 1px solid #A9B8C2;
}
#channel-tabbar {
    border-right: 1px solid #A9B8C2;
    border-left: 1px solid #A9B8C2;
}
#outer-wrapper {
    background: #FFFFFF url("http://vb.forumua.org/images/css/header2.png") repeat-x;
}
#header .header-cell {
    background: #FFFFFF;
}
#header .site-logo {
    background: #FFFFFF url("http://vb.forumua.org/images/css/header.png") no-repeat right;
}
.sg-icon-wrapper {
    background: #FFFFFF;
}
.profile-settings-widget .widget-content>.h-margin-bottom-l {
    background-color: #FFFFFF;
}
.axd-widget {
    background: #FFFFFF !important
}
#activities-tab .activity_header .td.lastpost span,
#activity-stream-tab .activity_header .td.lastpost span {
    background: #FFFFFF;
}
.wollist-widget .toolbar_container {
    background: #FFFFFF;
}
/*разделитель разделов*/

.widget-tabs .widget-tabs-panel {
    background: #9A9A9A;
}
/*разделитель над виджетом*/

.canvas-layout-container .canvas-widget {
    background: #AAAAAA;
}
.b-module.canvas-widget.default-widget.activity-stream-widget.forum-activity-stream-widget.widget-tabs.ui-tabs.widget-no-header-buttons.widget-no-border.ui-widget.ui-widget-content.ui-corner-all {
    background: #AAAAAA;
}
.search-widget .widget-header,
.wol-widget .widget-header {
    background: #C7D0D7 url("http://vb.forumua.org/images/css/menu.png") bottom center;
}
#channel-tabbar ul li.current a,
#channel-tabbar ul li.current a:hover,
.edit-mode #channel-tabbar ul li.current a:hover {
    background: #ECECEC;
}
/*ативный таб*/

.view-mode .widget-tabs .widget-tabs-nav li.ui-tabs-selected a {
    background: #A5ECAC;
}
/*список тем*/

.topic-list-container .topic-list {
    background: #ECECEC none 0 0
}
/*прилепленные темы*/

.topic-list-container .topic-list .topic-item.sticky {
    background: #C0DECE;
}
.view-mode .widget-tabs .widget-tabs-nav {
    background: #F9F9F9 url("http://vb.forumua.org/images/css/menu.png") repeat-x !important;
}
.private-message-widget .message-list .message-item {
    background: #ECECEC;
}
.privateMessageDetail .b-post {
    background: #DCE1E5;
}
.b-toolbar {
    background: #cfd1cf;
    background-image: -webkit-gradient(linear, left top, left bottom, from(#f5f5f5), to(#cfd1cf));
    background-image: -moz-linear-gradient(top, #f5f5f5, #cfd1cf);
    background-image: -webkit-linear-gradient(top, #f5f5f5, #cfd1cf);
    background-image: -o-linear-gradient(top, #f5f5f5, #cfd1cf);
    background-image: -ms-linear-gradient(top, #f5f5f5, #cfd1cf);
    background-image: linear-gradient(top, #f5f5f5, #cfd1cf);
}
.forum-conversation-content-widget .h-clearfix.js-button-group {
    background: #cfd1cf;
    background-image: -webkit-gradient(linear, left top, left bottom, from(#f5f5f5), to(#cfd1cf));
    background-image: -moz-linear-gradient(top, #f5f5f5, #cfd1cf);
    background-image: -webkit-linear-gradient(top, #f5f5f5, #cfd1cf);
    background-image: -o-linear-gradient(top, #f5f5f5, #cfd1cf);
    background-image: -ms-linear-gradient(top, #f5f5f5, #cfd1cf);
    background-image: linear-gradient(top, #f5f5f5, #cfd1cf);
}
.b-smilie__container--big .b-smilie__category {
    background: #F8F8F8 none 0 0;
}
.b-content-entry-panel {
    background: #ECECEC none 0 0;
}
.dialog-container.ui-dialog .ui-dialog-titlebar {
    background: #ECECEC url("http://vb.forumua.org/images/css/menu.png") bottom center;
}
.ui-dialog .ui-dialog-content {
    background: #F8F8F8;
}
.b-editor .autosave-indicator {
    background-color: #FFFF00;
    border: 1px solid #A9B8C2;
    color: #0000FF;
    font-size: 13px;
}
.b-toolbar__item:hover {
    background: #F8F8F8;
}
.b-editor .cke_top {
    background: #cfd1cf;
    background-image: -webkit-gradient(linear, left top, left bottom, from(#f5f5f5), to(#cfd1cf));
    background-image: -moz-linear-gradient(top, #f5f5f5, #cfd1cf);
    background-image: -webkit-linear-gradient(top, #f5f5f5, #cfd1cf);
    background-image: -o-linear-gradient(top, #f5f5f5, #cfd1cf);
    background-image: -ms-linear-gradient(top, #f5f5f5, #cfd1cf);
    background-image: linear-gradient(top, #f5f5f5, #cfd1cf);
}
.private-message-widget .widget-content .content-entry-box .js-button-group {
    background: #cfd1cf;
    background-image: -webkit-gradient(linear, left top, left bottom, from(#f5f5f5), to(#cfd1cf));
    background-image: -moz-linear-gradient(top, #f5f5f5, #cfd1cf);
    background-image: -webkit-linear-gradient(top, #f5f5f5, #cfd1cf);
    background-image: -o-linear-gradient(top, #f5f5f5, #cfd1cf);
    background-image: -ms-linear-gradient(top, #f5f5f5, #cfd1cf);
    background-image: linear-gradient(top, #f5f5f5, #cfd1cf);
}
.private-message-widget .widget-content .content-entry-box .b-editor {
    border-width: 0 1px 0 1px;
}
.placeholder {
    color: #2793e6 !important;
    font: bold 16px Helvetica, Arial, Verdana, sans-serif;
}
body {
    font: 14px Helvetica, Arial, Verdana, sans-serif;
    color: #000000;
}
}

@-moz-document regexp("(https|http)://forumua.org/"), regexp("(https|http)://(www|vb).forumua.org/"), regexp("(https|http)://forumua.org/forum(.*)"), regexp("(https|http)://(www|vb).forumua.org/forum(.*)") {
/*выкл панель с виджетами на главной странице и в подфорумах*/
/* в этой же панели отображаются уведомления и ЛС, поэтому в url forumua.org/privatemassage/ панель должна оставаться включенной */

.section-1 {
    display: none !important
}
}
