css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dir.xiph.org") {

#xiphlogo:before {content: "icecast directory"; line-height: 97px; padding-left: 180px; font-size: 50px; font-weight: 400; font-family: 'Bookman URW', Georgia, Serif; color: rgb(220, 220, 220); white-space: nowrap;}
#xiphlogo {background: url("https://wiki.xiph.org/images/thumb/e/ef/Icecast_Logo.svg/100px-Icecast_Logo.svg.png") no-repeat scroll 60px top transparent; overflow: visible; padding-top: 1px; margin-top: -15px; height: 100px;}
#xiphbar_outer {background-color: rgb(50, 50, 60); min-width: 100%; max-width: 840px; width: 100%;}
#xiphbar {min-width: 600px; max-width: 1060px; width: 100%;}
img#xiphbanner {visibility: hidden; width: 0px;}

#thepage {min-width: 600px; max-width: 1080px;}
.pager {padding: 5px;font-size: 2.0em;font-weight: 600; text-align: center;}

body {background-color: rgb(30, 30, 50); font-family: 'Ubuntu','Droid Sans','Tangerine',"Open Sans","sans-serif";}
.row1 {background-color: rgb(34, 34, 54);} 

body, .no-link {color: rgb(151, 151, 171);}
a {color: rgb(102, 152, 204);}
table.servers-list p.stream-description {font-size: 1.3em; font-weight: 500;}
table.servers-list span.name a {text-decoration: none;}
table.servers-list span.name a:hover {text-decoration: underline;  color: rgb(51, 142, 204);}
table.servers-list p.stream-onair {font-size: 1.2em; color: rgb(102, 152, 102);}
table.servers-list span.listeners {color: rgb(70, 70, 90);}
table.servers-list div.stream-tags {text-align: center;}
table.servers-list div.stream-tags strong {color: rgb(50, 50, 70);text-transform: lowercase;}
table.servers-list div.stream-tags a {font-size: 1.2em; color: rgb(70, 70, 90);text-decoration: none; padding:1px; text-transform: lowercase;}
table.servers-list div.stream-tags a:hover{color: rgb(30, 30, 50); background: rgb(140, 140, 160); background-clip: content-box; border-radius: 3px; padding:1px;}
table.servers-list td.tune-in p{visibility:hidden;}
table.servers-list td.tune-in a{visibility:visible; text-decoration: none; font-weight: 600;}
table.servers-list td.tune-in a:hover{color: rgb(30, 30, 50); background-color: rgb(91, 148, 204); background-clip: content-box; border-radius: 3px;}

table.servers-list td.tune-in {width: 100px; max-width: 100px; min-width: 100px;}
#player {width: 100%; height: 30px;}

table.servers-list tr td {border-bottom: 0px solid rgb(59, 92, 115);}
#navbar {border-color: -moz-use-text-color rgb(59, 92, 115) -moz-use-text-color -moz-use-text-color;}
#content {border-left: 1px solid rgb(59, 92, 115);}


}
