css:
@-moz-document domain("atlassian.net") {
#mw-container .drilldown-title,
.aui-page-panel-content.one-panel .search-results,
.search-results-container,
.search-blanket,
.list-container-popular-stream .stream-item-heading-link,
body#tinymce.page-edit,
body#tinymce.template-editor,
#description-val.editable-field,
.adg3 .aui-page-header #summary-val,
.wiki-edit-toolbar~.wiki-edit-content>.wiki-textfield,
form.aui input[type=text],
body.mceContentBody,
p {
    direction: rtl;
    font-family: 'IRANYekan', 'Iran Sans', 'IranSans', 'Iran', 'Droid Arabic Naskh', Tahoma, sans-serif;
}
.markdown {
    word-wrap: normal;
    overflow-wrap: normal;
    word-break: normal;
}
time:before {
    content: '\200F'
}
.aui-buttons .aui-button:first-child {
    border-top-right-radius: 3.01px;
    border-bottom-right-radius: 3.01px;
    border-top-left-radius: 0;
    border-bottom-left-radius: 0;
}
.aui-buttons .aui-button~.aui-button {
    border-left-width: 1px;
    padding-left: 6px;
    border-right-width: 0;
    padding-right: 11px;
}
.user-profile .content-navigation .ajs-menu-bar {
    float: left;
}
.code,
code,
[data-macro-name="code"],
.wiki-content .wysiwyg-macro-body>pre {
    direction: ltr;
    text-align: left;
}
#search-results-meta {
    direction: ltr;
    text-align: right;
}
.search-macro-medium,
.mytasks,
ul.inline-task-list {
    direction: ltr;
}
.recently-updated-concise .update-item-content {
    padding-right: 21px;
    padding-left: 0;
}
#mw-container .drilldown-header-title {
    padding-left: 0;
    padding-right: 5px;
}
#mw-container .drilldown-new-actions {
    padding: 5px 10px 5px 0;
    text-align: right;
}
#mw-container .mw-notification-text,
#mw-container .mw-drilldown-item .mw-drilldown-excerpt,
.list-container-all-updates .update-items .update-item-content,
#content-title,
body.mceContentBody,
#title-text,
.wiki-content,
.acs-side-bar-content,
.aui-label,
.default-list-view .item-title,
.contenteditor #full-height-container>div,
.contenteditor #editpagetemplate>div,
.contenteditor #createpagetemplate>div,
.contenteditor #editpageform>div,
.contenteditor #createpageform>div,
.update-item-details,
table.aui>thead>tr>th,
table.aui>tbody>tr>th,
table.aui>thead>tr>td,
table.aui>tbody>tr>td,
table.aui>tfoot>tr>td {
    font-family: 'Iran Sans', 'IranSans', 'Iran', 'Droid Arabic Naskh', serif;
}
#mw-container .mw-drilldown-item .mw-drilldown-excerpt {
    direction: rtl;
}
.list-container-all-updates .update-items .update-item-content,
.default-list-view .item-title,
.update-item-details {
    direction: rtl;
    text-align: left;
}
#content-title {
    height: 54px;
    line-height: 54px;
}
.contenteditor #editor-precursor {
    direction: rtl;
}
#editor-precursor .cell {
    padding-left: 0;
    padding-right: 30px;
}
#breadcrumbs li:before {
    padding: 0 6px 0 2px;
}
#editor-precursor #breadcrumbs {
    padding-left: 0;
    padding-right: 10px;
}
#content-title-div {
    padding: 0 10px 0 30px;
    font-weight: 400;
}
#editor-precursor .aui-toolbar2 {
    padding-right: 10px;
    padding-left: 0;
}
body.mceContentBody {
    direction: rtl;
    text-align: right;
    font-size: 14px;
}
#title-text {
    direction: rtl;
    text-align: right;
    font-size: 24px;
}
#main-header:not(.doctheme-no-override) #title-text {
    padding-top: 10px;
}
.wiki-content,
.confluenceTh,
.confluenceTd,
#comments-section .section-header {
    direction: rtl;
    text-align: right;
}
.wiki-content .header,
.wiki-content .footer,
.wiki-content .cell {
    line-height: 26px;
    font-size: 15px;
}
.wiki-content ul.inline-task-list>li {
    line-height: 30px;
    text-align: left;
    direction: rtl
}
.acs-side-bar-content,
.aui-label {
    direction: rtl;
    text-align: left;
}
.aui-label {
    line-height: 27px;
}
.wiki-content ul,
.confluenceTable ol,
.confluenceTable ul {
    line-height: 24px;
}
.wiki-content ol,
.wiki-content ol ol ol ol,
.wiki-content ol ol ol ol ol ol ol,
.wiki-content ol ol ol ol ol ol ol ol ol ol {
    list-style-type: decimal;
    list-style-type: arabic-indic;
}
.confluenceTable ol,
.confluenceTable ul {
    margin-right: 0;
    margin-left: 0;
    padding-left: 0;
    padding-right: 22px;
}
.wiki-content time,
.wiki-content .user-mention {
    font-family: Arial, sans-serif;
}
.comment .comment-user-logo {
    margin: 0 0 0 10px;
    float: right;
}
.quick-comment-body {
    padding-right: 42px;
    padding-left: 0;
}
.comment-body,
.comment-header {
    padding: 0 42px 0 0;
}
.aui-toolbar2 .aui-buttons {
    float: right;
}
#rte-savebar .aui-buttons {
    margin-right: 0;
    margin-left: 10px;
}
#rte-savebar .save-button-container {
    float: right;
    padding-left: 10px;
    padding-right: 0;
    margin-right: 0;
    margin-left: 10px;
}
ol.comment-threads.top-level {
    padding-right: 0;
}
ol.comment-threads {
    padding-left: 0;
    padding-right: 40px;
}
#comments-section .comment.focused {
    margin-right: -10px;
    padding-right: 10px;
    margin-left: 0;
    padding-left: 0;
}
.comment-body .comment-content.wiki-content {
    padding-left: 20px;
    padding-right: 0;
}
.comment-header .author {
    text-align: right;
}
.comment-actions-primary li {
    float: right;
    direction: rtl;
}
.page-metadata ul li {
    float: right;
    padding: 0 0 0 5px;
}
#main {
    padding-right: 40px;
    padding-left: 20px;
}
#main #content {
    padding-left: 20px;
    padding-right: 0;
}
.confluenceTable .inline-task-list {
    direction: rtl;
    text-align: right;
    margin-left: 22px !important;
}
#main-content .confluence-information-macro .title {
    margin-right: 20px;
}
#main-content .confluence-information-macro .aui-icon {
    left: 0;
    right: 10px;
}
.aui-page-panel-inner .stickable{
    direction: rtl;
    text-align: right;
}

.aui-page-panel-inner .stickable .update-item-profile{
    padding: 13px 0 5px 14px;
}

.aui-page-panel-inner .stickable .update-item-content{
    direction: rtl;
    text-align: right;
}

.aui-page-panel-inner .stickable .aui-icon{
        text-align: right;
}
li.stream-item .stream-icon-container{
        margin: 3px 14px 0 0;
}
.update-groupings .grouping .update-item-profile {
    float: right !important;
}
.update-groupings .grouping .update-items{
    padding-left: 0 !important;
    padding-right: 58px;
}
.update-groupings .grouping .update-item-icon{
    float: right;
}
.update-groupings .grouping .update-item-details{
    direction: rtl !important;
    text-align: right;
    padding-right: 21px;
}
.recently-updated .update-item-icon{
    float: right !important;
}
.blog-post-listing .logo-heading-block{
    text-align: right;
    direction: rtl;
}
.blog-post-listing {
    padding: 30px 68px 20px 20px !important;
}
.logoBlock {
    right: 0px !important;
    left: auto;
}
.recently-updated-concise .update-item-content {
    text-align: right;
    direction: rtl;
}
.content-navigation {
    float: left !important;
}
.recently-updated h2{
    text-align: right;
}
.content-by-label>li>div:first-child {
    float: right;
}
.content-by-label>li .details {
    padding-right: 21px;
}
.content-header .list-title {
    width: auto;
    float: right;
}
.aui-page-panel-content .cq-questionlist {
    text-align: right;
    font-family: 'IRANYekan', 'IRANSans', 'Iran Sans', 'IranSans', 'Iran', 'Droid Arabic Naskh', Tahoma, sans-serif;
}

.cq-questionlist .cq-stats {
    float: right !important;
}

.aui-page-panel-content .cq-context-title {
    text-align: right;
    font-family: 'IRANYekan', 'IRANSans', 'Iran Sans', 'IranSans', 'Iran', 'Droid Arabic Naskh', Tahoma, sans-serif;
}
}
